// üìã ÿ¨ÿØŸàŸÑ ÿßŸÑŸÖÿÆÿßŸÑŸÅÿßÿ™
export const violations = pgTable('violations', {
  id: serial('id').primaryKey(),
  user_id: integer('user_id'),
