});

// üß± ÿ¨ÿØŸàŸÑ ÿÆŸÑÿ∑ÿßÿ™ ÿßŸÑŸÖŸàÿßÿØ
export const mixing_recipes = pgTable('mixing_recipes', {
  id: serial('id').primaryKey(),
  machine_type: varchar('machine_type', { length: 20 }), // A / ABA
  formula_layers: integer('formula_layers'),
  material_items: json('material_items'), // [{item_id: 1, percentage: 70}, {...}]
  created_at: timestamp('created_at').defaultNow()
});

// üßç‚Äç‚ôÇÔ∏è ÿ¨ÿØŸàŸÑ ÿßŸÑÿ™ÿØÿ±Ÿäÿ®
export const training_records = pgTable('training_records', {
  id: serial('id').primaryKey(),
  user_id: integer('user_id'),
  training_type: varchar('training_type', { length: 100 }),
  date: date('date'),
  status: varchar('status', { length: 20 }) // completed / pending
});

// üì¶ ÿ¨ÿØŸàŸÑ ÿßŸÑŸÖŸàÿ±ÿØŸäŸÜ
export const suppliers = pgTable('suppliers', {
  name_ar: varchar('name_ar', { length: 100 }),
  id: serial('id').primaryKey(),
  name: varchar('name', { length: 100 }),
  contact: varchar('contact', { length: 100 }),
  materials_supplied: json('materials_supplied')
});


// ÿßŸÑÿπŸÑÿßŸÇÿßÿ™ ÿ®ŸäŸÜ ÿßŸÑÿ¨ÿØÿßŸàŸÑ (Relations)

// users ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ sections Ÿà roles
// customers ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ users (ŸÖŸÜÿØŸàÿ® ŸÖÿ®Ÿäÿπÿßÿ™)
// machines ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ sections
// rolls ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ production_orders, machines, employees, sections
// waste ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ rolls Ÿà production_orders
// production_orders ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ orders Ÿà customer_products
// orders ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ customers
// quality_checks ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ users (checked_by)
// training_records ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ users
// maintenance_requests ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÄ machines Ÿà users

// ŸÖŸÑÿßÿ≠ÿ∏ÿ©: Drizzle ŸäÿØÿπŸÖ ÿßŸÑÿπŸÑÿßŸÇÿßÿ™ ÿ®Ÿàÿßÿ≥ÿ∑ÿ© joins ÿØÿßÿÆŸÑ ÿßŸÑŸÉŸàŸäÿ±Ÿäÿ≤ ŸàŸÑŸäÿ≥ ÿπŸÜ ÿ∑ÿ±ŸäŸÇ foreign key constraints ŸÖÿ®ÿßÿ¥ÿ±ÿ© ŸÅŸä ÿßŸÑÿ™ÿπÿ±ŸäŸÅ.
// ŸäŸÅÿ∂ŸÑ ÿßŸÑÿ≠ŸÅÿßÿ∏ ÿπŸÑŸâ ÿßŸÑÿπŸÑÿßŸÇÿßÿ™ ŸÖŸÜÿ∑ŸÇŸäŸãÿß ÿØÿßÿÆŸÑ ÿßŸÑŸÉŸàÿØ.



// üóÇÔ∏è ÿ¨ÿØŸàŸÑ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ŸàÿßŸÑŸÅÿ±ÿπŸäÿ©
export const categories = pgTable('categories', {
  id: serial('id').primaryKey(),
  name_ar: varchar('name_ar', { length: 100 }),
  name_en: varchar('name_en', { length: 100 }),
  parent_id: integer('parent_id')
});


// üì¶ ÿ¨ÿØŸàŸÑ ÿßŸÑÿ£ÿµŸÜÿßŸÅ (Items)
export const items = pgTable('items', {
  id: serial('id').primaryKey(),
  name_ar: varchar('name_ar', { length: 100 }),
  name_en: varchar('name_en', { length: 100 }),
  unit: varchar('unit', { length: 20 }),
  type: varchar('type', { length: 50 }), // raw / final
  price: integer('price')
});


// üåç ÿ¨ÿØŸàŸÑ ÿßŸÑŸÖŸàÿßŸÇÿπ ÿßŸÑÿ¨ÿ∫ÿ±ÿßŸÅŸäÿ©
export const locations = pgTable('locations', {
  id: serial('id').primaryKey(),
  name_ar: varchar('name_ar', { length: 100 }),
  name_en: varchar('name_en', { length: 100 }),
  coordinates: varchar('coordinates', { length: 100 }),
  tolerance_range: integer('tolerance_range')
});


// üè¢ ÿ¨ÿØŸàŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿµŸÜÿπ
export const company_profile = pgTable('company_profile', {
  id: serial('id').primaryKey(),
  name_ar: varchar('name_ar', { length: 100 }),
  name_en: varchar('name_en', { length: 100 }),
  address: text('address'),
  tax_number: varchar('tax_number', { length: 20 }),
  phone: varchar('phone', { length: 20 }),
  logo_url: varchar('logo_url', { length: 255 })
});

