      const { username, password } = req.body;

      // Enhanced validation
      if (!username?.trim() || !password?.trim()) {
        return res.status(400).json({ message: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸàŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ" });
      }

      const user = await storage.getUserByUsername(username.trim());
      if (!user) {
        return res.status(401).json({ message: "ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©" });
      }

      // Enhanced null checks for user properties
      if (!user.password) {
        console.error('User found but password is null/undefined:', user.id);
        return res.status(401).json({ message: "ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©" });
      }

      // Check password using bcrypt for security
      const isPasswordValid = await bcrypt.compare(password, user.password);
      if (!isPasswordValid) {
        return res.status(401).json({ message: "ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©" });
      }

      if (user.status !== 'active') {
        return res.status(401).json({ message: "ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑" });
      }

      // Save user session with explicit save callback
      req.session.userId = user.id;

      // Debug session creation (remove in production)
      // console.log("Login successful - Session ID:", req.sessionID);
      // console.log("Login successful - User ID saved to session:", req.session.userId);

      // Ensure session is saved before responding
      req.session.save((err: any) => {
        if (err) {
          console.error("Session save error:", err);
          return res.status(500).json({ message: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ¨ŸÑÿ≥ÿ©" });
        }

        // Session saved successfully - safe property access
        res.json({ 
          user: { 
            id: user.id ?? null, 
            username: user.username ?? '', 
            display_name: user.display_name ?? '',
            display_name_ar: user.display_name_ar ?? '',
            role_id: user.role_id ?? null,
            section_id: user.section_id ?? null 
          } 
        });
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ" });
    }
  });

  // Get current user
  app.get("/api/me", requireAuth, async (req, res) => {
    try {
      // Double-check session (redundant with requireAuth but safer)
      if (!req.session?.userId || typeof req.session.userId !== 'number') {
        return res.status(401).json({ 
          message: "ÿ¨ŸÑÿ≥ÿ© ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©",
          success: false
        });
      }

      const user = await storage.getUserById(req.session.userId);
      if (!user) {
        // User doesn't exist in database, clear invalid session
        try {
          if (req.session?.destroy) {
            req.session.destroy((err: any) => {
              if (err) console.error("Error destroying invalid session:", err);
            });
          }
        } catch (destroyError) {
          console.error("Failed to destroy invalid session:", destroyError);
        }
        return res.status(404).json({ 
          message: "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ",
          success: false
        });
      }

      // Validate user status
      if (user.status !== 'active') {
        return res.status(403).json({
          message: "ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑",
          success: false
        });
      }

      // User found - extend session safely
      try {
        if (req.session?.touch) {
          req.session.touch();
        }

        // Save session to ensure it persists (non-blocking)
        if (req.session?.save) {
          req.session.save((err: any) => {
            if (err) {
              console.error("Error saving session on /api/me:", err);
              // Continue anyway, don't break the response
            }
          });
        }
      } catch (sessionError) {
        console.error("Session management error:", sessionError);
        // Don't fail the request for session issues
      }

      // Return sanitized user data
      const userData = {
        id: user.id || null,
        username: user.username || '',
        display_name: user.display_name || '',
        display_name_ar: user.display_name_ar || '',
        role_id: user.role_id || null,
        section_id: user.section_id || null
      };

      res.json({ 
        user: userData,
        success: true
      });
    } catch (error) {
      console.error("Get current user error:", error);
      res.status(500).json({ 
        message: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ",
        success: false
      });
    }
  });

  // Logout
  app.post("/api/logout", async (req, res) => {
    try {
      if (req.session?.destroy) {
        req.session.destroy((err) => {
