  async createCut(cutData: InsertCut): Promise<Cut> {
    try {
      return await db.transaction(async (tx) => {
        // Get the roll and validate available weight
        const [roll] = await tx
          .select()
          .from(rolls)
          .where(eq(rolls.id, cutData.roll_id))
          .for('update');

        if (!roll) {
          throw new Error('ÿßŸÑÿ±ŸàŸÑ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ');
        }

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑÿµÿßŸÅŸäÿ© ŸÑÿß ÿ™ÿ™ÿ¨ÿßŸàÿ≤ Ÿàÿ≤ŸÜ ÿßŸÑÿ±ŸàŸÑ ÿßŸÑÿ£ÿµŸÑŸä
        const rollWeight = normalizeDecimal(roll.weight_kg);
        const cutWeight = normalizeDecimal(cutData.cut_weight_kg);

        if (cutWeight > rollWeight) {
          throw new Error(`ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑÿµÿßŸÅŸäÿ© (${cutWeight.toFixed(2)} ŸÉŸäŸÑŸà) ŸÑÿß ŸäŸÖŸÉŸÜ ÿ£ŸÜ ÿ™ÿ™ÿ¨ÿßŸàÿ≤ Ÿàÿ≤ŸÜ ÿßŸÑÿ±ŸàŸÑ (${rollWeight.toFixed(2)} ŸÉŸäŸÑŸà)`);
        }

        if (cutWeight <= 0) {
          throw new Error('ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑÿµÿßŸÅŸäÿ© Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ÿ£ŸÉÿ®ÿ± ŸÖŸÜ ÿµŸÅÿ±');
        }

        // Create the cut - convert numeric decimal fields to strings at persistence boundary
        const processedCutData = {
          ...cutData,
          cut_weight_kg: numberToDecimalString(cutData.cut_weight_kg, 3)
        };

        const [cut] = await tx
          .insert(cuts)
          .values(processedCutData)
          .returning();

        // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸáÿØÿ± ŸàÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑÿµÿßŸÅŸäÿ© ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸäÿ©
        const totalCutWeight = cutWeight;
        const waste = rollWeight - totalCutWeight;

        // ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ±ŸàŸÑ ŸÖÿπ ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑÿµÿßŸÅŸäÿ© ŸàÿßŸÑŸáÿØÿ±
        await tx
          .update(rolls)
          .set({
            cut_weight_total_kg: numberToDecimalString(totalCutWeight, 3),
            waste_kg: numberToDecimalString(waste, 3),
            stage: 'cutting', // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖÿ±ÿ≠ŸÑÿ© ÿ•ŸÑŸâ ÿ™ŸÖ ÿßŸÑÿ™ŸÇÿ∑Ÿäÿπ
            cut_completed_at: new Date(),
            cut_by: cutData.performed_by
          })
          .where(eq(rolls.id, cutData.roll_id));

        return cut;
      });
    } catch (error) {
      console.error('Error creating cut:', error);
      throw error;
    }
  }

