import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY 
});

interface LearningData {
  user_id: number;
  action_type: string;
  context: string;
  success: boolean;
  execution_time: number;
  user_feedback?: 'positive' | 'negative' | 'neutral';
  timestamp: Date;
}

interface LearningInsight {
  pattern: string;
  frequency: number;
  success_rate: number;
  recommendations: string[];
  improvement_areas: string[];
}

interface UserPattern {
  user_id: number;
  common_actions: string[];
  preferred_workflows: string[];
  peak_usage_times: string[];
  success_patterns: string[];
  areas_for_improvement: string[];
}

export class AILearning {
  private static learningData: LearningData[] = [];
  private static insights: LearningInsight[] = [];
  private static userPatterns: Map<number, UserPattern> = new Map();

  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ
  static async recordLearningData(
    userId: number,
    actionType: string,
    context: string,
    success: boolean,
    executionTime: number,
    userFeedback?: 'positive' | 'negative' | 'neutral'
  ): Promise<void> {
    try {
      const learningEntry: LearningData = {
        user_id: userId,
        action_type: actionType,
        context,
        success,
        execution_time: executionTime,
        user_feedback: userFeedback,
        timestamp: new Date()
      };

      this.learningData.push(learningEntry);

      // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ© ŸÑŸÑÿ≠ŸÅÿßÿ∏ ÿπŸÑŸâ ÿßŸÑÿ∞ÿßŸÉÿ±ÿ© (ÿßŸÑÿßÿ≠ÿ™ŸÅÿßÿ∏ ÿ®ÿ¢ÿÆÿ± 1000 ÿ•ÿØÿÆÿßŸÑ)
      const LEARNING_DATA_LIMIT = 1000;
      if (this.learningData.length > LEARNING_DATA_LIMIT) {
        this.learningData = this.learningData.slice(-LEARNING_DATA_LIMIT);
      }

      // ÿ™ÿ≠ÿØŸäÿ´ ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      await this.updateUserPatterns(userId);

      // ÿ™ÿ≠ŸÑŸäŸÑ ÿØŸàÿ±Ÿä ŸÉŸÑ 50 ÿ•ÿØÿÆÿßŸÑ ÿ¨ÿØŸäÿØ
      if (this.learningData.length % 50 === 0) {
        await this.analyzeLearningPatterns();
      }

      console.log(`üìä ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ: ${actionType} - ŸÜÿ¨ÿ≠: ${success} - ŸàŸÇÿ™ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞: ${executionTime}ms`);
    } catch (error) {
      console.error('Learning data recording error:', error);
    }
  }

  // ÿ™ÿ≠ŸÑŸäŸÑ ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑÿ™ÿπŸÑŸÖ
  private static async analyzeLearningPatterns(): Promise<void> {
    try {
      // ÿ™ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ≠ÿ≥ÿ® ŸÜŸàÿπ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°
      const actionGroups = this.groupByAction();

      for (const [actionType, data] of Object.entries(actionGroups)) {
        const insight = await this.generateActionInsight(actionType, data);
        if (insight) {
          this.insights.push(insight);
        }
      }

      // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ±ÿ§Ÿâ ÿßŸÑŸÇÿØŸäŸÖÿ© (ÿßŸÑÿßÿ≠ÿ™ŸÅÿßÿ∏ ÿ®ÿ¢ÿÆÿ± 100)
      if (this.insights.length > 100) {
        this.insights = this.insights.slice(-100);
      }

      console.log(`üß† ÿ™ŸÖ ÿ™ÿ≠ŸÑŸäŸÑ ${Object.keys(actionGroups).length} ŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™`);
    } catch (error) {
      console.error('Learning patterns analysis error:', error);
    }
  }

  // ÿ™ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ≠ÿ≥ÿ® ŸÜŸàÿπ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°
  private static groupByAction(): Record<string, LearningData[]> {
    const groups: Record<string, LearningData[]> = {};

    for (const entry of this.learningData) {
      if (!groups[entry.action_type]) {
        groups[entry.action_type] = [];
      }
      groups[entry.action_type].push(entry);
    }

    return groups;
  }

  // ÿ™ŸàŸÑŸäÿØ ÿ±ÿ§Ÿâ ŸÑŸÑÿ•ÿ¨ÿ±ÿßÿ°
  private static async generateActionInsight(actionType: string, data: LearningData[]): Promise<LearningInsight | null> {
    try {
      const successRate = data.filter(d => d.success).length / data.length;
      const avgExecutionTime = data.reduce((sum, d) => sum + d.execution_time, 0) / data.length;

      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: `ÿ£ŸÜÿ™ ŸÜÿ∏ÿßŸÖ ÿ™ÿπŸÑŸÖ ÿ¢ŸÑŸä ŸÖÿ™ÿÆÿµÿµ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿ£ŸÜŸÖÿßÿ∑ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÜÿ∏ÿßŸÖ. ÿ≠ŸÑŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿßŸÑŸäÿ© ŸàŸÇÿØŸÖ ÿ±ÿ§Ÿâ ÿ®ÿ™ŸÜÿ≥ŸäŸÇ JSON:

{
  "pattern": "ŸàÿµŸÅ ÿßŸÑŸÜŸÖÿ∑ ÿßŸÑŸÖŸÉÿ™ÿ¥ŸÅ",
  "recommendations": ["ÿ™ŸàÿµŸäÿ© 1", "ÿ™ŸàÿµŸäÿ© 2", "..."],
  "improvement_areas": ["ŸÖÿ¨ÿßŸÑ ÿ™ÿ≠ÿ≥ŸäŸÜ 1", "ŸÖÿ¨ÿßŸÑ ÿ™ÿ≠ÿ≥ŸäŸÜ 2", "..."]
}

ÿ±ŸÉÿ≤ ÿπŸÑŸâ:
- ÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ£ÿØÿßÿ°
- ÿ™ŸÇŸÑŸäŸÑ ŸàŸÇÿ™ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞
- ÿ≤ŸäÿßÿØÿ© ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠
- ÿ™ÿ≠ÿ≥ŸäŸÜ ÿ™ÿ¨ÿ±ÿ®ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ`
          },
          {
            role: "user",
            content: `ŸÜŸàÿπ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°: ${actionType}
ÿπÿØÿØ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿßÿ™: ${data.length}
ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠: ${(successRate * 100).toFixed(1)}%
ŸÖÿ™Ÿàÿ≥ÿ∑ ŸàŸÇÿ™ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞: ${avgExecutionTime.toFixed(0)}ms
ÿßŸÑÿ™ÿπŸÑŸäŸÇÿßÿ™ ÿßŸÑÿ•Ÿäÿ¨ÿßÿ®Ÿäÿ©: ${data.filter(d => d.user_feedback === 'positive').length}
ÿßŸÑÿ™ÿπŸÑŸäŸÇÿßÿ™ ÿßŸÑÿ≥ŸÑÿ®Ÿäÿ©: ${data.filter(d => d.user_feedback === 'negative').length}

ÿ≠ŸÑŸÑ Ÿáÿ∞Ÿá ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸàŸÇÿØŸÖ ÿ±ÿ§Ÿâ ŸÑŸÑÿ™ÿ≠ÿ≥ŸäŸÜ.`
          }
        ],
        response_format: { type: "json_object" },
        temperature: 0.3
      });

      const result = JSON.parse(response.choices[0].message.content || '{}');

      return {
        pattern: result.pattern || `ŸÜŸÖÿ∑ ${actionType}`,
        frequency: data.length,
        success_rate: successRate,
        recommendations: result.recommendations || [],
        improvement_areas: result.improvement_areas || []
      };
    } catch (error) {
      console.error('Action insight generation error:', error);
      return null;
    }
  }

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  private static async updateUserPatterns(userId: number): Promise<void> {
    try {
      const userData = this.learningData.filter(d => d.user_id === userId);

      if (userData.length < 10) return; // ŸÜÿ≠ÿ™ÿßÿ¨ ÿ®ŸäÿßŸÜÿßÿ™ ŸÉÿßŸÅŸäÿ© ŸÑŸÑÿ™ÿ≠ŸÑŸäŸÑ

      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: `ÿ≠ŸÑŸÑ ÿ£ŸÜŸÖÿßÿ∑ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸàŸÇÿØŸÖ ÿ™ÿ≠ŸÑŸäŸÑÿßŸã ÿ®ÿ™ŸÜÿ≥ŸäŸÇ JSON:

{
  "common_actions": ["ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑÿ£ŸÉÿ´ÿ± ÿ¥ŸäŸàÿπÿßŸã"],
  "preferred_workflows": ["ÿ≥ŸÑÿßÿ≥ŸÑ ÿßŸÑÿπŸÖŸÑ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©"],
  "peak_usage_times": ["ÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ∞ÿ±Ÿàÿ©"],
  "success_patterns": ["ÿßŸÑÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑŸÜÿßÿ¨ÿ≠ÿ©"],
  "areas_for_improvement": ["ŸÖÿ¨ÿßŸÑÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ≥ŸäŸÜ"]
}`
          },
          {
            role: "user",
            content: `ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}:
ÿπÿØÿØ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™: ${userData.length}
ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: ${(userData.filter(d => d.success).length / userData.length * 100).toFixed(1)}%
ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑÿ¥ÿßÿ¶ÿπÿ©: ${Array.from(new Set(userData.map(d => d.action_type))).join(', ')}
ŸÖÿ™Ÿàÿ≥ÿ∑ ŸàŸÇÿ™ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞: ${(userData.reduce((sum, d) => sum + d.execution_time, 0) / userData.length).toFixed(0)}ms

ÿ≠ŸÑŸÑ ÿ£ŸÜŸÖÿßÿ∑ Ÿáÿ∞ÿß ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ.`
          }
        ],
        response_format: { type: "json_object" },
        temperature: 0.3
      });

      const result = JSON.parse(response.choices[0].message.content || '{}');

      this.userPatterns.set(userId, {
        user_id: userId,
        common_actions: result.common_actions || [],
        preferred_workflows: result.preferred_workflows || [],
        peak_usage_times: result.peak_usage_times || [],
        success_patterns: result.success_patterns || [],
        areas_for_improvement: result.areas_for_improvement || []
      });

      console.log(`üë§ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}`);
    } catch (error) {
      console.error('User patterns update error:', error);
    }
  }

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ™ŸàÿµŸäÿßÿ™ ŸÖÿÆÿµÿµÿ© ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  static async getPersonalizedRecommendations(userId: number): Promise<string[]> {
    try {
      const userPattern = this.userPatterns.get(userId);
      const userData = this.learningData.filter(d => d.user_id === userId);

      if (!userPattern || userData.length < 5) {
        return [
          "ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿµŸàÿ™Ÿä ŸÑÿ™ÿ≥ÿ±Ÿäÿπ ÿßŸÑÿπŸÖŸÑŸäÿßÿ™",
          "ÿßÿ≥ÿ™ŸÅÿØ ŸÖŸÜ ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿßŸÑÿ∞ŸÉŸäÿ© ŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ£ÿØÿßÿ°",
          "ÿ±ÿßÿ¨ÿπ ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ ŸÑÿ™ÿÆÿµŸäÿµ ÿ™ÿ¨ÿ±ÿ®ÿ™ŸÉ"
        ];
      }

      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: `ÿ£ŸÜÿ™ ŸÖÿ≥ÿ™ÿ¥ÿßÿ± ÿ∞ŸÉŸä ŸÑÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨Ÿäÿ©. ŸÇÿØŸÖ ÿ™ŸàÿµŸäÿßÿ™ ŸÖÿÆÿµÿµÿ© ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿ£ŸÜŸÖÿßÿ∑ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖŸá.

ÿ£ÿ±ÿ¨ÿπ ŸÇÿßÿ¶ŸÖÿ© ŸÖŸÜ ÿßŸÑÿ™ŸàÿµŸäÿßÿ™ ÿßŸÑÿπŸÖŸÑŸäÿ© ÿ®ÿ™ŸÜÿ≥ŸäŸÇ JSON:
{
  "recommendations": ["ÿ™ŸàÿµŸäÿ© 1", "ÿ™ŸàÿµŸäÿ© 2", "..."]
}`
          },
          {
            role: "user",
            content: `ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}:
ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑÿ¥ÿßÿ¶ÿπÿ©: ${userPattern.common_actions.join(', ')}
ÿ≥ŸÑÿßÿ≥ŸÑ ÿßŸÑÿπŸÖŸÑ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©: ${userPattern.preferred_workflows.join(', ')}
ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠: ${(userData.filter(d => d.success).length / userData.length * 100).toFixed(1)}%
ŸÖÿ¨ÿßŸÑÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ≥ŸäŸÜ: ${userPattern.areas_for_improvement.join(', ')}

ŸÇÿØŸÖ ÿ™ŸàÿµŸäÿßÿ™ ÿ¥ÿÆÿµŸäÿ© ŸÑÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨Ÿäÿ©.`
          }
        ],
        response_format: { type: "json_object" },
        temperature: 0.4
      });

      const result = JSON.parse(response.choices[0].message.content || '{}');
      return result.recommendations || [];
    } catch (error) {
      console.error('Personalized recommendations error:', error);
      return ["ÿßÿ≥ÿ™ŸÖÿ± ŸÅŸä ÿßÿ≥ÿ™ŸÉÿ¥ÿßŸÅ ŸÖŸäÿ≤ÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ ŸÑÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨Ÿäÿ©"];
    }
  }

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ
  static getLearningStatistics(): any {
    const totalActions = this.learningData.length;
    const successfulActions = this.learningData.filter(d => d.success).length;
    const averageExecutionTime = totalActions > 0 
      ? this.learningData.reduce((sum, d) => sum + d.execution_time, 0) / totalActions 
      : 0;

    const actionTypes = Array.from(new Set(this.learningData.map(d => d.action_type)));
    const uniqueUsers = Array.from(new Set(this.learningData.map(d => d.user_id)));

    return {
      total_actions: totalActions,
      success_rate: totalActions > 0 ? (successfulActions / totalActions * 100).toFixed(1) + '%' : '0%',
      average_execution_time: averageExecutionTime.toFixed(0) + 'ms',
      unique_action_types: actionTypes.length,
      active_users: uniqueUsers.length,
      insights_generated: this.insights.length,
      user_patterns_tracked: this.userPatterns.size
    };
  }

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ±ÿ§Ÿâ ŸÖÿ≠ÿØÿØÿ©
  static getInsightsByAction(actionType: string): LearningInsight[] {
    return this.insights.filter(insight => 
      insight.pattern.toLowerCase().includes(actionType.toLowerCase())
    );
  }

  // ÿ™ÿµÿØŸäÿ± ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ (ŸÑŸÑŸÜÿ≥ÿÆ ÿßŸÑÿßÿ≠ÿ™Ÿäÿßÿ∑Ÿä ÿ£Ÿà ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ)
  static exportLearningData(): any {
    return {
      learning_data: this.learningData,
      insights: this.insights,
      user_patterns: Array.from(this.userPatterns.entries()),
      export_timestamp: new Date().toISOString()
    };
  }

  // ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ
  static importLearningData(data: any): void {
    try {
      if (data.learning_data) {
        this.learningData = data.learning_data;
      }
      if (data.insights) {
        this.insights = data.insights;
      }
      if (data.user_patterns) {
        this.userPatterns = new Map(data.user_patterns);
      }

      console.log('üì• ÿ™ŸÖ ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ ÿ®ŸÜÿ¨ÿßÿ≠');
    } catch (error) {
      console.error('Learning data import error:', error);
    }
  }

  // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ© (ÿ£ŸÉÿ´ÿ± ŸÖŸÜ 30 ŸäŸàŸÖ)
  static cleanupOldData(): void {
    const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);

    const beforeCount = this.learningData.length;
    this.learningData = this.learningData.filter(d => d.timestamp > thirtyDaysAgo);
    const afterCount = this.learningData.length;

    console.log(`üßπ ÿ™ŸÖ ÿ™ŸÜÿ∏ŸäŸÅ ${beforeCount - afterCount} ÿ•ÿØÿÆÿßŸÑ ŸÇÿØŸäŸÖ ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸÖ`);
  }
}

// ÿ™ŸÜÿ∏ŸäŸÅ ÿØŸàÿ±Ÿä ŸÑŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ© (ŸÉŸÑ 24 ÿ≥ÿßÿπÿ©)
setInterval(() => {
  AILearning.cleanupOldData();
}, 24 * 60 * 60 * 1000);

