import type { IStorage } from '../storage';

export interface MetaWhatsAppConfig {
  accessToken: string;
  phoneNumberId: string;
  businessAccountId: string;
  apiVersion: string;
}

export interface WhatsAppTemplateMessage {
  to: string;
  type: 'template';
  template: {
    name: string;
    language: {
      code: string;
    };
    components?: Array<{
      type: string;
      parameters?: Array<{
        type: string;
        text: string;
      }>;
    }>;
  };
}

export interface WhatsAppTextMessage {
  to: string;
  type: 'text';
  text: {
    body: string;
  };
}

export class MetaWhatsAppService {
  private config: MetaWhatsAppConfig;
  private storage: IStorage;
  private baseUrl: string;

  constructor(storage: IStorage, config?: Partial<MetaWhatsAppConfig>) {
    this.storage = storage;

    this.config = {
      accessToken: process.env.META_ACCESS_TOKEN || '',
      phoneNumberId: process.env.META_PHONE_NUMBER_ID || '',
      businessAccountId: process.env.META_BUSINESS_ACCOUNT_ID || '795259496521200',
      apiVersion: 'v21.0',
      ...config
    };

    this.baseUrl = `https://graph.facebook.com/${this.config.apiVersion}`;

    if (!this.config.accessToken || !this.config.phoneNumberId) {
      console.warn('‚ö†Ô∏è Meta WhatsApp API credentials not configured. Set META_ACCESS_TOKEN and META_PHONE_NUMBER_ID environment variables.');
    } else {
      console.log('‚úÖ Meta WhatsApp API service initialized successfully');
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÜÿµŸäÿ© ŸÖÿ®ÿßÿ¥ÿ±ÿ©
   */
  async sendTextMessage(
    to: string,
    message: string,
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error('Meta WhatsApp API ÿ∫Ÿäÿ± ŸÖŸèÿπÿØ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      // ÿ™ŸÜÿ≥ŸäŸÇ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ (ÿ•ÿ≤ÿßŸÑÿ© + Ÿàwhatsapp: ÿ•ŸÜ Ÿàÿ¨ÿØÿ™)
      const formattedPhone = to.replace(/[\+\s\-\(\)]/g, '').replace('whatsapp:', '');

      const messageData: WhatsAppTextMessage = {
        to: formattedPhone,
        type: 'text',
        text: {
          body: message
        }
      };

      const response = await fetch(`${this.baseUrl}/${this.config.phoneNumberId}/messages`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.config.accessToken}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(messageData)
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}: ${response.statusText}`);
      }

      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const notificationData = {
        title: options?.title || 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: message,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: to,
        status: 'sent' as const,
        external_id: result.messages?.[0]?.id,
        external_status: 'sent',
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(`üì± ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ŸÖÿ®ÿßÿ¥ÿ±ÿ© ÿ•ŸÑŸâ ${to} - ID: ${result.messages?.[0]?.id}`);

      return {
        success: true,
        messageId: result.messages?.[0]?.id
      };

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ÿπÿ®ÿ± Meta API:', error);

      const notificationData = {
        title: options?.title || 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: message,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: to,
        status: 'failed' as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÇÿßŸÑÿ® Meta ŸÖŸèŸàÿßŸÅŸÇ ÿπŸÑŸäŸá
   */
  async sendTemplateMessage(
    to: string,
    templateName: string,
    language: string = 'ar',
    variables: string[] = [],
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error('Meta WhatsApp API ÿ∫Ÿäÿ± ŸÖŸèÿπÿØ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      // ÿ™ŸÜÿ≥ŸäŸÇ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ
      const formattedPhone = to.replace(/[\+\s\-\(\)]/g, '').replace('whatsapp:', '');

      const messageData: WhatsAppTemplateMessage = {
        to: formattedPhone,
        type: 'template',
        template: {
          name: templateName,
          language: {
            code: language
          }
        }
      };

      // ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑŸÇÿßŸÑÿ® ÿ•ŸÜ Ÿàÿ¨ÿØÿ™
      if (variables && variables.length > 0) {
        messageData.template.components = [
          {
            type: 'body',
            parameters: variables.map(variable => ({
              type: 'text',
              text: variable
            }))
          }
        ];
      }

      const response = await fetch(`${this.baseUrl}/${this.config.phoneNumberId}/messages`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.config.accessToken}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(messageData)
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}: ${response.statusText}`);
      }

      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const notificationData = {
        title: options?.title || 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ®)',
        message: `ŸÇÿßŸÑÿ®: ${templateName} - ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™: ${variables.join(', ')}`,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: to,
        status: 'sent' as const,
        external_id: result.messages?.[0]?.id,
        external_status: 'sent',
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(`üì± ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ® Meta) ÿ•ŸÑŸâ ${to} - ID: ${result.messages?.[0]?.id}`);

      return {
        success: true,
        messageId: result.messages?.[0]?.id
      };

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ® Meta):', error);

      const notificationData = {
        title: options?.title || 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ®)',
        message: `ŸÇÿßŸÑÿ®: ${templateName} - ÿÆÿ∑ÿ£: ${error.message}`,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: to,
        status: 'failed' as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä
   */
  async getPhoneNumberInfo(): Promise<any> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error('Meta WhatsApp API ÿ∫Ÿäÿ± ŸÖŸèÿπÿØ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      const response = await fetch(
        `${this.baseUrl}/${this.config.phoneNumberId}?fields=display_phone_number,verified_name,quality_rating`,
        {
          headers: {
            'Authorization': `Bearer ${this.config.accessToken}`
          }
        }
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}`);
      }

      return result;

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ±ŸÇŸÖ:', error);
      throw error;
    }
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÇŸàÿßŸÑÿ® ÿßŸÑŸÖŸèŸàÿßŸÅŸÇÿ©
   */
  async getApprovedTemplates(): Promise<any[]> {
    try {
      if (!this.config.accessToken || !this.config.businessAccountId) {
        throw new Error('Meta WhatsApp API ÿ∫Ÿäÿ± ŸÖŸèÿπÿØ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      const response = await fetch(
        `${this.baseUrl}/${this.config.businessAccountId}/message_templates?fields=name,status,language,components`,
        {
          headers: {
            'Authorization': `Bearer ${this.config.accessToken}`
          }
        }
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}`);
      }

      // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÇŸàÿßŸÑÿ® ÿßŸÑŸÖŸèŸàÿßŸÅŸÇÿ© ŸÅŸÇÿ∑
      return result.data?.filter((template: any) => template.status === 'APPROVED') || [];

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑŸÇŸàÿßŸÑÿ®:', error);
      throw error;
    }
  }

  /**
   * ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑÿ•ÿπÿØÿßÿØ
   */
  async testConnection(): Promise<{ success: boolean; error?: string; data?: any }> {
    try {
      const phoneInfo = await this.getPhoneNumberInfo();
      return {
        success: true,
        data: phoneInfo
      };
    } catch (error: any) {
      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * ŸÖÿπÿßŸÑÿ¨ÿ© Webhook ÿßŸÑŸàÿßÿ±ÿØÿ© ŸÖŸÜ Meta
   */
  async handleWebhook(body: any): Promise<void> {
    try {
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ
      if (body.entry && body.entry[0] && body.entry[0].changes) {
        for (const change of body.entry[0].changes) {
          if (change.field === 'messages') {
            const value = change.value;

            // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ
            if (value.statuses) {
              for (const status of value.statuses) {
                await this.updateMessageStatus(status.id, status.status);
              }
            }

            // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ ÿßŸÑŸàÿßÿ±ÿØÿ©
            if (value.messages) {
              for (const message of value.messages) {
                await this.handleIncomingMessage(message);
              }
            }
          }
        }
      }
    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ŸÖÿπÿßŸÑÿ¨ÿ© Webhook:', error);
    }
  }

  /**
   * ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©
   */
  private async updateMessageStatus(messageId: string, status: string): Promise<void> {
    try {
      // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const notifications = await this.storage.getNotifications();
      const notification = notifications.find(n => n.twilio_sid === messageId || (n as any).external_id === messageId);

      if (notification) {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ©
        const updatedNotification = {
          ...notification,
          external_status: status,
          delivered_at: status === 'delivered' ? new Date() : notification.delivered_at,
          read_at: status === 'read' ? new Date() : notification.read_at
        };

        console.log(`üìä ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ${messageId}: ${status}`);
      }
    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©:', error);
    }
  }

  /**
   * ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ ÿßŸÑŸàÿßÿ±ÿØÿ©
   */
  private async handleIncomingMessage(message: any): Promise<void> {
    try {
      console.log('üì® ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ±ÿØÿ©:', {
        from: message.from,
        type: message.type,
        text: message.text?.body || 'ÿ∫Ÿäÿ± ŸÜÿµŸäÿ©'
      });

      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑŸàÿßÿ±ÿØÿ© ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const notificationData = {
        title: 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ±ÿØÿ©',
        message: message.text?.body || 'ÿ±ÿ≥ÿßŸÑÿ© ÿ∫Ÿäÿ± ŸÜÿµŸäÿ©',
        type: 'whatsapp' as const,
        priority: 'normal',
        recipient_type: 'system' as const,
        phone_number: message.from,
        status: 'received' as const,
        external_id: message.id,
        external_status: 'received',
        received_at: new Date(),
        context_type: 'incoming_message'
      };

      await this.storage.createNotification(notificationData);

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑŸàÿßÿ±ÿØÿ©:', error);
    }
  }
}

