  }

  // ÿ™ŸÜŸÅŸäÿ∞ ÿßÿ≥ÿ™ÿπŸÑÿßŸÖ ÿ¢ŸÖŸÜ
  private async executeSafeQuery(sql: string): Promise<any> {
