import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import type { AuthUser } from "@/types";

interface AuthContextType {
  user: AuthUser | null;
  login: (username: string, password: string) => Promise<void>;
  logout: () => void;
  isLoading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check for current user session via API
    const checkAuth = async () => {
      try {
        // First check localStorage for cached user data
        const cachedUser = localStorage.getItem('mpbf_user');
        if (cachedUser) {
          try {
            const parsedUser = JSON.parse(cachedUser);
            setUser(parsedUser);
            setIsLoading(false);
          } catch (e) {
            localStorage.removeItem('mpbf_user');
          }
        }

        // Then verify with server
        const response = await fetch('/api/me', {
          credentials: 'include' // Include cookies for session validation
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
          localStorage.setItem('mpbf_user', JSON.stringify(data.user));
        } else {
          // No active session on server
          setUser(null);
          localStorage.removeItem('mpbf_user');
        }
      } catch (error) {
        console.warn('Error checking auth session:', error);
        // Keep cached user if server is unreachable - don't logout user on network errors
        const cachedUser = localStorage.getItem('mpbf_user');
        if (cachedUser && !user) {
          try {
            const parsedUser = JSON.parse(cachedUser);
            setUser(parsedUser);
          } catch (e) {
            localStorage.removeItem('mpbf_user');
            setUser(null);
          }
        }
        // Don't logout on network errors - preserve existing auth state
      }
      setIsLoading(false);
    };

    checkAuth();
  }, []);

  const login = async (username: string, password: string) => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Ensure cookies are included in requests
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ');
      }

      const data = await response.json();
      setUser(data.user);
      localStorage.setItem('mpbf_user', JSON.stringify(data.user));
    } catch (error) {
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const logout = async () => {
    try {
      await fetch('/api/logout', { 
        method: 'POST',
        credentials: 'include' // Ensure cookies are included
      });
    } catch (error) {
      console.warn('Error during logout:', error);
    }
    setUser(null);
    localStorage.removeItem('mpbf_user');
    // Clear any cached queries related to user data
    if (typeof window !== 'undefined') {
      window.location.reload();
    }
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, isLoading }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

