import { QueryClient, QueryFunction, QueryCache, MutationCache } from "@tanstack/react-query";

// Create a single instance to prevent multiple React contexts
let globalQueryClient: QueryClient | undefined;

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    let errorMessage = res.statusText || 'Unknown error';

    try {
      // Clone the response to avoid consuming the body stream
      const responseClone = res.clone();
      const text = await responseClone.text();

      if (text.trim()) {
        try {
          const errorData = JSON.parse(text);
          errorMessage = errorData.message || errorData.error || errorData.detail || text;
        } catch {
          // If JSON parsing fails, use the raw text if it's meaningful
          errorMessage = text.length > 200 ? text.substring(0, 200) + '...' : text;
        }
      }
    } catch {
      // If we can't read the response body, use status-based error messages
      errorMessage = getStatusMessage(res.status);
    }

    const error = new Error(`${res.status}: ${errorMessage}`);
    (error as any).status = res.status;
    (error as any).statusText = res.statusText;
    throw error;
  }
}

function getStatusMessage(status: number): string {
  switch (status) {
    case 400: return 'ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖŸèÿ±ÿ≥ŸÑÿ© ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©. Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑŸÖÿØÿÆŸÑÿßÿ™.';
    case 401: return 'ÿßŸÜÿ™Ÿáÿ™ ÿµŸÑÿßÿ≠Ÿäÿ© ÿ¨ŸÑÿ≥ÿ™ŸÉ. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.';
    case 403: return 'ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿµŸÑÿßÿ≠Ÿäÿ© ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ Ÿáÿ∞ÿß ÿßŸÑŸÖŸàÿ±ÿØ.';
    case 404: return 'ÿßŸÑŸÖŸàÿ±ÿØ ÿßŸÑŸÖÿ∑ŸÑŸàÿ® ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ.';
    case 409: return 'ÿ™ÿπÿßÿ±ÿ∂ ŸÅŸä ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™. ŸÇÿØ ŸäŸÉŸàŸÜ ÿßŸÑŸÖŸàÿ±ÿØ ŸÖŸàÿ¨ŸàÿØ ŸÖÿ≥ÿ®ŸÇÿßŸã.';
    case 422: return 'ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑŸÖÿØÿÆŸÑÿßÿ™.';
    case 429: return 'ÿ∑ŸÑÿ®ÿßÿ™ ŸÉÿ´Ÿäÿ±ÿ© ÿ¨ÿØÿßŸã. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ÿ®ÿπÿØ ŸÇŸÑŸäŸÑ.';
    case 500: return 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ŸÑÿßÿ≠ŸÇÿßŸã.';
    case 502: return 'ÿßŸÑÿÆÿØŸÖÿ© ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ÿ© ŸÖÿ§ŸÇÿ™ÿßŸã. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.';
    case 503: return 'ÿßŸÑÿÆÿØŸÖÿ© ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿßŸÑŸäÿßŸã. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ŸÑÿßÿ≠ŸÇÿßŸã.';
    case 504: return 'ÿßŸÜÿ™Ÿáÿ™ ŸÖŸáŸÑÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.';
    default: return `ÿÆÿ∑ÿ£ ${status} - ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ`;
  }
}

export async function apiRequest(
  url: string,
  options?: {
    method?: string;
    body?: string;
    timeout?: number;
  }
): Promise<Response> {
  const { method = 'GET', body, timeout = 30000 } = options || {};

  try {
    // Create timeout controller
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    const res = await fetch(url, {
      method,
      headers: body ? { "Content-Type": "application/json" } : {},
      body,
      credentials: "include",
      signal: controller.signal,
    });

    clearTimeout(timeoutId);
    await throwIfResNotOk(res);
    return res;

  } catch (error: any) {
    // Handle specific error types with meaningful messages
    if (error.name === 'AbortError') {
      const timeoutError = new Error('ÿßŸÜÿ™Ÿáÿ™ ŸÖŸáŸÑÿ© ÿßŸÑÿ∑ŸÑÿ® - Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ');
      (timeoutError as any).type = 'timeout';
      throw timeoutError;
    }

    if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
      const networkError = new Error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ¥ÿ®ŸÉÿ© - Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™');
      (networkError as any).type = 'network';
      throw networkError;
    }

    // Re-throw error as-is
    throw error;
  }
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey, signal }) => {
    try {
      const url = queryKey.join("/") as string;

      const res = await fetch(url, {
        credentials: "include",
        signal, // Let React Query handle cancellation properly
      });

      if (unauthorizedBehavior === "returnNull" && res.status === 401) {
        return null;
      }

      await throwIfResNotOk(res);

      // Handle empty responses gracefully
      const contentType = res.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        if (res.status === 204) return null; // No Content
        const text = await res.text();
        if (!text.trim()) return null; // Empty response
        throw new Error('Invalid response - expected JSON');
      }

      try {
        const data = await res.json();
        return data;
      } catch (jsonError) {
        throw new Error('Invalid response - malformed data');
      }

    } catch (error: any) {
      // Handle AbortError gracefully during query cancellation
      if (error.name === 'AbortError') {
        // If signal was aborted, this is normal during component cleanup
        // Log debug info but don't create console noise
        console.debug('Query cancelled during cleanup:', queryKey);
        throw error; // Still throw to signal cancellation to React Query
      }

      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        throw new Error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ¥ÿ®ŸÉÿ© - Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™');
      }

      // Re-throw all other errors as-is for proper error handling
      throw error;
    }
  };

export function getQueryClient(): QueryClient {
  if (!globalQueryClient) {
    globalQueryClient = new QueryClient({
      defaultOptions: {
        queries: {
          queryFn: getQueryFn({ on401: "throw" }),
          refetchInterval: false,
          refetchOnWindowFocus: false,
          refetchOnMount: true,
          refetchOnReconnect: 'always',
          // Increase staleTime to reduce unnecessary refetches
          staleTime: 2 * 60 * 1000, // 2 minutes - data considered fresh longer
          gcTime: 10 * 60 * 1000, // 10 minutes garbage collection - keep data longer
          // Prevent excessive retries that can cause cancellation issues
          retry: (failureCount, error: any) => {
            // Don't retry after 2 attempts (reduced from 3)
            if (failureCount > 1) return false;

            // Never retry AbortError (query cancellation)
            if (error?.name === 'AbortError') return false;

            // Don't retry client errors (4xx) - these need user action
            if (error?.status >= 400 && error?.status < 500) return false;

            // Don't retry timeout errors
            if (error?.type === 'timeout') return false;

            // Only retry network errors and server errors (5xx) once
            if (error?.type === 'network' || (error?.status >= 500)) return failureCount < 1;

            // Don't retry other errors to prevent cascading cancellations
            return false;
          },
          retryDelay: attemptIndex => Math.min(2000 * 2 ** attemptIndex, 10000), // Faster exponential backoff, max 10s
          // Disable automatic background refetching that can cause cancellations
          refetchIntervalInBackground: false,
        },
        mutations: {
          retry: (failureCount, error: any) => {
            // Don't retry mutations at all to avoid duplicate operations
            return false;
          },
          // Remove retryDelay for mutations since we're not retrying
        },
      },
      // Add global query cancellation optimization
      queryCache: new QueryCache({
        onError: (error, query) => {
          // Silently handle AbortErrors during development to reduce console noise
          if (import.meta.env.DEV && error?.name === 'AbortError') {
            console.debug('Query cancelled during cleanup:', query.queryKey);
            return;
          }
          // Let other errors propagate normally
        },
      }),
      // Add mutation cache error handling
      mutationCache: new MutationCache({
        onError: (error, _variables, _context, mutation) => {
          // Silently handle AbortErrors during development
          if (import.meta.env.DEV && error?.name === 'AbortError') {
            console.debug('Mutation cancelled:', mutation.options.mutationKey);
            return;
          }
          // Let other errors propagate normally
        },
      }),
    });
  }
  return globalQueryClient;
}

export const queryClient = getQueryClient();

