// Spare Part Form Component
function SparePartForm({ onSubmit, isLoading }: { onSubmit: (data: any) => void, isLoading: boolean }) {
  const { data: spareParts } = useQuery({ queryKey: ["/api/spare-parts"] });
  const { data: machines } = useQuery({ queryKey: ["/api/machines"] });

  // Generate next part ID automatically
  const generateNextPartId = (currentSpareParts: any[]) => {
    if (!Array.isArray(currentSpareParts)) return 'SP001';

    const partNumbers = currentSpareParts
      .map((part: any) => part.part_id)
      .filter((id: string) => id && id.match(/^SP\d+$/))
      .map((id: string) => parseInt(id.replace('SP', '')))
      .filter((num: number) => !isNaN(num));

    const nextNumber = partNumbers.length > 0 ? Math.max(...partNumbers) + 1 : 1;
    return `SP${nextNumber.toString().padStart(3, '0')}`;
  };

  const form = useForm({
    defaultValues: {
      part_id: 'SP001',
      machine_name: '',
      part_name: '',
      code: '',
      serial_number: '',
      specifications: ''
    }
  });

  // Update part_id when spare parts data changes
  useEffect(() => {
    if (spareParts && Array.isArray(spareParts)) {
      const nextId = generateNextPartId(spareParts);
      if (nextId !== form.getValues('part_id')) {
        form.setValue('part_id', nextId);
      }
    }
  }, [spareParts, form]);

  const handleSubmit = (data: any) => {
    onSubmit(data);
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="part_id"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿ±ŸÇŸÖ ÿßŸÑŸÇÿ∑ÿπÿ© (ÿ™ŸÑŸÇÿßÿ¶Ÿä)</FormLabel>
                <FormControl>
                  <Input {...field} disabled className="bg-gray-100" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="code"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿßŸÑŸÉŸàÿØ</FormLabel>
                <FormControl>
                  <Input placeholder="A8908" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="machine_name"
          render={({ field }) => (
            <FormItem>
              <FormLabel>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿßŸÉŸäŸÜÿ©</FormLabel>
              <FormControl>
                <Select onValueChange={field.onChange} value={field.value}>
                  <SelectTrigger>
                    <SelectValue placeholder="ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿßŸÉŸäŸÜÿ©" />
                  </SelectTrigger>
                  <SelectContent>
                    {Array.isArray(machines) && machines.length > 0 ? (
                      machines.map((machine: any) => (
                        <SelectItem key={machine.id} value={machine.id ? `machine_${machine.id}` : "unknown"}>
                          {machine.name_ar || machine.name} ({machine.id})
                        </SelectItem>
                      ))
                    ) : (
                      <SelectItem value="no_machines">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖÿßŸÉŸäŸÜÿßÿ™ ŸÖÿ™ÿßÿ≠ÿ©</SelectItem>
                    )}
                  </SelectContent>
                </Select>
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="part_name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿßÿ≥ŸÖ ÿßŸÑŸÇÿ∑ÿπÿ©</FormLabel>
                <FormControl>
                  <Input placeholder="ŸÖÿßÿ∑Ÿàÿ±" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="serial_number"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿßŸÑÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ≥ŸÑÿ≥ŸÑŸä</FormLabel>
                <FormControl>
                  <Input placeholder="E5SH973798" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="specifications"
          render={({ field }) => (
            <FormItem>
              <FormLabel>ÿßŸÑŸÖŸàÿßÿµŸÅÿßÿ™</FormLabel>
              <FormControl>
                <Textarea placeholder="ŸÇŸàÿ© 380 ŸÅŸàŸÑÿ™ Ÿà 10 ÿßŸÖÿ®Ÿäÿ±" {...field} />
