    tableCount: 8,
    totalRecords: 1247,
    databaseSize: '45.2 MB',
    lastBackup: 'ÿßŸÑŸäŸàŸÖ'
  });

  // System settings state
  const [systemSettings, setSystemSettings] = useState({
    companyName: "ŸÖÿµŸÜÿπ ÿ£ŸÉŸäÿßÿ≥ MPBF",
    timezone: "Asia/Riyadh",
    currency: "SAR",
    language: "ar",
    dateFormat: "DD/MM/YYYY",
    country: "ÿßŸÑŸÖŸÖŸÑŸÉÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©",
    region: "ÿßŸÑÿ±Ÿäÿßÿ∂",
    workingHours: {
      start: "08:00",
      end: "17:00"
    },
    shifts: [
      { id: 1, name: "ÿßŸÑÿµÿ®ÿßÿ≠Ÿäÿ©", start: "08:00", end: "16:00" },
      { id: 2, name: "ÿßŸÑŸÖÿ≥ÿßÿ¶Ÿäÿ©", start: "16:00", end: "00:00" },
      { id: 3, name: "ÿßŸÑŸÑŸäŸÑŸäÿ©", start: "00:00", end: "08:00" }
    ],
    backup: {
      enabled: true,
      frequency: "daily",
      retention: 30
    }
  });

  // Load settings from database when data is available
  useEffect(() => {
    if (systemSettingsData && Array.isArray(systemSettingsData)) {
