  // Parse file data based on format
  const parseFileData = async (file: File) => {
    try {
      const fileText = await file.text();
      let data: any[] = [];
      let headers: string[] = [];

      if (file.name.endsWith('.json')) {
        const jsonData = JSON.parse(fileText);
        if (Array.isArray(jsonData) && jsonData.length > 0) {
          data = jsonData;
          headers = Object.keys(jsonData[0]);
        }
      } else if (file.name.endsWith('.csv')) {
        const lines = fileText.split('\n').filter(line => line.trim());
        if (lines.length > 0) {
          headers = lines[0].split(',').map(h => h.trim().replace(/"/g, ''));
          data = lines.slice(1).map(line => {
            const values = line.split(',').map(v => v.trim().replace(/"/g, ''));
            const row: any = {};
            headers.forEach((header, index) => {
              row[header] = values[index] || '';
            });
            return row;
          });
        }
      } else if (file.name.endsWith('.xlsx')) {
        // For Excel files, we'll parse them on the server side
        headers = ['Column 1', 'Column 2', 'Column 3']; // Placeholder
        data = [{ 'Column 1': 'ÿ≥Ÿäÿ™ŸÖ ÿ™ÿ≠ŸÑŸäŸÑ ŸÖŸÑŸÅ Excel ÿπŸÑŸâ ÿßŸÑÿÆÿßÿØŸÖ', 'Column 2': '', 'Column 3': '' }];
      }

