import { useState, useEffect } from 'react';
import Header from "@/components/layout/Header";
import Sidebar from "@/components/layout/Sidebar";
import MobileNav from "@/components/layout/MobileNav";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar, Clock, MapPin, User, AlertTriangle, FileText, CheckCircle, XCircle } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { formatNumber } from "@/lib/formatNumber";

// Types for dashboard data
interface UserData {
  id: number;
  username: string;
  email?: string;
  full_name?: string;
  position?: string;
  department?: string;
  hire_date?: string;
  phone?: string;
}

interface AttendanceRecord {
  id: number;
  user_id: number;
  status: 'ÿ≠ÿßÿ∂ÿ±' | 'ÿ∫ÿßÿ¶ÿ®' | 'ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿ∫ÿØÿßÿ°' | 'ŸÖÿ∫ÿßÿØÿ±';
  check_in_time?: string;
  check_out_time?: string;
  lunch_start_time?: string;
  lunch_end_time?: string;
  date: string;
  notes?: string;
  created_at?: string;
  updated_at?: string;
}

interface Violation {
  id: number;
  user_id: number;
  type: string;
  description: string;
  penalty: string;
  status: 'ŸÖÿπŸÑŸÇ' | 'ŸÖÿ∑ÿ®ŸÇ' | 'ŸÖŸÑÿ∫Ÿä';
  date: string;
  created_by: number;
}

interface UserRequest {
  id: number;
  user_id: number;
  type: 'ÿ•ÿ¨ÿßÿ≤ÿ©' | 'ÿ¥ŸÉŸàŸâ' | 'ÿ∑ŸÑÿ® ÿÆÿßÿµ';
  title: string;
  description: string;
  status: 'ŸÖÿπŸÑŸÇ' | 'ŸÖŸàÿßŸÅŸÇ' | 'ŸÖÿ±ŸÅŸàÿ∂';
  date: string;
  response?: string;
}

export default function UserDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [currentLocation, setCurrentLocation] = useState<{lat: number, lng: number} | null>(null);
  const [locationError, setLocationError] = useState<string>('');
  const [currentTime, setCurrentTime] = useState(new Date());

  // Get current location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCurrentLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          setLocationError('ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑŸÖŸàŸÇÿπ ÿßŸÑÿ≠ÿßŸÑŸä');
        }
      );
    }
  }, []);

  // Update time display every minute for live hour calculation
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  // Fetch user data
  const { data: userData } = useQuery<UserData>({
    queryKey: ['/api/users', user?.id],
    enabled: !!user?.id
  });

  // Fetch attendance records
  const { data: attendanceRecords } = useQuery<AttendanceRecord[]>({
    queryKey: ['/api/attendance'],
    select: (data) => data.filter(record => record.user_id === user?.id)
  });

  // Fetch violations
  const { data: violations } = useQuery<Violation[]>({
    queryKey: ['/api/violations'],
    select: (data) => data.filter(violation => violation.user_id === user?.id)
  });

  // Fetch user requests
  const { data: userRequests } = useQuery<UserRequest[]>({
    queryKey: ['/api/user-requests'],
    select: (data) => data.filter(request => request.user_id === user?.id)
  });

  // Fetch daily attendance status
  const { data: dailyAttendanceStatus } = useQuery<{
    hasCheckedIn: boolean;
    hasStartedLunch: boolean;
    hasEndedLunch: boolean;
    hasCheckedOut: boolean;
    currentStatus: string;
  }>({
    queryKey: ['/api/attendance/daily-status', user?.id],
    enabled: !!user?.id,
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  // Current attendance status - get the latest record for today
  const todayAttendance = attendanceRecords?.filter(record => 
    record.date === new Date().toISOString().split('T')[0]
  ).sort((a, b) => {
    const dateA = a.created_at ? new Date(a.created_at).getTime() : 0;
    const dateB = b.created_at ? new Date(b.created_at).getTime() : 0;
    return dateB - dateA;
  })[0];

  // Attendance mutation
  const attendanceMutation = useMutation({
    mutationFn: async (data: { status: string; notes?: string; action?: string }) => {
      const response = await fetch('/api/attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: user?.id,
          status: data.status,
          action: data.action,
          date: new Date().toISOString().split('T')[0],
          notes: data.notes,
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ≠ÿ∂Ÿàÿ±');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance/daily-status', user?.id] });
      toast({ title: "ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ≠ÿ∂Ÿàÿ± ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: (error: Error) => {
      toast({ 
        title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ", 
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Calculate working hours, overtime, and break time
  const calculateDailyHours = (attendanceRecords: AttendanceRecord[] | undefined, userId: number) => {
    const today = new Date().toISOString().split('T')[0];
    const todayRecords = attendanceRecords?.filter(record => {
      if (!record.date || record.user_id !== userId) return false;
      const recordDate = new Date(record.date).toISOString().split('T')[0];
      return recordDate === today;
    }).sort((a, b) => {
      const timeA = a.created_at ? new Date(a.created_at).getTime() : 0;
      const timeB = b.created_at ? new Date(b.created_at).getTime() : 0;
      return timeA - timeB;
    }) || [];

    console.log('üìä Today Records for calculation:', todayRecords.map(r => ({
      id: r.id,
      status: r.status,
      check_in_time: r.check_in_time,
      check_out_time: r.check_out_time,
      lunch_start_time: r.lunch_start_time,
      lunch_end_time: r.lunch_end_time,
      created_at: r.created_at
    })));

    if (todayRecords.length === 0) {
      return {
        workingHours: 0,
        overtimeHours: 0,
        deficitHours: 0,
        breakMinutes: 0,
        totalMinutes: 0,
        isFriday: false
      };
    }

    // Find check-in time (first "ÿ≠ÿßÿ∂ÿ±" record with check_in_time)
    const checkInRecord = todayRecords.find(r => r.check_in_time && r.status === 'ÿ≠ÿßÿ∂ÿ±');
    if (!checkInRecord?.check_in_time) {
      console.log('‚ùå No check-in time found');
      return {
        workingHours: 0,
        overtimeHours: 0,
        deficitHours: 0,
        breakMinutes: 0,
        totalMinutes: 0,
        isFriday: false
      };
    }

    const checkInTime = new Date(checkInRecord.check_in_time);
    console.log('‚úÖ Check-in time:', checkInTime);

    // Find check-out time (last "ŸÖÿ∫ÿßÿØÿ±" record with check_out_time)
    const checkOutRecord = todayRecords.reverse().find(r => r.check_out_time && r.status === 'ŸÖÿ∫ÿßÿØÿ±');
    const hasCheckedOut = checkOutRecord && checkOutRecord.check_out_time;

    const checkOutTime = hasCheckedOut ? 
      new Date(checkOutRecord.check_out_time!) : 
      new Date(); // Current time if still working

    console.log('üèÅ Check-out time:', checkOutTime, 'Has checked out:', hasCheckedOut);

    // Calculate total time worked in minutes
    const totalMinutesWorked = Math.floor((checkOutTime.getTime() - checkInTime.getTime()) / (1000 * 60));
    console.log('‚è±Ô∏è Total minutes worked:', totalMinutesWorked);

    // Calculate break time in minutes
    let breakMinutes = 0;
    const lunchStartRecord = todayRecords.find(r => r.lunch_start_time);
    const lunchEndRecord = todayRecords.find(r => r.lunch_end_time);

    if (lunchStartRecord?.lunch_start_time && lunchEndRecord?.lunch_end_time) {
      const lunchStart = new Date(lunchStartRecord.lunch_start_time);
      const lunchEnd = new Date(lunchEndRecord.lunch_end_time);
      breakMinutes = Math.floor((lunchEnd.getTime() - lunchStart.getTime()) / (1000 * 60));
      console.log('‚òï Break time (completed):', breakMinutes, 'minutes');
    } else if (lunchStartRecord?.lunch_start_time && !lunchEndRecord?.lunch_end_time) {
      // Still on break - calculate from break start to now or check-out
      const lunchStart = new Date(lunchStartRecord.lunch_start_time);
      const endTime = hasCheckedOut ? checkOutTime : new Date();
      breakMinutes = Math.floor((endTime.getTime() - lunchStart.getTime()) / (1000 * 60));
      console.log('‚òï Break time (ongoing/until checkout):', breakMinutes, 'minutes');
    }

    // Net working time (excluding break)
    const netWorkingMinutes = Math.max(0, totalMinutesWorked - breakMinutes);
    const netWorkingHours = netWorkingMinutes / 60;
    console.log('üíº Net working minutes:', netWorkingMinutes, 'hours:', netWorkingHours.toFixed(2));

    // Check if today is Friday (5 in JavaScript, where Sunday = 0)
    const isFriday = new Date().getDay() === 5;

    // Standard working hours (8 hours = 480 minutes)
    const standardWorkingMinutes = 8 * 60; // 480 minutes

    let workingHours = 0;
    let overtimeHours = 0;
    let deficitHours = 0;

    if (isFriday) {
      // All hours on Friday are overtime
      overtimeHours = netWorkingHours;
      workingHours = 0;
      console.log('üïå Friday: All hours are overtime:', overtimeHours);
    } else {
      if (netWorkingMinutes >= standardWorkingMinutes) {
        // Normal case: worked 8+ hours
        workingHours = 8;
        overtimeHours = (netWorkingMinutes - standardWorkingMinutes) / 60;
        console.log('üí™ Standard + Overtime:', workingHours, 'hours +', overtimeHours.toFixed(2), 'overtime');
      } else {
        // Worked less than 8 hours
        workingHours = netWorkingHours;
        deficitHours = (standardWorkingMinutes - netWorkingMinutes) / 60;
        console.log('‚è≥ Partial work day:', workingHours.toFixed(2), 'hours, deficit:', deficitHours.toFixed(2));
      }
    }

    const result = {
      workingHours: Math.round(workingHours * 100) / 100,
      overtimeHours: Math.round(overtimeHours * 100) / 100,
      deficitHours: Math.round(deficitHours * 100) / 100,
      breakMinutes: Math.round(breakMinutes),
      totalMinutes: totalMinutesWorked,
      isFriday
    };

    console.log('üìã Final calculation result:', result);
    return result;
  };

  const dailyHours = calculateDailyHours(attendanceRecords, user?.id || 0);

  // Request form
  const requestForm = useForm({
    defaultValues: {
      type: '',
      title: '',
      description: ''
    }
  });

  // Submit request mutation
  const submitRequestMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch('/api/user-requests', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...data,
          user_id: user?.id,
          date: new Date().toISOString(),
          status: 'ŸÖÿπŸÑŸÇ'
        })
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user-requests'] });
      toast({ title: "ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠" });
      requestForm.reset();
    }
  });

  const handleAttendanceAction = (status: string, action?: string) => {
    attendanceMutation.mutate({ status, action });
  };

  const getStatusColor = (status: string) => {
    const colors = {
      'ÿ≠ÿßÿ∂ÿ±': 'bg-green-500',
      'ÿ∫ÿßÿ¶ÿ®': 'bg-red-500',
      'ŸÅŸä ÿßŸÑÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ©': 'bg-yellow-500',
      'ŸäÿπŸÖŸÑ': 'bg-blue-500',
      'ŸÖÿ∫ÿßÿØÿ±': 'bg-gray-500'
    };
    return colors[status as keyof typeof colors] || 'bg-gray-500';
  };

  const getStatusBadgeVariant = (status: string): "default" | "destructive" | "secondary" | "outline" | "warning" => {
    const variants: Record<string, "default" | "destructive" | "secondary" | "outline" | "warning"> = {
      'ŸÖÿπŸÑŸÇ': 'secondary',
      'ŸÖŸàÿßŸÅŸÇ': 'default',
      'ŸÖÿ±ŸÅŸàÿ∂': 'destructive',
      'ŸÖÿ∑ÿ®ŸÇ': 'destructive',
      'ŸÖŸÑÿ∫Ÿä': 'outline'
    };
    return variants[status] || 'secondary';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Header />

      <div className="flex">
        <Sidebar />
        <MobileNav />

        <main className="flex-1 lg:mr-64 p-4 pb-20 lg:pb-4">
          <div className="max-w-7xl mx-auto">
            <div className="mb-6">
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ¥ÿÆÿµŸäÿ©</h1>
              <p className="text-gray-600 dark:text-gray-400">ŸÖÿ±ÿ≠ÿ®ÿßŸã {userData?.full_name || userData?.username}</p>
            </div>

            <Tabs defaultValue="overview" className="space-y-6">
              <TabsList className="grid w-full grid-cols-5">
                <TabsTrigger value="overview">ŸÜÿ∏ÿ±ÿ© ÿπÿßŸÖÿ©</TabsTrigger>
                <TabsTrigger value="attendance">ÿßŸÑÿ≠ÿ∂Ÿàÿ±</TabsTrigger>
                <TabsTrigger value="violations">ÿßŸÑŸÖÿÆÿßŸÑŸÅÿßÿ™</TabsTrigger>
                <TabsTrigger value="requests">ÿ∑ŸÑÿ®ÿßÿ™Ÿä</TabsTrigger>
                <TabsTrigger value="location">ÿßŸÑŸÖŸàŸÇÿπ</TabsTrigger>
              </TabsList>

              {/* Overview Tab */}
              <TabsContent value="overview" className="space-y-6">
                {/* Current Date Display */}
                <div className="mb-6 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg border">
                  <div className="flex items-center justify-between">
                    <div>
                      <h2 className="text-xl font-bold text-blue-900 dark:text-blue-100">
                        {new Date().toLocaleDateString('en-US', { 
                          weekday: 'long', 
                          year: 'numeric', 
                          month: 'long', 
                          day: 'numeric' 
                        })}
                      </h2>
                      <p className="text-sm text-blue-600 dark:text-blue-300">
                        {new Date().toLocaleTimeString('en-US', {
                          hour: '2-digit',
                          minute: '2-digit',
                          hour12: true
                        })}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-gray-600 dark:text-gray-300">ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ©</p>
                      <span className={`inline-flex px-3 py-1 rounded-full text-sm font-medium ${
                        dailyAttendanceStatus?.currentStatus === 'ÿ≠ÿßÿ∂ÿ±' ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300' :
                        dailyAttendanceStatus?.currentStatus === 'ŸÅŸä ÿßŸÑÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ©' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-300' :
                        dailyAttendanceStatus?.currentStatus === 'ŸäÿπŸÖŸÑ' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300' :
                        dailyAttendanceStatus?.currentStatus === 'ŸÖÿ∫ÿßÿØÿ±' ? 'bg-gray-100 text-gray-800 dark:bg-gray-900/30 dark:text-gray-300' :
                        'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300'
                      }`}>
                        {dailyAttendanceStatus?.currentStatus || 'ÿ∫ÿßÿ¶ÿ®'}
                      </span>
                    </div>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                  <Card>
                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium">ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ± ÿßŸÑŸäŸàŸÖ</CardTitle>
                      <CheckCircle className="h-4 w-4 text-muted-foreground" />
                    </CardHeader>
                    <CardContent>
                      <div className="text-2xl font-bold">
                        {dailyAttendanceStatus?.currentStatus ? (
                          <div className="flex flex-col gap-2">
                            <Badge className={getStatusColor(dailyAttendanceStatus.currentStatus)}>
                              {dailyAttendanceStatus.currentStatus}
                            </Badge>
                            {(dailyAttendanceStatus.currentStatus === 'ÿ≠ÿßÿ∂ÿ±' || 
                              dailyAttendanceStatus.currentStatus === 'ŸÅŸä ÿßŸÑÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ©' || 
                              dailyAttendanceStatus.currentStatus === 'ŸäÿπŸÖŸÑ' || 
                              dailyAttendanceStatus.currentStatus === 'ŸÖÿ∫ÿßÿØÿ±') && 
                              dailyAttendanceStatus.hasCheckedIn && (
                              <span className="text-sm text-gray-600 dark:text-gray-300">
                                {(() => {
                                  const todayRecord = attendanceRecords?.find(record => 
                                    record.date === new Date().toISOString().split('T')[0] && 
                                    record.user_id === user?.id &&
                                    record.check_in_time
                                  );

                                  if (!todayRecord?.check_in_time) return '';

                                  const checkIn = new Date(todayRecord.check_in_time);
                                  const now = todayRecord.check_out_time ? new Date(todayRecord.check_out_time) : currentTime;
                                  const diff = now.getTime() - checkIn.getTime();
                                  const hours = Math.floor(diff / (1000 * 60 * 60));
                                  const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

                                  return `${hours} ÿ≥ÿßÿπÿ© ${minutes} ÿØŸÇŸäŸÇÿ©`;
                                })()}
                              </span>
                            )}
                          </div>
                        ) : (
                          <Badge variant="outline">ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ</Badge>
                        )}
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium">ÿπÿØÿØ ÿ£ŸäÿßŸÖ ÿßŸÑÿ≠ÿ∂Ÿàÿ±</CardTitle>
                      <Calendar className="h-4 w-4 text-muted-foreground" />
                    </CardHeader>
                    <CardContent>
                      <div className="text-2xl font-bold">
                        {formatNumber(attendanceRecords?.filter(r => r.check_in_time !== null).length || 0)}
