    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/inventory'] });
      queryClient.invalidateQueries({ queryKey: ['/api/inventory/stats'] });
      toast({
        title: "ÿ™ŸÖ ÿßŸÑÿ≠ÿ∞ŸÅ ÿ®ŸÜÿ¨ÿßÿ≠",
        description: "ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿµŸÜŸÅ ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ"
      });
