  const { data: notifications, refetch: refetchNotifications } = useQuery({
    queryKey: ['/api/notifications'],
    refetchInterval: 5000, // ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ 5 ÿ´ŸàÿßŸÜ
    gcTime: 60 * 1000, // 1 minute garbage collection for fast cleanup
  });

  // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿßÿ≥ÿ™ÿπŸÑÿßŸÖÿßÿ™ ÿπŸÜÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ
  useEffect(() => {
    return () => {
      // Cancel all queries for this component when unmounting
      queryClient.cancelQueries({ queryKey: ['/api/notifications'] });
    };
  }, [queryClient]);

  // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ•ŸÑŸâ ŸÖÿµŸÅŸàŸÅÿ©
