    {
      label: "ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©",
      value: formatNumber((stats as any)?.activeOrders || 12),
      change: "+12% ŸÖŸÜ ÿßŸÑÿ£ÿ≥ÿ®Ÿàÿπ ÿßŸÑŸÖÿßÿ∂Ÿä",
      trend: 'up',
      icon: <ShoppingCart className="w-6 h-6" />,
      color: "text-blue-600"
    },
    {
      label: "ŸÖÿπÿØŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨",
      value: formatPercentage((stats as any)?.productionRate || 85),
      change: ((stats as any)?.productionRate || 85) >= 85 ? "ÿ£ÿØÿßÿ° ŸÖŸÖÿ™ÿßÿ≤" : ((stats as any)?.productionRate || 85) >= 70 ? "ÿ£ÿØÿßÿ° ÿ¨ŸäÿØ" : "Ÿäÿ≠ÿ™ÿßÿ¨ ÿ™ÿ≠ÿ≥ŸäŸÜ",
      trend: ((stats as any)?.productionRate || 85) >= 85 ? 'up' : ((stats as any)?.productionRate || 85) >= 70 ? 'neutral' : 'down',
      icon: <TrendingUp className="w-6 h-6" />,
      color: ((stats as any)?.productionRate || 85) >= 85 ? "text-green-600" : ((stats as any)?.productionRate || 85) >= 70 ? "text-yellow-600" : "text-red-600"
    },
    {
      label: "ÿßŸÑÿπŸÖÿßŸÑ ÿßŸÑÿ≠ÿßÿ∂ÿ±ŸäŸÜ",
      value: `${formatNumber((stats as any)?.presentEmployees || 18)}/${formatNumber((stats as any)?.totalEmployees || 22)}`,
      change: `${formatPercentage(Math.round((((stats as any)?.presentEmployees || 18) / ((stats as any)?.totalEmployees || 22)) * 100))} ŸÖÿπÿØŸÑ ÿßŸÑÿ≠ÿ∂Ÿàÿ±`,
      trend: 'neutral',
      icon: <Users className="w-6 h-6" />,
      color: "text-purple-600"
    },
    {
      label: "ÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑÿµŸäÿßŸÜÿ©",
      value: formatNumber((stats as any)?.maintenanceAlerts || 2),
      change: ((stats as any)?.maintenanceAlerts || 2) > 0 ? "Ÿäÿ™ÿ∑ŸÑÿ® ÿßŸÜÿ™ÿ®ÿßŸá" : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ ÿ™ÿπŸÖŸÑ ÿ®ÿ¥ŸÉŸÑ ÿ∑ÿ®ŸäÿπŸä",
