import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Textarea } from "@/components/ui/textarea";
import { Clock, Edit, UserCheck, Coffee, LogOut, UserX } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";

const attendanceSchema = z.object({
  user_id: z.number(),
  status: z.string().min(1, "ÿßŸÑÿ≠ÿßŸÑÿ© ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  notes: z.string().optional()
});

export default function AttendanceManagement() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingAttendance, setEditingAttendance] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<z.infer<typeof attendanceSchema>>({
    resolver: zodResolver(attendanceSchema),
    defaultValues: {
      user_id: undefined as any,
      status: "ÿ∫ÿßÿ¶ÿ®",
      notes: ""
    }
  });

  // Fetch attendance data
  const { data: attendanceData = [], isLoading } = useQuery({
    queryKey: ['/api/attendance'],
    queryFn: async () => {
      const response = await fetch('/api/attendance');
      if (!response.ok) throw new Error('ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ≠ÿ∂Ÿàÿ±');
      return response.json();
    }
  });

  // Fetch users data
  const { data: users = [] } = useQuery({
    queryKey: ['/api/users'],
    queryFn: async () => {
      const response = await fetch('/api/users');
      if (!response.ok) throw new Error('ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ');
      return response.json();
    }
  });

  // Attendance mutation
  const attendanceMutation = useMutation({
    mutationFn: async (data: z.infer<typeof attendanceSchema>) => {
      const url = editingAttendance ? `/api/attendance/${editingAttendance.id}` : '/api/attendance';
      const method = editingAttendance ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });

      if (!response.ok) throw new Error('ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ≠ÿ∂Ÿàÿ±');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setIsDialogOpen(false);
      setEditingAttendance(null);
      form.reset();
      toast({
        title: "ÿ™ŸÖ ÿßŸÑÿ≠ŸÅÿ∏ ÿ®ŸÜÿ¨ÿßÿ≠",
        description: editingAttendance ? "ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±" : "ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±"
      });
    },
    onError: (error) => {
      toast({
        title: "ÿÆÿ∑ÿ£",
        description: error instanceof Error ? error.message : "ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™",
        variant: "destructive"
      });
    }
  });

  const onSubmit = (data: z.infer<typeof attendanceSchema>) => {
    attendanceMutation.mutate(data);
  };

  const handleEdit = (attendance: any) => {
    setEditingAttendance(attendance);
    form.setValue('user_id', attendance.user_id);
    form.setValue('status', attendance.status);
    form.setValue('notes', attendance.notes || '');
    setIsDialogOpen(true);
  };

  const handleAdd = () => {
    setEditingAttendance(null);
    form.reset();
    setIsDialogOpen(true);
  };

  const getStatusBadge = (status: string) => {
    const statusMap = {
      'ÿ≠ÿßÿ∂ÿ±': { label: 'ÿ≠ÿßÿ∂ÿ±', variant: 'default' as const, icon: UserCheck, color: 'bg-green-100 text-green-800' },
      'ÿ∫ÿßÿ¶ÿ®': { label: 'ÿ∫ÿßÿ¶ÿ®', variant: 'destructive' as const, icon: UserX, color: 'bg-red-100 text-red-800' },
      'ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿ∫ÿØÿßÿ°': { label: 'ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿ∫ÿØÿßÿ°', variant: 'secondary' as const, icon: Coffee, color: 'bg-orange-100 text-orange-800' },
      'ŸÖÿ∫ÿßÿØÿ±': { label: 'ŸÖÿ∫ÿßÿØÿ±', variant: 'outline' as const, icon: LogOut, color: 'bg-gray-100 text-gray-800' }
    };

    const statusInfo = statusMap[status as keyof typeof statusMap] || statusMap['ÿ∫ÿßÿ¶ÿ®'];
    const IconComponent = statusInfo.icon;

    return (
      <div className={`inline-flex items-center gap-2 px-3 py-1 rounded-full text-sm font-medium ${statusInfo.color}`}>
        <IconComponent className="h-4 w-4" />
        {statusInfo.label}
      </div>
    );
  };

  // Group attendance by today's data
  const todayAttendance = attendanceData.filter((record: any) => {
    const today = new Date().toISOString().split('T')[0];
    const recordDate = record.date || new Date(record.created_at).toISOString().split('T')[0];
    return recordDate === today;
  });

  // Create attendance summary for all users with proper typing
  interface AttendanceSummaryItem {
    id: number;
    username: string;
    display_name?: string;
    display_name_ar?: string;
    attendance: {
      status: string;
      user_id: number;
      notes?: string;
      created_at?: string;
      updated_at?: string;
    };
  }

  const attendanceSummary: AttendanceSummaryItem[] = users.map((user: any) => {
    const userAttendance = todayAttendance.find((record: any) => record.user_id === user.id);
    return {
      ...user,
      attendance: userAttendance || { status: 'ÿ∫ÿßÿ¶ÿ®', user_id: user.id }
    };
  });

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±</h2>
          <p className="text-gray-600 mt-1">ŸÖÿ™ÿßÿ®ÿπÿ© ÿ≠ÿ∂Ÿàÿ± ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ Ÿàÿ≠ÿßŸÑÿßÿ™ŸáŸÖ ÿßŸÑŸäŸàŸÖŸäÿ©</p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleAdd}>
              <Clock className="h-4 w-4 mr-2" />
              ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ≠ÿ∂Ÿàÿ±
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                {editingAttendance ? 'ÿ™ÿπÿØŸäŸÑ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±' : 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ≠ÿ∂Ÿàÿ± ÿ¨ÿØŸäÿØ'}
              </DialogTitle>
              <DialogDescription>
                {editingAttendance ? 'ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿ≠ÿ∂Ÿàÿ± ÿßŸÑŸÖŸàÿ∏ŸÅ Ÿàÿ•ÿ∂ÿßŸÅÿ© ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™' : 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ≠ÿßŸÑÿ© ÿ≠ÿ∂Ÿàÿ± ÿ¨ÿØŸäÿØÿ© ŸÑŸÑŸÖŸàÿ∏ŸÅ ŸÖÿπ ÿßŸÑŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™'}
              </DialogDescription>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="user_id"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ÿßŸÑŸÖŸàÿ∏ŸÅ</FormLabel>
                      <Select onValueChange={(value) => field.onChange(parseInt(value))} value={field.value !== undefined ? field.value.toString() : ""}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="ÿßÿÆÿ™ÿ± ÿßŸÑŸÖŸàÿ∏ŸÅ" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {users.map((user: any) => (
                            <SelectItem key={user.id} value={user.id.toString()}>
                              {user.display_name_ar || user.username}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="status"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
