  XCircle,
  MoreHorizontal,
  Eye,
  Edit,
  Trash
} from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { format } from "date-fns";

const trainingProgramSchema = z.object({
  title: z.string().min(1, "ÿπŸÜŸàÿßŸÜ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®"),
  title_ar: z.string().min(1, "ÿßŸÑÿπŸÜŸàÿßŸÜ ÿ®ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ŸÖÿ∑ŸÑŸàÿ®"),
  description: z.string().optional(),
  description_ar: z.string().optional(),
  category: z.string().min(1, "ÿßŸÑŸÅÿ¶ÿ© ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  training_scope: z.string().min(1, "ŸÜŸàÿπ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®"),
  duration_hours: z.number().min(1, "ŸÖÿØÿ© ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  max_participants: z.number().min(1, "ÿßŸÑÿπÿØÿØ ÿßŸÑÿ£ŸÇÿµŸâ ŸÑŸÑŸÖÿ¥ÿßÿ±ŸÉŸäŸÜ ŸÖÿ∑ŸÑŸàÿ®"),
  location: z.string().min(1, "ŸÖŸÉÿßŸÜ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®"),
  practical_requirements: z.string().optional(),
  instructor_id: z.number().optional(),
  department_id: z.string().optional(),
  status: z.string().default('active')
});

const enrollmentSchema = z.object({
  program_id: z.string().min(1, "ÿ®ÿ±ŸÜÿßŸÖÿ¨ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®"),
  employee_id: z.string().min(1, "ÿßŸÑŸÖŸàÿ∏ŸÅ ŸÖÿ∑ŸÑŸàÿ®"),
  training_date: z.string().min(1, "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ŸÖÿ∑ŸÑŸàÿ®"),
  attendance_notes: z.string().optional()
});

const evaluationSchema = z.object({
  enrollment_id: z.string().min(1, "ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ ŸÖÿ∑ŸÑŸàÿ®"),
  program_id: z.string().min(1, "ÿßŸÑÿ®ÿ±ŸÜÿßŸÖÿ¨ ŸÖÿ∑ŸÑŸàÿ®"),
  employee_id: z.string().min(1, "ÿßŸÑŸÖŸàÿ∏ŸÅ ŸÖÿ∑ŸÑŸàÿ®"),
  evaluator_id: z.string().default("1"),
  evaluation_date: z.string(),
  theoretical_understanding: z.string().min(1, "ÿßŸÑŸÅŸáŸÖ ÿßŸÑŸÜÿ∏ÿ±Ÿä ŸÖÿ∑ŸÑŸàÿ®"),
  practical_skills: z.string().min(1, "ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑÿπŸÖŸÑŸäÿ© ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  safety_compliance: z.string().min(1, "ÿßŸÑÿßŸÑÿ™ÿ≤ÿßŸÖ ÿ®ÿßŸÑÿ≥ŸÑÿßŸÖÿ© ŸÖÿ∑ŸÑŸàÿ®"),
  teamwork: z.string().min(1, "ÿßŸÑÿπŸÖŸÑ ÿßŸÑÿ¨ŸÖÿßÿπŸä ŸÖÿ∑ŸÑŸàÿ®"),
  communication: z.string().min(1, "ÿßŸÑÿ™ŸàÿßÿµŸÑ ŸÖÿ∑ŸÑŸàÿ®"),
  strengths: z.string().optional(),
  areas_for_improvement: z.string().optional(),
  additional_notes: z.string().optional(),
  recommendation: z.string().min(1, "ÿßŸÑÿ™ŸàÿµŸäÿ© ŸÖÿ∑ŸÑŸàÿ®ÿ©")
});

interface TrainingProgram {
  id: number;
  title: string;
  title_ar: string;
  description?: string;
  description_ar?: string;
  category: string;
  training_scope: string;
  duration_hours: number;
  max_participants?: number;
  location?: string;
  practical_requirements?: string;
  instructor_id?: number;
  department_id?: string;
  status: string;
  created_at: string;
  updated_at?: string;
}

interface TrainingEnrollment {
  id: number;
  program_id: number;
  employee_id: number;
  enrolled_date: string;
  training_date?: string;
  attendance_status: string;
  completion_status: string;
  attendance_notes?: string;
  practical_performance?: string;
  final_score?: number;
  certificate_issued: boolean;
  certificate_number?: string;
}

interface TrainingEvaluation {
  id: number;
  enrollment_id: number;
  program_id: number;
  employee_id: number;
  evaluator_id: number;
  evaluation_date: string;
  theoretical_understanding: number;
  practical_skills: number;
  safety_compliance: number;
  teamwork: number;
  communication: number;
  overall_rating: number;
  strengths?: string;
  areas_for_improvement?: string;
  additional_notes?: string;
  recommendation: string;
}

export default function FieldTrainingPrograms() {
  const [selectedView, setSelectedView] = useState<'programs' | 'enrollments' | 'evaluations'>('programs');
  const [isCreateProgramOpen, setIsCreateProgramOpen] = useState(false);
  const [isEnrollmentOpen, setIsEnrollmentOpen] = useState(false);
  const [isEvaluationOpen, setIsEvaluationOpen] = useState(false);
  const [selectedEnrollment, setSelectedEnrollment] = useState<TrainingEnrollment | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Forms
  const programForm = useForm<z.infer<typeof trainingProgramSchema>>({
    resolver: zodResolver(trainingProgramSchema),
    defaultValues: {
      category: 'general',
      training_scope: 'safety',
      duration_hours: 4,
      max_participants: 20,
      status: 'active'
    }
  });

  const enrollmentForm = useForm<z.infer<typeof enrollmentSchema>>({
    resolver: zodResolver(enrollmentSchema),
    defaultValues: {
      program_id: "",
      employee_id: "",
      training_date: new Date().toISOString().split('T')[0],
      attendance_notes: ""
    }
  });

  const evaluationForm = useForm<z.infer<typeof evaluationSchema>>({
    resolver: zodResolver(evaluationSchema),
    defaultValues: {
      enrollment_id: "",
      program_id: "",
      employee_id: "",
      evaluator_id: "1",
      evaluation_date: new Date().toISOString().split('T')[0],
      theoretical_understanding: "3",
      practical_skills: "3",
      safety_compliance: "3",
      teamwork: "3",
      communication: "3",
      strengths: "",
      areas_for_improvement: "",
      additional_notes: "",
      recommendation: "pass"
    }
  });

  // Queries
  const { data: programs = [], isLoading: programsLoading } = useQuery<TrainingProgram[]>({
    queryKey: ['/api/hr/training-programs'],
    enabled: false, // Disable for now to test
    initialData: []
  });

  const { data: enrollments = [], isLoading: enrollmentsLoading } = useQuery<TrainingEnrollment[]>({
    queryKey: ['/api/hr/training-enrollments'],
    enabled: false, // Disable for now to test
    initialData: []
  });

  const { data: evaluations = [], isLoading: evaluationsLoading } = useQuery<TrainingEvaluation[]>({
    queryKey: ['/api/hr/training-evaluations'],
    enabled: false, // Disable for now to test
    initialData: []
  });

  const { data: users = [] } = useQuery({
    queryKey: ['/api/users'],
    enabled: false, // Disable for now to test
    initialData: []
  });

  const { data: sections = [] } = useQuery({
    queryKey: ['/api/sections'],
    enabled: false, // Disable for now to test
    initialData: []
  });

  // Mutations
  const createProgramMutation = useMutation({
    mutationFn: (data: z.infer<typeof trainingProgramSchema>) => 
      apiRequest('/api/hr/training-programs', { method: 'POST', body: data }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/hr/training-programs'] });
      setIsCreateProgramOpen(false);
      programForm.reset();
      toast({ title: "ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ®ÿ±ŸÜÿßŸÖÿ¨ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿ®ÿ±ŸÜÿßŸÖÿ¨ ÿßŸÑÿ™ÿØÿ±Ÿäÿ®", variant: "destructive" });
    }
  });

  const createEnrollmentMutation = useMutation({
    mutationFn: (data: any) => 
      apiRequest('/api/hr/training-enrollments', { 
        method: 'POST', 
        body: {
          ...data,
          program_id: parseInt(data.program_id),
          employee_id: parseInt(data.employee_id)
        }
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/hr/training-enrollments'] });
      setIsEnrollmentOpen(false);
      enrollmentForm.reset();
      toast({ title: "ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅ ŸÅŸä ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅ", variant: "destructive" });
    }
  });

  const createEvaluationMutation = useMutation({
    mutationFn: (data: any) => {
      const processedData = {
        ...data,
        enrollment_id: parseInt(data.enrollment_id),
        program_id: parseInt(data.program_id),
        employee_id: parseInt(data.employee_id),
        evaluator_id: parseInt(data.evaluator_id),
        theoretical_understanding: parseInt(data.theoretical_understanding),
        practical_skills: parseInt(data.practical_skills),
        safety_compliance: parseInt(data.safety_compliance),
        teamwork: parseInt(data.teamwork),
        communication: parseInt(data.communication),
        overall_rating: Math.round((
          parseInt(data.theoretical_understanding) +
          parseInt(data.practical_skills) +
          parseInt(data.safety_compliance) +
          parseInt(data.teamwork) +
          parseInt(data.communication)
        ) / 5 * 10) / 10
      };
      return apiRequest('/api/hr/training-evaluations', { method: 'POST', body: processedData });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/hr/training-evaluations'] });
      setIsEvaluationOpen(false);
      evaluationForm.reset();
      toast({ title: "ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ™ŸÇŸäŸäŸÖ ÿßŸÑÿ™ÿØÿ±Ÿäÿ® ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ŸÇŸäŸäŸÖ", variant: "destructive" });
    }
  });

  // Helper functions
  const getScopeIcon = (scope: string) => {
    switch (scope) {
      case 'safety': return <Shield className="w-4 h-4" />;
      case 'first_aid': return <Heart className="w-4 h-4" />;
      case 'fire_safety': return <Flame className="w-4 h-4" />;
      case 'technical': return <Wrench className="w-4 h-4" />;
      case 'film': return <Film className="w-4 h-4" />;
      case 'printing': return <Printer className="w-4 h-4" />;
      case 'cutting': return <Scissors className="w-4 h-4" />;
      default: return <Shield className="w-4 h-4" />;
    }
  };

  const getScopeText = (scope: string) => {
    switch (scope) {
      case 'safety': return 'ÿßŸÑÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÖŸáŸÜŸäÿ©';
      case 'first_aid': return 'ÿßŸÑÿ•ÿ≥ÿπÿßŸÅÿßÿ™ ÿßŸÑÿ£ŸàŸÑŸäÿ©';
      case 'fire_safety': return 'ÿßŸÑÿ≥ŸÑÿßŸÖÿ© ŸÖŸÜ ÿßŸÑÿ≠ÿ±ŸäŸÇ';
      case 'technical': return 'ÿßŸÑÿ™ŸÇŸÜŸä';
      case 'film': return 'ÿßŸÑŸÅŸäŸÑŸÖ';
      case 'printing': return 'ÿßŸÑÿ∑ÿ®ÿßÿπÿ©';
      case 'cutting': return 'ÿßŸÑÿ™ŸÇÿ∑Ÿäÿπ';
      default: return scope;
    }
  };

  const getCategoryText = (category: string) => {
    switch (category) {
      case 'general': return 'ÿ™ÿØÿ±Ÿäÿ® ÿπÿßŸÖ';
      case 'department_specific': return 'ÿ™ÿØÿ±Ÿäÿ® ŸÖÿ™ÿÆÿµÿµ';
      default: return category;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'inactive': return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
      case 'draft': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return 'ŸÜÿ¥ÿ∑';
      case 'inactive': return 'ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑';
      case 'draft': return 'ŸÖÿ≥ŸàÿØÿ©';
      default: return status;
    }
  };

  const getAttendanceStatusColor = (status: string) => {
    switch (status) {
      case 'attended': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
