// src/components/RollCreationModal.tsx
import React, { useEffect, useMemo, useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import type { ProductionOrder, Machine } from "@shared/schema";
import ErrorBoundary from "@/components/ErrorBoundary";

interface RollCreationModalProps {
  isOpen: boolean;
  onClose: () => void;
  selectedProductionOrderId?: number;
}

const rollFormSchema = z.object({
  production_order_id: z.number().min(1, "Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨"),
  weight_kg: z.string()
    .min(1, "Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸàÿ≤ŸÜ")
    .refine((val) => {
      const num = Number.parseFloat(val);
      return !isNaN(num) && num > 0;
    }, "ÿßŸÑŸàÿ≤ŸÜ Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ±ŸÇŸÖŸãÿß ÿ£ŸÉÿ®ÿ± ŸÖŸÜ 0"),
  machine_id: z.string().min(1, "Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑŸÖŸÉŸäŸÜÿ©")
});

type RollFormData = z.infer<typeof rollFormSchema>;

export default function RollCreationModal({
  isOpen,
  onClose,
  selectedProductionOrderId,
}: RollCreationModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<RollFormData>({
    resolver: zodResolver(rollFormSchema),
    defaultValues: {
      production_order_id: selectedProductionOrderId || 0,
      weight_kg: "",
      machine_id: "",
    }
  });

  const {
    data: productionOrders = [],
    isLoading: productionOrdersLoading,
  } = useQuery<ProductionOrder[]>({
    queryKey: ["/api/production-orders"],
  });

  const { data: machines = [], isLoading: machinesLoading } = useQuery<Machine[]>({
    queryKey: ["/api/machines"],
  });

  const { data: sections = [] } = useQuery<any[]>({
    queryKey: ["/api/sections"],
    staleTime: 10 * 60 * 1000 // 10 minutes
  });

  const { data: rolls = [] } = useQuery<any[]>({
    queryKey: ["/api/rolls"],
    staleTime: 1 * 60 * 1000 // 1 minute
  });

  const selectedOrder = useMemo(
    () =>
      productionOrders.find((o) => o.id === selectedProductionOrderId) || null,
    [productionOrders, selectedProductionOrderId]
  );

  // ŸÖÿ≤ÿßŸÖŸÜÿ© ŸÇŸäŸÖÿ© ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑŸÖÿÆÿ™ÿßÿ± ŸÖŸÜ ÿßŸÑŸÄprop ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ±Ÿá/ŸÅÿ™ÿ≠ ÿßŸÑŸÖŸàÿØÿßŸÑ
  useEffect(() => {
    if (isOpen) {
      form.setValue("production_order_id", selectedProductionOrderId || 0);

      // Set default weight to remaining quantity if a production order is selected
      if (selectedProductionOrderId && selectedOrder) {
        const remainingQuantity = calculateRemainingQuantity(selectedOrder);
        form.setValue("weight_kg", remainingQuantity > 0 ? remainingQuantity.toString() : "");
      }
    }
  }, [isOpen, selectedProductionOrderId, selectedOrder, rolls, form]);

  const createRollMutation = useMutation({
    mutationFn: async (data: RollFormData) => {
      const weightParsed = Number.parseFloat(data.weight_kg);

      const response = await apiRequest("/api/rolls", {
        method: "POST",
        body: JSON.stringify({
          production_order_id: data.production_order_id,
          weight_kg: weightParsed,
          machine_id: data.machine_id,
        }),
      });

      if (!response.ok) {
        const errText = await response.text();
        throw new Error(errText || "ŸÅÿ¥ŸÑ ÿßŸÑÿ∑ŸÑÿ®");
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ±ŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠",
        description: `ÿ±ŸÇŸÖ ÿßŸÑÿ±ŸàŸÑ: ${data.roll_number}`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/rolls"] });
      queryClient.invalidateQueries({ queryKey: ["/api/production-orders"] });
      onClose();
      form.reset();
    },
    onError: (error) => {
      console.error("Roll creation error:", error);

      let errorMessage = "ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ±ŸàŸÑ";

      if (error instanceof Error) {
        errorMessage = error.message;

        // Handle specific error types with better user messages
        if (errorMessage.includes('Network error') || errorMessage.includes('Failed to fetch')) {
          errorMessage = "ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™ ŸàÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.";
        } else if (errorMessage.includes('Validation') || errorMessage.includes('Invalid')) {
          errorMessage = "ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿØÿÆŸÑÿ© ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©. Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑÿ≠ŸÇŸàŸÑ ŸàÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.";
        } else if (errorMessage.includes('Conflict') || errorMessage.includes('already exists')) {
          errorMessage = "ÿßŸÑÿ±ŸàŸÑ ŸÖŸàÿ¨ŸàÿØ ŸÖÿ≥ÿ®ŸÇÿßŸã ÿ£Ÿà ŸäŸàÿ¨ÿØ ÿ™ÿ∂ÿßÿ±ÿ® ŸÅŸä ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™.";
        }
      }

      toast({
        title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ±ŸàŸÑ",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RollFormData) => {
    createRollMutation.mutate(data);
  };

  const handleClose = () => {
    if (!createRollMutation.isPending) {
      onClose();
      form.reset();
    }
  };

  // Calculate remaining quantity for a production order
  const calculateRemainingQuantity = (order: any) => {
    if (!order || !order.quantity_kg) return 0;

    const required = parseFloat(order.quantity_kg) || 0;
    const orderRolls = rolls.filter((roll: any) => roll.production_order_id === order.id);
    const produced = orderRolls.reduce((sum: number, roll: any) => sum + (parseFloat(roll.weight_kg) || 0), 0);

    return Math.max(0, required - produced);
  };

  // Filter machines to show only film section machines
  const filmSectionMachines = useMemo(() => {
    if (!sections.length || !machines.length) return machines;

    // Find film section
    const filmSection = sections.find((section: any) => 
      section.name?.toLowerCase().includes('film') || 
      section.name?.toLowerCase().includes('ŸÅŸäŸÑŸÖ') ||
      section.name_ar?.toLowerCase().includes('ŸÅŸäŸÑŸÖ') ||
      section.name_ar?.toLowerCase().includes('film')
    );

    if (!filmSection) return machines;

    // Filter machines that belong to film section
    return machines.filter((machine: any) => machine.section_id === filmSection.id);
  }, [machines, sections]);

  // ŸÇŸäŸÖÿ© Select ŸÑÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿπŸÜÿØ ÿπÿØŸÖ ÿ™ŸÖÿ±Ÿäÿ± selectedProductionOrderId
  const productionOrderValue =
    form.watch("production_order_id") && !selectedProductionOrderId
      ? String(form.watch("production_order_id"))
      : undefined;

  return (
    <Dialog
      open={isOpen}
      onOpenChange={(open) => {
        if (!open) handleClose(); // ŸÑŸÖÿßÿ∞ÿß: ŸÑÿß ŸÜÿ∫ŸÑŸÇ ÿ•ŸÑÿß ÿπŸÜÿØ ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ•ÿ∫ŸÑÿßŸÇ
      }}
    >
      <DialogContent
        className="max-w-md"
        aria-describedby="roll-creation-description"
      >
        <DialogHeader>
          <DialogTitle>ÿ•ŸÜÿ¥ÿßÿ° ÿ±ŸàŸÑ ÿ¨ÿØŸäÿØ</DialogTitle>
          <DialogDescription id="roll-creation-description">
            ÿ•ÿ∂ÿßŸÅÿ© ÿ±ŸàŸÑ ÿ¨ÿØŸäÿØ ÿ•ŸÑŸâ ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑŸÖÿ≠ÿØÿØ
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          {!selectedProductionOrderId && (
            <FormField
              control={form.control}
              name="production_order_id"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ *</FormLabel>
                  <Select
                    value={field.value ? String(field.value) : undefined}
                    onValueChange={(value) => field.onChange(Number.parseInt(value, 10))}
                    disabled={productionOrdersLoading}
                  >
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="ÿßÿÆÿ™ÿ± ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {productionOrdersLoading ? (
                        <SelectItem value="loading" disabled>
                          ÿ¨ÿßÿ±Ÿê ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...
                        </SelectItem>
                      ) : productionOrders.length ? (
                        productionOrders
                          .filter((order) => order.id)
                          .map((order) => (
                            <SelectItem key={order.id} value={String(order.id)}>
                              {order.production_order_number} -
                              {" "}
                              {(order as any).customer_name_ar ||
                                (order as any).customer_name ||
                                "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"}
                              {" "}- {" "}
                              {(order as any).item_name_ar ||
                                (order as any).item_name ||
                                (order as any).size_caption ||
                                "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"}
                            </SelectItem>
                          ))
                      ) : (
                        <SelectItem value="empty" disabled>
                          ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸàÿßŸÖÿ± ÿ•ŸÜÿ™ÿßÿ¨ ŸÖÿ™ÿßÿ≠ÿ©
                        </SelectItem>
                      )}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          )}

          {selectedProductionOrderId && (
            <div className="space-y-2">
              <Label>ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑŸÖÿ≠ÿØÿØ</Label>
              <div className="p-3 bg-gray-50 rounded-md border">
                <p className="font-medium text-sm">
                  {selectedOrder?.production_order_number ||
                    `PO-${selectedProductionOrderId}`}
                </p>
                <p className="text-xs text-gray-600">
                  {`${(selectedOrder as any)?.customer_name_ar ||
                    (selectedOrder as any)?.customer_name ||
                    "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"} - ${(selectedOrder as any)?.item_name_ar ||
                    (selectedOrder as any)?.item_name ||
                    (selectedOrder as any)?.size_caption ||
                    "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"}`}
                </p>
              </div>
            </div>
          )}

          <FormField
            control={form.control}
            name="weight_kg"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿßŸÑŸàÿ≤ŸÜ (ŸÉÿ¨ŸÖ) *</FormLabel>
                <FormControl>
                  <Input
                    type="number"
                    step="0.1"
                    min="0.1"
                    inputMode="decimal"
                    placeholder="45.2"
                    className="text-right"
                    data-testid="input-weight_kg"
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="machine_id"
            render={({ field }) => (
              <FormItem>
                <FormLabel>ÿßŸÑŸÖŸÉŸäŸÜÿ© *</FormLabel>
                <Select
                  value={field.value || undefined}
                  onValueChange={field.onChange}
                  disabled={machinesLoading}
                >
                  <FormControl>
                    <SelectTrigger data-testid="select-machine">
                      <SelectValue placeholder="ÿßÿÆÿ™ÿ± ÿßŸÑŸÖŸÉŸäŸÜÿ©" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {machinesLoading ? (
                      <SelectItem value="loading" disabled>
                        ÿ¨ÿßÿ±Ÿê ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...
                      </SelectItem>
                    ) : filmSectionMachines.length ? (
                      filmSectionMachines
                        .filter((m) => (m as any).status === "active" && (m as any).id)
                        .map((machine) => (
                          <SelectItem
                            key={String((machine as any).id)}
                            value={String((machine as any).id)}
                          >
                            {(machine as any).name_ar || (machine as any).name}
                          </SelectItem>
                        ))
                    ) : (
                      <SelectItem value="empty" disabled>
                        ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÉÿßÿ¶ŸÜ ŸÖÿ™ÿßÿ≠ÿ© ŸÅŸä ŸÇÿ≥ŸÖ ÿßŸÑŸÅŸäŸÑŸÖ
                      </SelectItem>
                    )}
                  </SelectContent>
                </Select>
                <FormMessage />
