import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { QrCode, Play, Scissors, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ProductionQueueProps {
  queueType: "printing" | "cutting";
  items: any[];
}

export default function ProductionQueue({ queueType, items }: ProductionQueueProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [processingId, setProcessingId] = useState<number | null>(null);

  const processItemMutation = useMutation({
    mutationFn: async (rollId: number) => {
      if (queueType === "printing") {
        const response = await fetch(`/api/rolls/${rollId}/print`, {
          method: 'PATCH',
          headers: { 'Content-Type': 'application/json' }
        });

        if (!response.ok) {
          const error = await response.json();
          throw new Error(error.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ∑ÿ®ÿßÿπÿ©');
        }

        return response.json();
      } else if (queueType === "cutting") {
        // For cutting, we'll need to show a cutting form
        // For now, just mark as cut with full weight
        const response = await fetch('/api/cuts', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            roll_id: rollId,
            cut_weight_kg: items.find(item => item.id === rollId)?.weight_kg || 0,
            pieces_count: 1
          })
        });

        if (!response.ok) {
          const error = await response.json();
          throw new Error(error.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ™ŸÇÿ∑Ÿäÿπ');
        }

        return response.json();
      }
    },
    onSuccess: () => {
      toast({
        title: "ÿ™ŸÖ ÿ®ŸÜÿ¨ÿßÿ≠",
        description: queueType === "printing" ? "ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ∑ÿ®ÿßÿπÿ©" : "ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ™ŸÇÿ∑Ÿäÿπ"
      });
      queryClient.invalidateQueries({ queryKey: [`/api/production/${queueType}-queue`] });
      queryClient.invalidateQueries({ queryKey: ['/api/rolls'] });
      setProcessingId(null);
    },
    onError: (error: Error) => {
      toast({
        title: "ÿÆÿ∑ÿ£",
        description: error.message,
        variant: "destructive"
      });
      setProcessingId(null);
    }
  });

  const handleProcess = (rollId: number) => {
    setProcessingId(rollId);
    processItemMutation.mutate(rollId);
  };

  const getStatusBadge = (item: any) => {
    if (queueType === "printing") {
      return <Badge variant="outline">ÿ¨ÿßŸáÿ≤ ŸÑŸÑÿ∑ÿ®ÿßÿπÿ©</Badge>;
    } else if (queueType === "cutting") {
      return <Badge variant="outline">ÿ¨ÿßŸáÿ≤ ŸÑŸÑÿ™ŸÇÿ∑Ÿäÿπ</Badge>;
    }
    return null;
  };

  const getActionButton = (item: any) => {
    const isProcessing = processingId === item.id;

    if (queueType === "printing") {
      return (
        <Button
          onClick={() => handleProcess(item.id)}
          disabled={isProcessing || processItemMutation.isPending}
          size="sm"
          data-testid={`button-print-${item.id}`}
        >
          <Play className="h-4 w-4 mr-1" />
          {isProcessing ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ∑ÿ®ÿßÿπÿ©..." : "ÿ∑ÿ®ÿßÿπÿ©"}
        </Button>
      );
    } else if (queueType === "cutting") {
      return (
        <Button
          onClick={() => handleProcess(item.id)}
          disabled={isProcessing || processItemMutation.isPending}
          size="sm"
          data-testid={`button-cut-${item.id}`}
        >
          <Scissors className="h-4 w-4 mr-1" />
          {isProcessing ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ŸÇÿ∑Ÿäÿπ..." : "ÿ™ŸÇÿ∑Ÿäÿπ"}
        </Button>
      );
    }
    return null;
  };

  if (items.length === 0) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <div className="text-gray-500">
            <Clock className="h-12 w-12 mx-auto mb-2 opacity-50" />
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿπŸÜÿßÿµÿ± ŸÅŸä ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {items.map((item) => (
        <Card key={item.id}>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4 space-x-reverse">
                <QrCode className="h-6 w-6 text-gray-400" />
                <div>
                  <p className="font-medium" data-testid={`text-roll-number-${item.id}`}>
                    {item.roll_number || `ÿ±ŸàŸÑ ${item.id}`}
                  </p>
                  <p className="text-sm text-gray-500">
                    ÿßŸÑŸàÿ≤ŸÜ: {parseFloat(item.weight_kg || item.weight || 0).toFixed(2)} ŸÉÿ¨ŸÖ
                  </p>
                  {item.machine_id && (
                    <p className="text-xs text-gray-400">ÿßŸÑŸÖŸÉŸäŸÜÿ©: {item.machine_id}</p>
                  )}
                </div>
              </div>

              <div className="flex items-center space-x-2 space-x-reverse">
                {getStatusBadge(item)}
                {getActionButton(item)}
              </div>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

