  });

  const handleWarehouseReceipt = () => {
    if (!receiptData.production_order_id || !receiptData.received_weight_kg) {
      toast({
        title: "ÿÆÿ∑ÿ£",
        description: "Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©",
        variant: "destructive"
      });
      return;
    }

    warehouseReceiptMutation.mutate({
      production_order_id: receiptData.production_order_id,
      cut_id: receiptData.cut_id ? parseInt(receiptData.cut_id) : null,
      received_weight_kg: parseFloat(receiptData.received_weight_kg)
    });
  };

  return (
    <div className="space-y-6">
      {/* Job Order Selection */}
      <Card>
        <CardHeader>
          <CardTitle>ÿßÿÆÿ™Ÿäÿßÿ± ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨</CardTitle>
        </CardHeader>
        <CardContent>
          <Select
            value={selectedProductionOrderId?.toString() || ""}
            onValueChange={(value) => setSelectedProductionOrderId(parseInt(value))}
          >
            <SelectTrigger data-testid="select-job-order">
              <SelectValue placeholder="ÿßÿÆÿ™ÿ± ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ŸÑŸÖÿ™ÿßÿ®ÿπÿ© ÿßŸÑÿ™ŸÇÿØŸÖ" />
            </SelectTrigger>
            <SelectContent>
              {productionOrders.filter((order: any) => order.status === 'in_production').map((order: any) => (
                <SelectItem key={order.id} value={order.id.toString()}>
                  {order.production_order_number} - {order.quantity_required} ŸÉÿ¨ŸÖ
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </CardContent>
      </Card>

      {/* Progress Display */}
      {selectedProductionOrderId && progress && (
        <div className="space-y-4">
          {/* Progress Summary */}
          <Card>
            <CardHeader>
              <CardTitle>ÿ™ŸÇÿØŸÖ ÿßŸÑÿ∑ŸÑÿ® - {progress.production_order?.production_order_number}</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <Package className="h-8 w-8 mx-auto mb-2 text-blue-500" />
                  <p className="text-sm text-gray-600">ŸÅŸäŸÑŸÖ</p>
                  <p className="font-bold text-lg">{progress.progress?.film_weight?.toFixed(2) || 0} ŸÉÿ¨ŸÖ</p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div 
                      className="bg-blue-500 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${Math.min(progress.progress?.film_percentage || 0, 100)}%` }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Play className="h-8 w-8 mx-auto mb-2 text-green-500" />
                  <p className="text-sm text-gray-600">ÿ∑ÿ®ÿßÿπÿ©</p>
                  <p className="font-bold text-lg">{progress.progress?.printed_weight?.toFixed(2) || 0} ŸÉÿ¨ŸÖ</p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div 
                      className="bg-green-500 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${Math.min(progress.progress?.printed_percentage || 0, 100)}%` }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Scissors className="h-8 w-8 mx-auto mb-2 text-orange-500" />
                  <p className="text-sm text-gray-600">ÿ™ŸÇÿ∑Ÿäÿπ</p>
                  <p className="font-bold text-lg">{progress.progress?.cut_weight?.toFixed(2) || 0} ŸÉÿ¨ŸÖ</p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div 
                      className="bg-orange-500 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${Math.min(progress.progress?.cut_percentage || 0, 100)}%` }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Archive className="h-8 w-8 mx-auto mb-2 text-purple-500" />
                  <p className="text-sm text-gray-600">ŸÖÿ≥ÿ™ŸàÿØÿπ</p>
                  <p className="font-bold text-lg">{progress.progress?.warehouse_weight?.toFixed(2) || 0} ŸÉÿ¨ŸÖ</p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div 
                      className="bg-purple-500 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${Math.min(progress.progress?.warehouse_percentage || 0, 100)}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Rolls Details */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>ÿßŸÑÿ±ŸàŸÑÿßÿ™</CardTitle>
                <Dialog open={warehouseDialogOpen} onOpenChange={setWarehouseDialogOpen}>
                  <DialogTrigger asChild>
                    <Button onClick={() => setReceiptData({ ...receiptData, production_order_id: selectedProductionOrderId })}>
                      <Plus className="h-4 w-4 mr-2" />
                      ÿßÿ≥ÿ™ŸÑÿßŸÖ ŸÖÿ≥ÿ™ŸàÿØÿπ
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-md">
                    <DialogHeader>
                      <DialogTitle>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿßŸÑŸÖÿ≥ÿ™ŸàÿØÿπ</DialogTitle>
                      <DialogDescription>
                        ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿßŸÑŸÖŸàÿßÿØ ŸÅŸä ÿßŸÑŸÖÿ≥ÿ™ŸàÿØÿπ ŸÖÿπ ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸàÿ≤ŸÜ ÿßŸÑŸÖÿ≥ÿ™ŸÑŸÖ
                      </DialogDescription>
                    </DialogHeader>
                    <div className="space-y-4">
                      <div className="space-y-2">
                        <Label>ÿßŸÑŸàÿ≤ŸÜ ÿßŸÑŸÖÿ≥ÿ™ŸÑŸÖ (ŸÉÿ¨ŸÖ) *</Label>
                        <Input
                          type="number"
                          step="0.1"
                          value={receiptData.received_weight_kg}
                          onChange={(e) => setReceiptData({ ...receiptData, received_weight_kg: e.target.value })}
                          placeholder="45.2"
                          className="text-right"
                          data-testid="input-received-weight"
                        />
                      </div>

                      <div className="flex justify-end space-x-3 space-x-reverse pt-4">
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setWarehouseDialogOpen(false)}
                          disabled={warehouseReceiptMutation.isPending}
                        >
                          ÿ•ŸÑÿ∫ÿßÿ°
                        </Button>
                        <Button
                          onClick={handleWarehouseReceipt}
                          disabled={warehouseReceiptMutation.isPending}
                          data-testid="button-confirm-receipt"
                        >
                          {warehouseReceiptMutation.isPending ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ..." : "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿßÿ≥ÿ™ŸÑÿßŸÖ"}
                        </Button>
                      </div>
                    </div>
                  </DialogContent>
                </Dialog>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {progress.rolls?.map((roll: any) => (
                  <div key={roll.id} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center space-x-3 space-x-reverse">
                      <QrCode className="h-5 w-5 text-gray-400" />
                      <div>
                        <p className="font-medium" data-testid={`text-roll-${roll.id}`}>
                          {roll.roll_number}
                        </p>
                        <p className="text-sm text-gray-500">
                          {roll.weight_kg?.toFixed(2)} ŸÉÿ¨ŸÖ - {roll.machine_id}
                        </p>
