import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import MemoryStore from "memorystore";
import connectPgSimple from "connect-pg-simple";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { db } from "./db";
import { users } from "@shared/schema";
import bcrypt from "bcrypt";

const app = express();
app.use(express.json({ limit: '50mb' }));
app.use(express.urlencoded({ extended: false, limit: '50mb' }));

// Security function to check for plaintext passwords
async function performPasswordSecurityCheck(): Promise<void> {
  try {
    console.log("üîí Performing startup password security check...");

    const allUsers = await db.select().from(users);
    let plaintextPasswordsFound = 0;
    const problematicUserIds: number[] = [];

    for (const user of allUsers) {
      if (!user.password) {
        console.warn(`‚ö†Ô∏è User ${user.id} (${user.username}) has no password set`);
        continue;
      }

      // Check if password is already hashed (bcrypt hashes start with $2a$, $2b$, or $2y$)
      const isHashedPassword = user.password.startsWith('$2a$') || 
                             user.password.startsWith('$2b$') || 
                             user.password.startsWith('$2y$');

      if (!isHashedPassword) {
        plaintextPasswordsFound++;
        problematicUserIds.push(user.id);
        console.error(`üö® SECURITY ALERT: User ${user.id} (${user.username}) has plaintext password!`);
      }
    }

    if (plaintextPasswordsFound > 0) {
      console.error(`üö® CRITICAL SECURITY ISSUE: Found ${plaintextPasswordsFound} user(s) with plaintext passwords!`);
      console.error(`üö® Affected user IDs: [${problematicUserIds.join(', ')}]`);
      console.error(`üö® This is a security vulnerability that must be addressed immediately!`);
      console.error(`üö® All passwords should be hashed with bcrypt before storage.`);

      // In production, you might want to take more drastic action:
      // - Exit the application
      // - Send alerts to administrators
      // - Disable affected accounts

      if (app.get("env") === "production") {
        console.error(`üö® PRODUCTION SECURITY VIOLATION: Application startup blocked due to plaintext passwords`);
        process.exit(1); // Exit in production to prevent security risk
      }
    } else {
      console.log(`‚úÖ Password security check passed: All ${allUsers.length} user passwords are properly hashed`);
    }

  } catch (error) {
    console.error("‚ùå Password security check failed:", error);

    // In production, fail-safe approach
    if (app.get("env") === "production") {
      console.error("üö® Production security check failure - shutting down for safety");
      process.exit(1);
    } else {
      console.warn("‚ö†Ô∏è Development mode: continuing despite security check failure");
    }
  }
}

// Configure CORS with strict allowlist - must be before session middleware
app.use((req, res, next) => {
  const origin = req.headers.origin;

  // Strict allowlist of trusted origins
  const allowedOrigins = [
    'http://localhost:5000',
    'https://localhost:5000',
    'http://127.0.0.1:5000',
    'https://127.0.0.1:5000',
    // Add specific Replit deployment URLs here - replace with actual URLs
    // Example: 'https://your-app-name.replit.app'
  ];

  // Add current host for same-origin requests
  const currentHost = req.get('host');
  if (currentHost) {
    allowedOrigins.push(`http://${currentHost}`);
    allowedOrigins.push(`https://${currentHost}`);
  }

  if (origin && allowedOrigins.includes(origin)) {
    res.header('Access-Control-Allow-Origin', origin);
  } else if (!origin) {
    // For same-origin requests (no origin header), allow the request
    res.header('Access-Control-Allow-Origin', currentHost ? `https://${currentHost}` : 'https://localhost:5000');
  }
  // Explicitly reject unauthorized origins - no wildcard '*' when credentials are enabled

  res.header('Access-Control-Allow-Credentials', 'true');
  res.header('Access-Control-Allow-Methods', 'GET,PUT,POST,DELETE,OPTIONS,PATCH');
  res.header('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization, Cache-Control, Pragma, Cookie, Set-Cookie');
  res.header('Access-Control-Expose-Headers', 'Set-Cookie');

  if (req.method === 'OPTIONS') {
    res.sendStatus(200);
  } else {
    next();
  }
});

// Configure session store with security validation
const isProduction = process.env.NODE_ENV === 'production';

// Security validation: Require SESSION_SECRET in production
if (isProduction && !process.env.SESSION_SECRET) {
  console.error('üö® SECURITY ERROR: SESSION_SECRET environment variable is required in production');
  console.error('üö® Please set SESSION_SECRET to a secure random value');
  process.exit(1);
}

// Configure production-ready session store
let sessionStore: any;
if (isProduction) {
  // Use PostgreSQL session store in production for scalability and persistence
  const PgSession = connectPgSimple(session);
  sessionStore = new PgSession({
    conString: process.env.DATABASE_URL,
    tableName: 'user_sessions',
    createTableIfMissing: true,
    pruneSessionInterval: 60 * 15 // Clean expired sessions every 15 minutes
  });
  console.log('‚úÖ Using PostgreSQL session store for production');
} else {
  // Use MemoryStore only in development
  const MemoryStoreSession = MemoryStore(session);
  sessionStore = new MemoryStoreSession({
    checkPeriod: 86400000 // prune expired entries every 24h
  });
  console.log('‚ö†Ô∏è Using MemoryStore session store for development only');
}

app.use(session({
  store: sessionStore,
  secret: process.env.SESSION_SECRET || (isProduction ? (() => {
    console.error('üö® CRITICAL: SESSION_SECRET missing in production');
    process.exit(1);
  })() : 'dev-secret-key-not-for-production'),
  resave: true, // Always save session to extend lifetime
  saveUninitialized: false, // Don't create session until something stored
  rolling: true, // Reset expiry on activity - crucial for keeping session alive
  cookie: {
    secure: isProduction, // HTTPS-only in production for security
    httpOnly: true, // ALWAYS prevent XSS - critical security fix
    maxAge: 30 * 24 * 60 * 60 * 1000, // 30 days
    sameSite: isProduction ? 'strict' : 'lax' // Stricter CSRF protection in production
  },
  name: 'plastic-bag-session', // Custom session name
  unset: 'keep' // Keep the session even if we unset properties
}));

// Session extension middleware - extends session on any API call
app.use((req, res, next) => {
  // For API requests, extend the session if it exists
  if (req.path.startsWith("/api") && req.session && req.session.userId) {
    // Touch the session to reset expiry with rolling sessions
    req.session.touch();
  }
  next();
});

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;

    // Skip logging spam HEAD requests to /api (likely from browser extensions/dev tools)
    if (req.method === 'HEAD' && path === '/api') {
      return;
    }

    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;

      // Security: Only log response metadata in production, never response bodies
      // Response bodies may contain sensitive data like passwords, tokens, PII
      if (!isProduction && capturedJsonResponse) {
        // In development, sanitize sensitive fields before logging
        const sanitizedResponse = sanitizeResponseForLogging(capturedJsonResponse);
        if (Object.keys(sanitizedResponse).length > 0) {
          logLine += ` :: ${JSON.stringify(sanitizedResponse)}`;
        }
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

// Security function to sanitize response data for logging
function sanitizeResponseForLogging(response: any): any {
  if (!response || typeof response !== 'object') {
    return {};
  }

  // List of sensitive field patterns to exclude from logs
  const sensitiveFields = [
    'password', 'passwd', 'pwd', 'secret', 'token', 'key', 'auth',
    'session', 'cookie', 'authorization', 'credential', 'private',
    'ssn', 'social', 'email', 'phone', 'address', 'ip', 'personal',
    'card', 'payment', 'billing', 'account', 'user_id', 'userId'
  ];

  const sanitized: any = {};

  for (const [key, value] of Object.entries(response)) {
    const keyLower = key.toLowerCase();
    const isSensitive = sensitiveFields.some(field => keyLower.includes(field));

    if (isSensitive) {
      sanitized[key] = '[REDACTED]';
    } else if (Array.isArray(value)) {
      sanitized[key] = `[Array:${value.length}]`;
    } else if (typeof value === 'object' && value !== null) {
      sanitized[key] = '[Object]';
    } else {
      sanitized[key] = value;
    }
  }

  return sanitized;
}

(async () => {
  // Enhanced database initialization for production deployment
  if (app.get("env") === "production") {
    try {
      console.log("üöÄ Initializing production database...");

      // Try primary migration approach first
      try {
        const { migrate } = await import('drizzle-orm/neon-serverless/migrator');
        const { db } = await import('./db.js');
        await migrate(db, { migrationsFolder: './migrations' });
        console.log("‚úÖ Database migrations completed via migrate()");
      } catch (migrationError: any) {
        console.log("‚ö†Ô∏è Standard migration failed, trying alternative approach...");
        console.log("Migration error:", migrationError?.message || migrationError);

        // Alternative approach: Use schema push for deployment
        try {
          const { db } = await import('./db.js');

          // Test database connection first
          await db.execute('SELECT 1 as test');
          console.log("‚úÖ Database connection verified");

          // Check if database is empty or needs schema initialization
          const tableCheck = await db.execute(`
            SELECT table_name FROM information_schema.tables 
            WHERE table_schema = 'public' AND table_type = 'BASE TABLE'
          `);

          if (tableCheck.rows.length === 0) {
            console.log("üÜï Fresh database detected - will initialize via schema push");
            console.log("‚úÖ Database ready for schema initialization on first request");
          } else {
            console.log(`‚úÖ Existing database with ${tableCheck.rows.length} tables detected`);
          }

        } catch (connectionError: any) {
          console.error("‚ùå Database connection failed:", connectionError?.message || connectionError);
          throw connectionError;
        }
      }

    } catch (error: any) {
      console.error("‚ùå Database initialization failed:", error?.message || error);
      console.error("This may be a temporary platform issue. Continuing with server startup...");
      console.error("Database operations will be retried on first request.");
      // Don't exit - let the server start and handle database issues gracefully
    }
  }

  // Security check: Verify no plaintext passwords remain
  await performPasswordSecurityCheck();

  // API-specific middleware to ensure JSON responses (MUST be before routes)
  app.use('/api/*', (req: Request, res: Response, next: NextFunction) => {
    // Set JSON content type for all API responses
    res.setHeader('Content-Type', 'application/json');
    next();
  });

  const server = await registerRoutes(app);

  // 404 handler for unmatched API routes (MUST be after routes)
  app.use('/api/*', (req: Request, res: Response, next: NextFunction) => {
    // If no route matched, send 404 JSON response instead of falling through to HTML
    if (!res.headersSent) {
      return res.status(404).json({ message: "API endpoint not found" });
    }
    next();
  });

  // Error handling middleware for API routes (MUST be after routes)
  app.use('/api/*', (err: any, req: Request, res: Response, next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    // Ensure we always return JSON for API routes
    if (!res.headersSent) {
      res.status(status).json({ message });
    }
    console.error('API Error:', err);
  });

  // General error handling middleware
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

