// Database error handling utilities
class DatabaseError extends Error {
  public code?: string;
  public constraint?: string;
  public table?: string;

  constructor(message: string, originalError?: any) {
    super(message);
    this.name = 'DatabaseError';

    if (originalError) {
      this.code = originalError.code;
      this.constraint = originalError.constraint;
      this.table = originalError.table;
    }
  }
}

function handleDatabaseError(error: any, operation: string, context?: string): never {
  console.error(`Database error during ${operation}:`, error);

  // Handle specific database errors
  if (error.code === '23505') {
    // Unique constraint violation
    throw new DatabaseError(`ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖŸÉÿ±ÿ±ÿ© - ${context || 'ÿßŸÑÿπŸÜÿµÿ± ŸÖŸàÿ¨ŸàÿØ ŸÖÿ≥ÿ®ŸÇÿßŸã'}`, error);
  }

  if (error.code === '23503') {
    // Foreign key constraint violation
    throw new DatabaseError(`ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ±ÿ®ÿ∑ - ${context || 'ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ±ÿ¨ÿπŸäÿ© ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©'}`, error);
  }

  if (error.code === '23502') {
    // Not null constraint violation
    throw new DatabaseError(`ÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ∑ŸÑŸàÿ®ÿ© ŸÖŸÅŸÇŸàÿØÿ© - ${context || 'Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©'}`, error);
  }

  if (error.code === '42P01') {
    // Table does not exist
    throw new DatabaseError('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÜÿ∏ÿßŸÖ - ÿ¨ÿØŸàŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ', error);
  }

  if (error.code === '53300') {
    // Too many connections
    throw new DatabaseError('ÿßŸÑÿÆÿßÿØŸÖ ŸÖÿ¥ÿ∫ŸàŸÑ - Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã', error);
  }

  if (error.code === '08006' || error.code === '08003') {
    // Connection failure
    throw new DatabaseError('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ - Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã', error);
  }

  // Generic database error
  throw new DatabaseError(
    `ÿÆÿ∑ÿ£ ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ£ÿ´ŸÜÿßÿ° ${operation} - ${context || 'Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã'}`,
    error
  );
}

async function withDatabaseErrorHandling<T>(
  operation: () => Promise<T>,
  operationName: string,
  context?: string
): Promise<T> {
  try {
    return await operation();
  } catch (error) {
    handleDatabaseError(error, operationName, context);
  }
}

