  async getUserNotifications(userId: number, options?: { unreadOnly?: boolean; limit?: number; offset?: number }): Promise<Notification[]> {
    return withDatabaseErrorHandling(
      async () => {
        if (!userId || typeof userId !== 'number' || userId <= 0) {
          throw new Error('ŸÖÿπÿ±ŸÅ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ∑ŸÑŸàÿ®');
        }

        const limit = options?.limit || 50;
        const offset = options?.offset || 0;

        let query = db
          .select()
          .from(notifications)
          .where(
            or(
              eq(notifications.recipient_id, userId.toString()),
              and(
                eq(notifications.recipient_type, 'all'),
                eq(notifications.type, 'system')
              )
            )
          )
          .orderBy(desc(notifications.created_at))
          .limit(limit)
          .offset(offset);

        // Add unread filter if specified
        if (options?.unreadOnly) {
          query = db
            .select()
            .from(notifications)
            .where(
              and(
                or(
                  eq(notifications.recipient_id, userId.toString()),
                  and(
                    eq(notifications.recipient_type, 'all'),
                    eq(notifications.type, 'system')
                  )
                ),
                sql`${notifications.read_at} IS NULL`
              )
            )
            .orderBy(desc(notifications.created_at))
            .limit(limit)
            .offset(offset);
        }

        return await query;
      },
      'ÿ¨ŸÑÿ® ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ',
      `ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ±ŸÇŸÖ ${userId}`
    );
  }

