@import url('https://fonts.googleapis.com/css2?family=Cairo:wght@300;400;500;600;700&family=Inter:wght@300;400;500;600&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* Custom colors matching the design reference */
  --primary-50: hsl(214, 100%, 97%);
  --primary-500: hsl(217, 91%, 60%);
  --primary-600: hsl(221, 83%, 53%);
  --primary-700: hsl(224, 76%, 48%);
  --primary-900: hsl(224, 64%, 33%);

  --success: hsl(142, 76%, 36%);
  --success-foreground: hsl(355, 100%, 97%);
  --success-50: hsl(138, 76%, 97%);
  --success-500: hsl(142, 71%, 45%);
  --success-600: hsl(142, 76%, 36%);

  --warning: hsl(38, 92%, 50%);
  --warning-foreground: hsl(48, 96%, 89%);
  --warning-50: hsl(48, 100%, 96%);
  --warning-500: hsl(38, 92%, 50%);
  --warning-600: hsl(32, 95%, 44%);

  --danger: hsl(0, 84%, 60%);
  --danger-foreground: hsl(0, 0%, 98%);
  --danger-50: hsl(0, 86%, 97%);
  --danger-500: hsl(0, 84%, 60%);
  --danger-600: hsl(0, 72%, 51%);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
}

@layer base {
  * {
    border-color: var(--border);
  }

  body {
    font-family: 'Cairo', 'Inter', system-ui, -apple-system, sans-serif;
    direction: rtl;
    background-color: var(--background);
    color: var(--foreground);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  [dir="ltr"] body {
    direction: ltr;
    font-family: 'Inter', 'Cairo', system-ui, -apple-system, sans-serif;
  }

  /* RTL support for space utilities */
  .space-x-reverse > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 1;
    margin-right: calc(var(--tw-space-x) * var(--tw-space-x-reverse));
    margin-left: calc(var(--tw-space-x) * calc(1 - var(--tw-space-x-reverse)));
  }
}

@layer components {
  .font-cairo {
    font-family: 'Cairo', system-ui, -apple-system, sans-serif;
  }

  .font-inter {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }

  /* Button variants matching design reference */
  .btn-primary {
    background-color: hsl(var(--primary-600));
    color: hsl(var(--primary-foreground));
    @apply px-4 py-2 rounded-lg transition-colors font-medium;
  }

  .btn-primary:hover {
    background-color: hsl(var(--primary-500));
  }

  .btn-success {
    background-color: hsl(var(--success-600));
    color: hsl(var(--success-foreground));
    @apply px-4 py-2 rounded-lg transition-colors font-medium;
  }

  .btn-success:hover {
    background-color: hsl(var(--success-500));
  }

  .btn-warning {
    background-color: hsl(var(--warning-600));
    color: hsl(var(--warning-foreground));
    @apply px-4 py-2 rounded-lg transition-colors font-medium;
  }

  .btn-warning:hover {
    background-color: hsl(var(--warning-500));
  }

  .btn-danger {
    background-color: hsl(var(--danger-600));
    color: hsl(var(--danger-foreground));
    @apply px-4 py-2 rounded-lg transition-colors font-medium;
  }

  .btn-danger:hover {
    background-color: hsl(var(--danger-500));
  }

  /* Card components */
  .card-stats {
    @apply bg-card rounded-lg shadow-sm border p-6 hover:shadow-md transition-shadow;
  }

  /* Status indicators */
  .status-active {
    background-color: hsl(var(--success-50));
    color: hsl(var(--success-600));
    border: 1px solid hsl(var(--success-600) / 0.2);
  }

  .status-warning {
    background-color: hsl(var(--warning-50));
    color: hsl(var(--warning-600));
    border: 1px solid hsl(var(--warning-600) / 0.2);
  }

  .status-danger {
    background-color: hsl(var(--danger-50));
    color: hsl(var(--danger-600));
    border: 1px solid hsl(var(--danger-600) / 0.2);
  }

  /* Navigation items */
  .nav-item {
    @apply flex items-center text-gray-700 hover:bg-gray-50 rounded-lg p-3 cursor-pointer transition-colors;
  }

  .nav-item-active {
    background-color: hsl(var(--primary-50));
    border: 1px solid hsl(var(--primary-600) / 0.2);
    color: hsl(var(--primary-700));
  }

  /* Progress bars with custom colors */
  .progress-primary .progress-indicator {
    background-color: hsl(var(--primary-600));
  }

  .progress-success .progress-indicator {
    background-color: hsl(var(--success-600));
  }

  .progress-warning .progress-indicator {
    background-color: hsl(var(--warning-600));
  }

  .progress-danger .progress-indicator {
    background-color: hsl(var(--danger-600));
  }

  /* Badge variants */
  .badge-success {
    background-color: hsl(var(--success-50));
    color: hsl(var(--success-600));
    border: 1px solid hsl(var(--success-600) / 0.2);
  }

  .badge-warning {
    background-color: hsl(var(--warning-50));
    color: hsl(var(--warning-600));
    border: 1px solid hsl(var(--warning-600) / 0.2);
  }

  .badge-danger {
    background-color: hsl(var(--danger-50));
    color: hsl(var(--danger-600));
    border: 1px solid hsl(var(--danger-600) / 0.2);
  }

  .badge-primary {
    background-color: hsl(var(--primary-50));
    color: hsl(var(--primary-600));
    border: 1px solid hsl(var(--primary-600) / 0.2);
  }

  /* Machine status indicators */
  .machine-status-active {
    background-color: hsl(var(--success-500));
  }

  .machine-status-maintenance {
    background-color: hsl(var(--warning-500));
  }

  .machine-status-down {
    background-color: hsl(var(--danger-500));
  }

  /* Form inputs with RTL support */
  .input-rtl {
    @apply text-right;
  }

  .input-rtl::placeholder {
    text-align: right;
  }

  /* Table styling matching design */
  .table-header {
    @apply bg-gray-50 text-xs font-medium text-gray-500 uppercase tracking-wider;
  }

  .table-row {
    @apply hover:bg-gray-50 transition-colors;
  }

  .table-cell {
    @apply px-6 py-4 whitespace-nowrap text-sm text-gray-900;
  }

  /* Chat interface */
  .chat-bubble-user {
    @apply bg-primary text-primary-foreground rounded-lg p-3 max-w-xs;
  }

  .chat-bubble-assistant {
    @apply bg-muted text-foreground rounded-lg p-3 max-w-xs;
  }
}

/* Custom utilities for RTL */
@layer utilities {
  .space-x-reverse > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 1;
    margin-right: calc(var(--tw-space-x) * var(--tw-space-x-reverse));
    margin-left: calc(var(--tw-space-x) * calc(1 - var(--tw-space-x-reverse)));
  }

  /* Text alignment utilities for RTL */
  .text-start {
    text-align: start;
  }

  .text-end {
    text-align: end;
  }

  /* Custom background utilities using CSS variables */
  .bg-primary-50 {
    background-color: hsl(var(--primary-50));
  }

  .bg-primary-600 {
    background-color: hsl(var(--primary-600));
  }

  .bg-primary-700 {
    background-color: hsl(var(--primary-700));
  }

  .bg-success-50 {
    background-color: hsl(var(--success-50));
  }

  .bg-success-500 {
    background-color: hsl(var(--success-500));
  }

  .bg-success-600 {
    background-color: hsl(var(--success-600));
  }

  .bg-warning-50 {
    background-color: hsl(var(--warning-50));
  }

  .bg-warning-500 {
    background-color: hsl(var(--warning-500));
  }

  .bg-warning-600 {
    background-color: hsl(var(--warning-600));
  }

  .bg-danger-50 {
    background-color: hsl(var(--danger-50));
  }

  .bg-danger-500 {
    background-color: hsl(var(--danger-500));
  }

  .bg-danger-600 {
    background-color: hsl(var(--danger-600));
  }

  .text-primary-600 {
    color: hsl(var(--primary-600));
  }

  .text-primary-700 {
    color: hsl(var(--primary-700));
  }

  .text-success-600 {
    color: hsl(var(--success-600));
  }

  .text-warning-600 {
    color: hsl(var(--warning-600));
  }

  .text-danger-600 {
    color: hsl(var(--danger-600));
  }

  /* Border utilities */
  .border-primary-200 {
    border-color: hsl(var(--primary-600) / 0.2);
  }

  .border-success-200 {
    border-color: hsl(var(--success-600) / 0.2);
  }

  .border-warning-200 {
    border-color: hsl(var(--warning-600) / 0.2);
  }

  .border-danger-200 {
    border-color: hsl(var(--danger-600) / 0.2);
  }

  /* Hover utilities */
  .hover\:bg-primary-700:hover {
    background-color: hsl(var(--primary-700));
  }

  .hover\:text-primary-800:hover {
    color: hsl(var(--primary-700));
  }

  .hover\:text-primary\/80:hover {
    color: hsl(var(--primary-600) / 0.8);
  }
}

/* Custom scrollbar styling */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--primary));
}

/* Animation utilities */
.animate-pulse-slow {
  animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.animate-bounce-gentle {
  animation: bounce 2s infinite;
}

/* Loading skeleton animations */
@keyframes shimmer {
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
}

.loading-shimmer {
  background: linear-gradient(90deg, hsl(var(--muted)) 25%, hsl(var(--muted-foreground) / 0.1) 50%, hsl(var(--muted)) 75%);
  background-size: 200px 100%;
  animation: shimmer 1.5s infinite;
}

/* Focus states for accessibility */
.focus-ring {
  @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2;
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .print-full-width {
    width: 100% !important;
  }
}

/* Mobile-specific adjustments */
@media (max-width: 768px) {
  .mobile-stack {
    @apply flex-col space-y-2 space-x-0;
  }

  .mobile-full-width {
    @apply w-full;
  }

  .mobile-text-sm {
    @apply text-sm;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --border: hsl(0, 0%, 20%);
    --input: hsl(0, 0%, 20%);
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* RTL-specific adjustments */
[dir="rtl"] {
  .space-x-2 > :not([hidden]) ~ :not([hidden]) {
    margin-left: 0;
    margin-right: 0.5rem;
  }

  .space-x-3 > :not([hidden]) ~ :not([hidden]) {
    margin-left: 0;
    margin-right: 0.75rem;
  }

  .space-x-4 > :not([hidden]) ~ :not([hidden]) {
    margin-left: 0;
    margin-right: 1rem;
  }

  .space-x-8 > :not([hidden]) ~ :not([hidden]) {
    margin-left: 0;
    margin-right: 2rem;
  }

  /* Margin utilities for RTL */
  .mr-1 {
    margin-left: 0.25rem;
  }

  .mr-2 {
    margin-left: 0.5rem;
  }

  .mr-3 {
    margin-left: 0.75rem;
  }

  .mr-6 {
    margin-left: 1.5rem;
  }

  .mr-8 {
    margin-left: 2rem;
  }

  .mr-auto {
    margin-left: auto;
  }

  .ml-1 {
    margin-right: 0.25rem;
  }

  .ml-2 {
    margin-right: 0.5rem;
  }

  .ml-3 {
    margin-right: 0.75rem;
  }
}

/* LTR-specific adjustments */
[dir="ltr"] {
  .space-x-reverse > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 0;
    margin-right: 0;
    margin-left: calc(var(--tw-space-x) * 1);
  }
}

/* Enhanced focus indicators for better accessibility */
.btn-primary:focus-visible,
.btn-success:focus-visible,
.btn-warning:focus-visible,
.btn-danger:focus-visible {
  @apply ring-2 ring-offset-2 ring-primary;
}

/* Custom progress bar styling */
.progress-bar {
  @apply w-full bg-gray-200 rounded-full h-2;
}

.progress-indicator {
  @apply h-2 rounded-full transition-all duration-300 ease-in-out;
}

/* Notification styling */
.notification-success {
  background-color: hsl(var(--success-50));
  border: 1px solid hsl(var(--success-600) / 0.2);
  color: hsl(var(--success-600));
}

.notification-error {
  background-color: hsl(var(--danger-50));
  border: 1px solid hsl(var(--danger-600) / 0.2);
  color: hsl(var(--danger-600));
}

.notification-warning {
  background-color: hsl(var(--warning-50));
  border: 1px solid hsl(var(--warning-600) / 0.2);
  color: hsl(var(--warning-600));
}

.notification-info {
  background-color: hsl(var(--primary-50));
  border: 1px solid hsl(var(--primary-600) / 0.2);
  color: hsl(var(--primary-600));
}

/* Enhanced table styling */
.table-container {
  @apply overflow-x-auto shadow-sm border border-gray-200 rounded-lg;
}

.table {
  @apply min-w-full divide-y divide-gray-200;
}

.table thead {
  @apply bg-gray-50;
}

.table tbody {
  @apply bg-white divide-y divide-gray-200;
}

.table th {
  @apply px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider;
}

.table td {
  @apply px-6 py-4 whitespace-nowrap text-sm text-gray-900;
}

/* Status badge enhancements */
.status-badge {
  @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
}

.status-badge.active {
  background-color: hsl(var(--success-50));
  color: hsl(var(--success-600));
  border: 1px solid hsl(var(--success-600) / 0.2);
}

.status-badge.warning {
  background-color: hsl(var(--warning-50));
  color: hsl(var(--warning-600));
  border: 1px solid hsl(var(--warning-600) / 0.2);
}

.status-badge.danger {
  background-color: hsl(var(--danger-50));
  color: hsl(var(--danger-600));
  border: 1px solid hsl(var(--danger-600) / 0.2);
}

/* Enhanced card styling */
.dashboard-card {
  @apply bg-white rounded-lg shadow-sm border border-gray-200 p-6 transition-all duration-200 hover:shadow-md;
}

.dashboard-card-icon {
  @apply p-3 rounded-full;
}

.dashboard-card-icon.primary {
  background-color: hsl(var(--primary-50));
  color: hsl(var(--primary-600));
}

.dashboard-card-icon.success {
  background-color: hsl(var(--success-50));
  color: hsl(var(--success-600));
}

.dashboard-card-icon.warning {
  background-color: hsl(var(--warning-50));
  color: hsl(var(--warning-600));
}

.dashboard-card-icon.danger {
  background-color: hsl(var(--danger-50));
  color: hsl(var(--danger-600));
}

/* Machine status enhancements */
.machine-indicator {
  @apply h-3 w-3 rounded-full;
}

.machine-indicator.active {
  @apply machine-status-active;
}

.machine-indicator.maintenance {
  @apply machine-status-maintenance;
}

.machine-indicator.down {
  @apply machine-status-down;
}

/* AI Assistant enhancements */
.ai-assistant-toggle {
  @apply bg-primary hover:bg-primary-700 text-white p-4 rounded-full shadow-lg transition-all duration-200 hover:scale-105;
}

.ai-chat-interface {
  @apply absolute bottom-16 left-0 w-80 bg-white rounded-lg shadow-xl border border-gray-200 max-h-96 flex flex-col;
}

.ai-message-user {
  @apply bg-primary text-white rounded-lg p-3 max-w-xs;
}

.ai-message-assistant {
  @apply bg-gray-100 text-gray-900 rounded-lg p-3 max-w-xs;
}

/* Enhanced sidebar styling */
.sidebar {
  @apply bg-white shadow-sm border-l border-gray-200 w-64 hidden lg:block;
}

.sidebar-nav {
  @apply p-4;
}

.sidebar-item {
  @apply nav-item;
}

.sidebar-item.active {
  background-color: hsl(var(--primary-50));
  border: 1px solid hsl(var(--primary-600) / 0.2);
  color: hsl(var(--primary-700));
}

.sidebar-subitem {
  @apply block text-sm text-primary hover:text-primary-700 py-1 transition-colors;
}

/* Mobile navigation enhancements */
.mobile-nav {
  @apply lg:hidden fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 z-40;
}

.mobile-nav-item {
  @apply flex flex-col items-center p-2 transition-colors;
}

.mobile-nav-item.active {
  @apply text-primary;
}

.mobile-nav-item:not(.active) {
  @apply text-gray-600;
}

/* Production workflow enhancements */
.production-tabs {
  @apply border-b border-gray-200;
}

.production-tab {
  @apply border-b-2 border-transparent py-4 px-1 text-sm font-medium transition-colors;
}

.production-tab.active {
  @apply border-primary text-primary;
}

.production-tab:not(.active) {
  @apply text-gray-500 hover:text-gray-700;
}

/* Form enhancements */
.form-input {
  @apply w-full border border-gray-300 rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition-colors;
}

.form-input.rtl {
  @apply text-right;
}

.form-input.rtl::placeholder {
  text-align: right;
}

.form-label {
  @apply block text-sm font-medium text-gray-700 mb-2;
}

.form-select {
  @apply form-input appearance-none;
}

/* Modal enhancements */
.modal-overlay {
  @apply fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50;
}

.modal-content {
  @apply bg-white rounded-lg shadow-xl max-w-md w-full mx-4;
}

.modal-header {
  @apply px-6 py-4 border-b border-gray-200;
}

.modal-body {
  @apply p-6;
}

.modal-footer {
  @apply px-6 py-4 border-t border-gray-200 flex justify-end;
}

.modal-footer > * + * {
  margin-left: 0.75rem;
}

[dir="rtl"] .modal-footer > * + * {
  margin-left: 0;
  margin-right: 0.75rem;
}

/* Accessibility improvements */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Skip to content link for screen readers */
.skip-to-content {
  @apply sr-only focus:not-sr-only focus:absolute focus:top-4 focus:left-4 focus:z-50 focus:px-4 focus:py-2 focus:bg-primary focus:text-white focus:rounded-lg;
}

/* High contrast mode adjustments */
@media (prefers-contrast: high) {
  .btn-primary,
  .btn-success,
  .btn-warning,
  .btn-danger {
    border: 2px solid currentColor;
  }

  .nav-item-active {
    border: 2px solid currentColor;
  }
}

/* Animation utilities for micro-interactions */
.hover-lift {
  @apply transition-transform duration-200 hover:translate-y-[-2px];
}

.hover-scale {
  @apply transition-transform duration-200 hover:scale-105;
}

.pulse-ring {
  @apply animate-ping absolute inline-flex h-full w-full rounded-full opacity-75;
}

/* Custom gradient backgrounds */
.gradient-primary {
  background: linear-gradient(135deg, hsl(var(--primary-600)), hsl(var(--primary-700)));
}

.gradient-success {
  background: linear-gradient(135deg, hsl(var(--success-500)), hsl(var(--success-600)));
}

.gradient-warning {
  background: linear-gradient(135deg, hsl(var(--warning-500)), hsl(var(--warning-600)));
}

.gradient-danger {
  background: linear-gradient(135deg, hsl(var(--danger-500)), hsl(var(--danger-600)));
}

