import OpenAI from "openai";
import { storage } from "../storage";
import { generateNotificationId } from "@shared/id-generator";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY 
});

interface SmartNotification {
  id: string;
  type: 'info' | 'warning' | 'error' | 'success';
  priority: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  message: string;
  actions?: NotificationAction[];
  targetUsers?: number[];
  createdAt: Date;
  expiresAt?: Date;
}

interface NotificationAction {
  label: string;
  action: string;
  url?: string;
  confirm?: boolean;
}

export class AINotifications {
  private static notifications: SmartNotification[] = [];

  // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ± ÿ∞ŸÉŸä
  static async sendIntelligentNotification(action: string, data: any): Promise<SmartNotification | null> {
    try {
      const notification = await this.generateNotification(action, data);

      if (notification) {
        this.notifications.push(notification);

        // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ÿßŸÑŸÖÿ≥ÿ™ŸáÿØŸÅŸäŸÜ
        await this.deliverNotification(notification);

        return notification;
      }

      return null;
    } catch (error) {
      console.error('Smart notification error:', error);
      return null;
    }
  }

  // ÿ™ŸàŸÑŸäÿØ ÿ•ÿ¥ÿπÿßÿ± ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ AI
  private static async generateNotification(action: string, data: any): Promise<SmartNotification | null> {
    try {
      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: `ÿ£ŸÜÿ™ ŸÜÿ∏ÿßŸÖ ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿ∞ŸÉŸä ŸÑŸÖÿµŸÜÿπ ÿßŸÑÿ£ŸÉŸäÿßÿ≥ ÿßŸÑÿ®ŸÑÿßÿ≥ÿ™ŸäŸÉŸäÿ©. ŸÇŸÖ ÿ®ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ° ŸàÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ Ÿàÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿ¥ÿπÿßÿ± ŸÖŸÜÿßÿ≥ÿ® ÿ®ÿ™ŸÜÿ≥ŸäŸÇ JSON:

{
  "shouldNotify": true/false,
  "type": "info/warning/error/success",
  "priority": "low/medium/high/critical",
  "title": "ÿπŸÜŸàÿßŸÜ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±",
  "message": "ŸÜÿµ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±",
  "actions": [
    {"label": "ÿ™ÿ≥ŸÖŸäÿ© ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°", "action": "ÿßÿ≥ŸÖ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°", "url": "ÿ±ÿßÿ®ÿ∑ ÿßÿÆÿ™Ÿäÿßÿ±Ÿä"}
  ],
  "targetUsers": [ŸÇÿßÿ¶ŸÖÿ© ŸÖÿπÿ±ŸÅÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ÿßŸÑŸÖÿ≥ÿ™ŸáÿØŸÅŸäŸÜ],
  "expiresIn": ÿπÿØÿØ ÿßŸÑÿ≥ÿßÿπÿßÿ™ ŸÇÿ®ŸÑ ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©
}

ŸÇŸàÿßÿπÿØ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™:
- ÿ•ŸÜÿ¥ÿßÿ°/ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπŸÖŸÑŸäÿßÿ™ ÿßŸÑÿπÿßÿØŸäÿ© ‚Üí ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ info ŸÖŸÜÿÆŸÅÿ∂ÿ© ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©
- ŸÖÿ¥ÿßŸÉŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨/ÿßŸÑÿ¨ŸàÿØÿ© ‚Üí ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ warning ŸÖÿ™Ÿàÿ≥ÿ∑ÿ© ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©  
- ÿ£ÿπÿ∑ÿßŸÑ ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ/ÿ£ÿÆÿ∑ÿßÿ° ÿßŸÑŸÜÿ∏ÿßŸÖ ‚Üí ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ error ÿπÿßŸÑŸäÿ© ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©
- ÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ∑Ÿàÿßÿ±ÿ¶ ‚Üí ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ critical ŸÑŸÑÿ¨ŸÖŸäÿπ`
          },
          {
            role: "user",
            content: `ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°: ${action}
ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™: ${JSON.stringify(data, null, 2)}

ŸÇÿ±ÿ± ŸÖÿß ÿ•ÿ∞ÿß ŸÉÿßŸÜ Ÿäÿ¨ÿ® ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ± ŸàŸÖÿß ŸÜŸàÿπŸá Ÿàÿ£ŸàŸÑŸàŸäÿ™Ÿá.`
          }
        ],
        response_format: { type: "json_object" },
        temperature: 0.2
      });

      const result = JSON.parse(response.choices[0].message.content || '{}');

      if (!result.shouldNotify) {
        return null;
      }

      const notification: SmartNotification = {
        id: generateNotificationId('notif'),
        type: result.type || 'info',
        priority: result.priority || 'medium',
        title: result.title || 'ÿ•ÿ¥ÿπÿßÿ± ÿßŸÑŸÜÿ∏ÿßŸÖ',
        message: result.message || 'ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿπŸÖŸÑŸäÿ© ŸÅŸä ÿßŸÑŸÜÿ∏ÿßŸÖ',
        actions: result.actions || [],
        targetUsers: result.targetUsers || [],
        createdAt: new Date(),
        expiresAt: result.expiresIn ? new Date(Date.now() + result.expiresIn * 60 * 60 * 1000) : undefined
      };

      return notification;
    } catch (error) {
      console.error('Notification generation error:', error);
      return null;
    }
  }

  // ÿ™ÿ≥ŸÑŸäŸÖ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±
  private static async deliverNotification(notification: SmartNotification): Promise<void> {
    try {
      // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      console.log(`üì¢ ÿ•ÿ¥ÿπÿßÿ± ÿ¨ÿØŸäÿØ [${notification.priority.toUpperCase()}]: ${notification.title}`);
      console.log(`   ${notification.message}`);

      // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿ™ŸÉÿßŸÖŸÑ ŸÖÿπ ÿÆÿØŸÖÿßÿ™ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑÿÆÿßÿ±ÿ¨Ÿäÿ© ŸáŸÜÿß
      // ŸÖÿ´ŸÑ: ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸäÿå SMSÿå Push notificationsÿå Slackÿå ÿ•ŸÑÿÆ

      // ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ŸÅŸàÿ±Ÿäÿ© ŸÑŸÑÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ≠ÿ±ÿ¨ÿ©
      if (notification.priority === 'critical') {
        await this.sendCriticalAlert(notification);
      }

    } catch (error) {
      console.error('Notification delivery error:', error);
    }
  }

  // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ™ŸÜÿ®ŸäŸá ÿ≠ÿ±ÿ¨
  private static async sendCriticalAlert(notification: SmartNotification): Promise<void> {
    console.log(`üö® ÿ™ŸÜÿ®ŸäŸá ÿ≠ÿ±ÿ¨: ${notification.title}`);
    console.log(`   ${notification.message}`);

    // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿ™ŸÉÿßŸÖŸÑ ŸÖÿπ ÿ£ŸÜÿ∏ŸÖÿ© ÿßŸÑÿ™ŸÜÿ®ŸäŸá ÿßŸÑÿ∑ÿßÿ±ÿ¶ÿ© ŸáŸÜÿß
  }

  // ŸÅÿ≠ÿµ ÿØŸàÿ±Ÿä ŸÑŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑÿ∞ŸÉŸäÿ©
  static async performIntelligentMonitoring(): Promise<SmartNotification[]> {
    const notifications: SmartNotification[] = [];

    try {
      // ŸÅÿ≠ÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ
      const machineNotifications = await this.checkMachineStatus();
      notifications.push(...machineNotifications);

      // ŸÅÿ≠ÿµ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨
      const productionNotifications = await this.checkProductionStatus();
      notifications.push(...productionNotifications);

      // ŸÅÿ≠ÿµ ÿßŸÑÿ¨ŸàÿØÿ©
      const qualityNotifications = await this.checkQualityStatus();
      notifications.push(...qualityNotifications);

      // ŸÅÿ≠ÿµ ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ
      const inventoryNotifications = await this.checkInventoryStatus();
      notifications.push(...inventoryNotifications);

      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™
      for (const notification of notifications) {
        await this.deliverNotification(notification);
      }

      return notifications;
    } catch (error) {
      console.error('Intelligent monitoring error:', error);
      return [];
    }
  }

  // ŸÅÿ≠ÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ
  private static async checkMachineStatus(): Promise<SmartNotification[]> {
    try {
      const machines = await storage.getMachines();
      const notifications: SmartNotification[] = [];

      const downMachines = machines.filter(m => m.status === 'down');
      const maintenanceMachines = machines.filter(m => m.status === 'maintenance');

      if (downMachines.length > 0) {
        notifications.push({
          id: generateNotificationId('machine_down'),
          type: 'error',
          priority: 'high',
          title: 'ŸÖŸÉÿßÿ¶ŸÜ ŸÖÿ™ŸàŸÇŸÅÿ©',
          message: `ŸäŸàÿ¨ÿØ ${downMachines.length} ŸÖŸÉŸäŸÜÿ© ŸÖÿ™ŸàŸÇŸÅÿ©. ŸäŸèÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ© ŸÅŸàÿ±ÿßŸã.`,
          actions: [
            { label: 'ÿπÿ±ÿ∂ ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ', action: 'navigate_machines', url: '/definitions?tab=machines' }
          ],
          createdAt: new Date(),
          expiresAt: new Date(Date.now() + 4 * 60 * 60 * 1000) // 4 ÿ≥ÿßÿπÿßÿ™
        });
      }

      if (maintenanceMachines.length > 3) {
        notifications.push({
          id: generateNotificationId('machine_maintenance'),
          type: 'warning',
          priority: 'medium',
          title: 'ÿπÿØÿØ ŸÉÿ®Ÿäÿ± ŸÖŸÜ ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ ŸÅŸä ÿßŸÑÿµŸäÿßŸÜÿ©',
          message: `ŸäŸàÿ¨ÿØ ${maintenanceMachines.length} ŸÖŸÉŸäŸÜÿ© ŸÅŸä ÿßŸÑÿµŸäÿßŸÜÿ©. ŸÇÿØ Ÿäÿ§ÿ´ÿ± ÿπŸÑŸâ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨.`,
          actions: [
            { label: 'ÿ¨ÿØŸàŸÑÿ© ÿßŸÑÿµŸäÿßŸÜÿ©', action: 'schedule_maintenance', url: '/maintenance' }
          ],
          createdAt: new Date()
        });
      }

      return notifications;
    } catch (error) {
      console.error('Machine status check error:', error);
      return [];
    }
  }

  // ŸÅÿ≠ÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨
  private static async checkProductionStatus(): Promise<SmartNotification[]> {
    try {
      const stats = await storage.getDashboardStats();
      const notifications: SmartNotification[] = [];

      if (stats.productionRate < 60) {
        notifications.push({
          id: generateNotificationId('production_low'),
          type: 'warning',
          priority: 'high',
          title: 'ŸÖÿπÿØŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ŸÖŸÜÿÆŸÅÿ∂',
          message: `ŸÖÿπÿØŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑÿ≠ÿßŸÑŸä ${stats.productionRate}% ÿ£ŸÇŸÑ ŸÖŸÜ ÿßŸÑŸÖÿπÿØŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®.`,
          actions: [
            { label: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨', action: 'analyze_production', url: '/production' }
          ],
          createdAt: new Date()
        });
      }

      if (stats.activeOrders > 20) {
        notifications.push({
          id: generateNotificationId('orders_high'),
          type: 'info',
          priority: 'medium',
          title: 'ÿπÿØÿØ ŸÉÿ®Ÿäÿ± ŸÖŸÜ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©',
          message: `ŸäŸàÿ¨ÿØ ${stats.activeOrders} ÿ∑ŸÑÿ® ŸÜÿ¥ÿ∑. ŸÇÿØ ÿ™ÿ≠ÿ™ÿßÿ¨ ŸÑÿ≤ŸäÿßÿØÿ© ÿßŸÑÿ≥ÿπÿ© ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨Ÿäÿ©.`,
          actions: [
            { label: 'ÿπÿ±ÿ∂ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™', action: 'view_orders', url: '/production' }
          ],
          createdAt: new Date()
        });
      }

      return notifications;
    } catch (error) {
      console.error('Production status check error:', error);
      return [];
    }
  }

  // ŸÅÿ≠ÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ¨ŸàÿØÿ©
  private static async checkQualityStatus(): Promise<SmartNotification[]> {
    try {
      const stats = await storage.getDashboardStats();
      const notifications: SmartNotification[] = [];

      if (stats.qualityScore < 80) {
        notifications.push({
          id: generateNotificationId('quality_low'),
          type: 'warning',
          priority: 'high',
          title: 'ÿßŸÜÿÆŸÅÿßÿ∂ ŸÖÿ§ÿ¥ÿ± ÿßŸÑÿ¨ŸàÿØÿ©',
          message: `ŸÖÿ§ÿ¥ÿ± ÿßŸÑÿ¨ŸàÿØÿ© ÿßŸÑÿ≠ÿßŸÑŸä ${stats.qualityScore}% ÿ£ŸÇŸÑ ŸÖŸÜ ÿßŸÑŸÖÿπÿßŸäŸäÿ± ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©.`,
          actions: [
            { label: 'ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑÿ¨ŸàÿØÿ©', action: 'review_quality', url: '/quality' }
          ],
          createdAt: new Date()
        });
      }

      if (stats.wastePercentage > 5) {
        notifications.push({
          id: generateNotificationId('waste_high'),
          type: 'warning',
          priority: 'medium',
          title: 'ÿßÿ±ÿ™ŸÅÿßÿπ ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸáÿØÿ±',
          message: `ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸáÿØÿ± ÿßŸÑÿ≠ÿßŸÑŸäÿ© ${stats.wastePercentage}% ÿ£ÿπŸÑŸâ ŸÖŸÜ ÿßŸÑŸÖÿπÿØŸÑ ÿßŸÑŸÖŸÇÿ®ŸàŸÑ.`,
          actions: [
            { label: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑŸáÿØÿ±', action: 'analyze_waste', url: '/quality' }
          ],
          createdAt: new Date()
        });
      }

      return notifications;
    } catch (error) {
      console.error('Quality status check error:', error);
      return [];
    }
  }

  // ŸÅÿ≠ÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ
  private static async checkInventoryStatus(): Promise<SmartNotification[]> {
    try {
      // ŸÖÿ≠ÿßŸÉÿßÿ© ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ ÿ≠ÿ™Ÿâ Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸàÿ∏ÿßÿ¶ŸÅ
      const inventory: any[] = [];
      const notifications: SmartNotification[] = [];

      const lowStockItems = inventory.filter((item: any) => 
        (item.current_stock || 0) < (item.min_stock || 10)
      );

      if (lowStockItems.length > 0) {
        notifications.push({
          id: generateNotificationId('inventory_low'),
          type: 'warning',
          priority: 'medium',
          title: 'ŸÜŸÇÿµ ŸÅŸä ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ',
          message: `ŸäŸàÿ¨ÿØ ${lowStockItems.length} ÿµŸÜŸÅ ÿ™ÿ≠ÿ™ ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ÿØŸÜŸâ ŸÑŸÑŸÖÿÆÿ≤ŸàŸÜ.`,
          actions: [
            { label: 'ÿπÿ±ÿ∂ ÿßŸÑŸÖÿÆÿ≤ŸàŸÜ', action: 'view_inventory', url: '/warehouse?tab=inventory' }
          ],
          createdAt: new Date()
        });
      }

      return notifications;
    } catch (error) {
      console.error('Inventory status check error:', error);
      return [];
    }
  }

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
  static getActiveNotifications(): SmartNotification[] {
    const now = new Date();
    return this.notifications.filter(notification => 
      !notification.expiresAt || notification.expiresAt > now
    );
  }

  // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©
  static cleanupExpiredNotifications(): void {
    const now = new Date();
    this.notifications = this.notifications.filter(notification => 
      !notification.expiresAt || notification.expiresAt > now
    );
  }

  // ÿ™ÿ≠ÿØŸäÿØ ŸÖÿß ÿ•ÿ∞ÿß ŸÉÿßŸÜ Ÿäÿ¨ÿ® ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ±
  static shouldSendNotification(action: string): boolean {
    const notificationActions = [
      'add_customer', 'add_order', 'add_production_order', 'add_machine',
      'update_order', 'update_machine', 'delete_customer', 'delete_order',
      'machine_down', 'quality_issue', 'low_inventory', 'production_delay'
    ];

    return notificationActions.includes(action);
  }
}

// ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑŸÅÿ≠ÿµ ÿßŸÑÿØŸàÿ±Ÿä ŸÉŸÑ 15 ÿØŸÇŸäŸÇÿ©
setInterval(async () => {
  await AINotifications.performIntelligentMonitoring();
  AINotifications.cleanupExpiredNotifications();
}, 15 * 60 * 1000);

