}

export class MetaWhatsAppService {
  private config: MetaWhatsAppConfig;
  private storage: IStorage;
  private baseUrl: string;

  constructor(storage: IStorage, config?: Partial<MetaWhatsAppConfig>) {
    this.storage = storage;

    this.config = {
      accessToken: process.env.META_ACCESS_TOKEN || '',
      phoneNumberId: process.env.META_PHONE_NUMBER_ID || '',
      businessAccountId: process.env.META_BUSINESS_ACCOUNT_ID || '795259496521200',
      apiVersion: 'v21.0',
      ...config
    };

    this.baseUrl = `https://graph.facebook.com/${this.config.apiVersion}`;

    if (!this.config.accessToken || !this.config.phoneNumberId) {
      console.warn('‚ö†Ô∏è Meta WhatsApp API credentials not configured. Set META_ACCESS_TOKEN and META_PHONE_NUMBER_ID environment variables.');
    } else {
      console.log('‚úÖ Meta WhatsApp API service initialized successfully');
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÜÿµŸäÿ© ŸÖÿ®ÿßÿ¥ÿ±ÿ©
   */
  async sendTextMessage(
    to: string,
    message: string,
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error('Meta WhatsApp API ÿ∫Ÿäÿ± ŸÖŸèÿπÿØ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      // ÿ™ŸÜÿ≥ŸäŸÇ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ (ÿ•ÿ≤ÿßŸÑÿ© + Ÿàwhatsapp: ÿ•ŸÜ Ÿàÿ¨ÿØÿ™)
      const formattedPhone = to.replace(/[\+\s\-\(\)]/g, '').replace('whatsapp:', '');

      const messageData: WhatsAppTextMessage = {
        to: formattedPhone,
        type: 'text',
        text: {
          body: message
        }
      };

      const response = await fetch(`${this.baseUrl}/${this.config.phoneNumberId}/messages`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.config.accessToken}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(messageData)
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}: ${response.statusText}`);
      }

      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const notificationData = {
        title: options?.title || 'ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: message,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: to,
        status: 'sent' as const,
        external_id: result.messages?.[0]?.id,
        external_status: 'sent',
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(`üì± ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ŸÖÿ®ÿßÿ¥ÿ±ÿ© ÿ•ŸÑŸâ ${to} - ID: ${result.messages?.[0]?.id}`);

      return {
        success: true,
        messageId: result.messages?.[0]?.id
