import twilio from 'twilio';
import type { IStorage } from '../storage';
import { MetaWhatsAppService } from './meta-whatsapp';

export interface NotificationData {
  title: string;
  title_ar?: string;
  message: string;
  message_ar?: string;
  type: 'whatsapp' | 'sms' | 'email' | 'push' | 'system';
  priority?: 'low' | 'normal' | 'high' | 'urgent';
  recipient_type: 'user' | 'group' | 'role' | 'all';
  recipient_id?: string;
  phone_number?: string;
  context_type?: string;
  context_id?: string;
  scheduled_for?: Date;
}

export interface WhatsAppTemplate {
  name: string;
  variables?: string[];
  language?: string;
}

export class NotificationService {
  private twilioClient: twilio.Twilio;
  public metaWhatsApp: MetaWhatsAppService;
  private storage: IStorage;
  private twilioPhoneNumber: string;
  private useMetaAPI: boolean;

  constructor(storage: IStorage) {
    this.storage = storage;

    // ÿ™ÿ≠ÿØŸäÿØ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Meta API ÿ£Ÿà Twilio
    this.useMetaAPI = !!(process.env.META_ACCESS_TOKEN && process.env.META_PHONE_NUMBER_ID);

    // ÿ™ŸáŸäÿ¶ÿ© Meta WhatsApp API
    this.metaWhatsApp = new MetaWhatsAppService(storage);

    // Initialize Twilio client
    const accountSid = process.env.TWILIO_ACCOUNT_SID;
    const authToken = process.env.TWILIO_AUTH_TOKEN;
    this.twilioPhoneNumber = process.env.TWILIO_PHONE_NUMBER || '';

    if (!accountSid || !authToken) {
      console.warn('Twilio credentials not found. WhatsApp messaging via Twilio will be disabled.');
      this.twilioClient = null as any;
    } else {
      this.twilioClient = twilio(accountSid, authToken);
      console.log('‚úÖ Twilio WhatsApp service initialized successfully');
    }

    if (this.useMetaAPI) {
      console.log('üöÄ Using Meta WhatsApp Business API directly');
    } else {
      console.log('üì± Using Twilio as WhatsApp gateway');
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÇÿßŸÑÿ® ŸÖŸèŸàÿßŸÅŸÇ ÿπŸÑŸäŸá
   */
  async sendWhatsAppTemplateMessage(
    phoneNumber: string,
    templateName: string,
    variables: string[] = [],
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.twilioClient) {
        throw new Error('ÿÆÿØŸÖÿ© Twilio ÿ∫Ÿäÿ± ŸÖŸèÿπÿØÿ© ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      const formattedNumber = phoneNumber.startsWith('whatsapp:') 
        ? phoneNumber 
        : `whatsapp:${phoneNumber}`;

      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ContentSid ŸÅŸä ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑÿ®Ÿäÿ¶ÿ©
      const contentSid = process.env.TWILIO_CONTENT_SID;

      let messageData: any;

      if (contentSid && templateName.includes('welcome_hxc4485f514cb7d4536026fc56250f75e7')) {
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Content Template ÿßŸÑŸÖŸèÿπÿØ ŸÅŸä Twilio Console
        messageData = {
          from: `whatsapp:${this.twilioPhoneNumber}`,
          to: formattedNumber,
          contentSid: contentSid
        };

        // ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑŸÇÿßŸÑÿ®
        if (variables && variables.length > 0) {
          messageData.contentVariables = JSON.stringify({
            "1": variables[0] || 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ŸÖŸÜ ŸÜÿ∏ÿßŸÖ MPBF'
          });
        }
      } else {
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÜÿµ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± ŸÉÿ≠ŸÑ ÿ®ÿØŸäŸÑ
        messageData = {
          from: `whatsapp:${this.twilioPhoneNumber}`,
          to: formattedNumber,
          body: variables[0] || 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ŸÖŸÜ ŸÜÿ∏ÿßŸÖ MPBF'
        };

        console.warn('‚ö†Ô∏è TWILIO_CONTENT_SID not configured. Using direct text message. Visit /twilio-content for setup instructions.');
      }

      const twilioMessage = await this.twilioClient.messages.create(messageData);

      // Save notification to database
      const notificationData = {
        title: options?.title || 'ÿ•ÿ¥ÿπÿßÿ± Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: `ŸÇÿßŸÑÿ®: ${templateName} - ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™: ${variables.join(', ')}`,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: phoneNumber,
        status: 'sent' as const,
        twilio_sid: twilioMessage.sid,
        external_status: twilioMessage.status,
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(`üì± ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ®) ÿ•ŸÑŸâ ${phoneNumber} - SID: ${twilioMessage.sid}`);

      return {
        success: true,
        messageId: twilioMessage.sid
      };

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸÇÿßŸÑÿ®):', error);

      const notificationData = {
        title: options?.title || 'ÿ•ÿ¥ÿπÿßÿ± Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: `ŸÇÿßŸÑÿ®: ${templateName} - ÿÆÿ∑ÿ£: ${error.message}`,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: phoneNumber,
        status: 'failed' as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ± Ÿàÿßÿ™ÿ≥ ÿßÿ® (ŸäÿÆÿ™ÿßÿ± API ÿßŸÑŸÖŸÜÿßÿ≥ÿ® ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã)
   */
  async sendWhatsAppMessage(
    phoneNumber: string, 
    message: string, 
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
      useTemplate?: boolean;
      templateName?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {

    if (this.useMetaAPI) {
      // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Meta API ŸÖÿ®ÿßÿ¥ÿ±ÿ©
      if (options?.useTemplate && options?.templateName) {
        return this.metaWhatsApp.sendTemplateMessage(
          phoneNumber,
          options.templateName,
          'ar',
          [message],
          options
        );
      } else {
        return this.metaWhatsApp.sendTextMessage(phoneNumber, message, options);
      }
    } else {
      // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Twilio (ÿßŸÑŸÇÿØŸäŸÖ)
      if (options?.useTemplate) {
        return this.sendWhatsAppTemplateMessage(
          phoneNumber,
          options.templateName || 'welcome_hxc4485f514cb7d4536026fc56250f75e7',
          [message],
          options
        );
      } else {
        return this.sendWhatsAppDirectMessage(phoneNumber, message, options);
      }
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ŸÖÿ®ÿßÿ¥ÿ±ÿ© (ŸÑŸÑÿßÿÆÿ™ÿ®ÿßÿ± ŸÅŸÇÿ∑ ŸÅŸä Sandbox)
   */
  async sendWhatsAppDirectMessage(
    phoneNumber: string, 
    message: string, 
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    }
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.twilioClient) {
        throw new Error('ÿÆÿØŸÖÿ© Twilio ÿ∫Ÿäÿ± ŸÖŸèÿπÿØÿ© ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠');
      }

      // Format phone number for WhatsApp (must include whatsapp: prefix)
      const formattedNumber = phoneNumber.startsWith('whatsapp:') 
        ? phoneNumber 
        : `whatsapp:${phoneNumber}`;

      // Send WhatsApp message via Twilio
      const twilioMessage = await this.twilioClient.messages.create({
        body: message,
        from: `whatsapp:${this.twilioPhoneNumber}`,
        to: formattedNumber
      });

      // Save notification to database
      const notificationData = {
        title: options?.title || 'ÿ•ÿ¥ÿπÿßÿ± Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: message,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: phoneNumber,
        status: 'sent' as const,
        twilio_sid: twilioMessage.sid,
        external_status: twilioMessage.status,
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(`üì± ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ® ÿ•ŸÑŸâ ${phoneNumber} - SID: ${twilioMessage.sid}`);

      return {
        success: true,
        messageId: twilioMessage.sid
      };

    } catch (error: any) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ ÿßÿ®:', error);

      // Save failed notification to database
      const notificationData = {
        title: options?.title || 'ÿ•ÿ¥ÿπÿßÿ± Ÿàÿßÿ™ÿ≥ ÿßÿ®',
        message: message,
        type: 'whatsapp' as const,
        priority: options?.priority || 'normal',
        recipient_type: 'user' as const,
        phone_number: phoneNumber,
        status: 'failed' as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ± ÿ•ŸÑŸâ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≠ÿØÿØ
   */
  async notifyUser(userId: number, notificationData: NotificationData): Promise<boolean> {
    try {
      // Get user details
      const user = await this.storage.getUserById(userId);
      if (!user) {
        console.warn(`ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId} ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ`);
        return false;
      }

      // Check if user has phone number for WhatsApp
      if (notificationData.type === 'whatsapp' && user.phone) {
        const result = await this.sendWhatsAppMessage(
          user.phone,
          notificationData.message_ar || notificationData.message,
          {
            title: notificationData.title_ar || notificationData.title,
            priority: notificationData.priority,
            context_type: notificationData.context_type,
            context_id: notificationData.context_id
          }
        );
        return result.success;
      }

      // Save as system notification if WhatsApp not available
      const dbNotification = {
        ...notificationData,
        recipient_type: 'user' as const,
        recipient_id: userId.toString(),
        status: 'sent' as const
      };

      await this.storage.createNotification(dbNotification);
      return true;

    } catch (error: any) {
      console.error(`ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}:`, error);
      return false;
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿ¨ŸÖÿßÿπŸäÿ© ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿØŸàÿ±
   */
  async notifyByRole(roleId: number, notificationData: NotificationData): Promise<number> {
    try {
      const users = await this.storage.getUsersByRole(roleId);
      let successCount = 0;

      for (const user of users) {
        const success = await this.notifyUser(user.id, notificationData);
        if (success) successCount++;

        // Add small delay to avoid rate limiting
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      console.log(`ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ${successCount} ÿ•ÿ¥ÿπÿßÿ± ŸÖŸÜ ÿ£ÿµŸÑ ${users.length} ŸÑŸÑÿØŸàÿ± ${roleId}`);
      return successCount;

    } catch (error: any) {
      console.error(`ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ŸÑŸÑÿØŸàÿ± ${roleId}:`, error);
      return 0;
    }
  }

  /**
   * ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑÿ≠ÿ∂Ÿàÿ±
   */
  async notifyAttendanceEvent(
    userId: number, 
    eventType: 'check_in' | 'check_out' | 'lunch_start' | 'lunch_end' | 'late',
    additionalInfo?: any
  ): Promise<boolean> {
    const messages = {
      check_in: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑŸÉ ÿ®ŸÜÿ¨ÿßÿ≠ ‚úÖ',
      check_out: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿÆÿ±Ÿàÿ¨ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠ üëã',
      lunch_start: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ®ÿØÿßŸäÿ© ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿßŸÑÿ∫ÿØÿßÿ° üçΩÔ∏è',
      lunch_end: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÜÿ™Ÿáÿßÿ° ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿßŸÑÿ∫ÿØÿßÿ° ‚úÖ',
      late: 'ÿ™ŸÜÿ®ŸäŸá: ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ™ÿ£ÿÆŸäÿ± ŸÅŸä ÿßŸÑÿ≠ÿ∂Ÿàÿ± ‚ö†Ô∏è'
    };

    const titles = {
      check_in: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ',
      check_out: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨',
      lunch_start: 'ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿßŸÑÿ∫ÿØÿßÿ°',
      lunch_end: 'ÿßŸÑÿπŸàÿØÿ© ŸÖŸÜ ÿßŸÑÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ©',
      late: 'ÿ™ŸÜÿ®ŸäŸá ÿ™ÿ£ÿÆŸäÿ±'
    };

    const notificationData: NotificationData = {
      title: titles[eventType],
      message: messages[eventType],
      type: 'whatsapp',
      priority: eventType === 'late' ? 'high' : 'normal',
      recipient_type: 'user',
      context_type: 'attendance',
      context_id: additionalInfo?.attendanceId?.toString()
    };

    return await this.notifyUser(userId, notificationData);
  }

  /**
   * ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
   */
  async notifyOrderEvent(
    orderNumber: string,
    eventType: 'created' | 'completed' | 'delayed' | 'cancelled',
    userIds?: number[]
  ): Promise<number> {
    const messages = {
      created: `ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ∑ŸÑÿ® ÿ¨ÿØŸäÿØ: ${orderNumber} üì¶`,
      completed: `ÿ™ŸÖ ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ∑ŸÑÿ®: ${orderNumber} ‚úÖ`,
      delayed: `ÿ™ÿ£ÿÆŸäÿ± ŸÅŸä ÿßŸÑÿ∑ŸÑÿ®: ${orderNumber} ‚ö†Ô∏è`,
      cancelled: `ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ∑ŸÑÿ®: ${orderNumber} ‚ùå`
    };

    const titles = {
      created: 'ÿ∑ŸÑÿ® ÿ¨ÿØŸäÿØ',
      completed: 'ÿßŸÉÿ™ŸÖÿßŸÑ ÿ∑ŸÑÿ®',
      delayed: 'ÿ™ÿ£ÿÆŸäÿ± ÿ∑ŸÑÿ®',
      cancelled: 'ÿ•ŸÑÿ∫ÿßÿ° ÿ∑ŸÑÿ®'
    };

    const notificationData: NotificationData = {
      title: titles[eventType],
      message: messages[eventType],
      type: 'whatsapp',
      priority: eventType === 'delayed' ? 'high' : 'normal',
      recipient_type: userIds ? 'user' : 'role',
      context_type: 'order',
      context_id: orderNumber
    };

    if (userIds && userIds.length > 0) {
      let successCount = 0;
      for (const userId of userIds) {
        const success = await this.notifyUser(userId, notificationData);
        if (success) successCount++;
        await new Promise(resolve => setTimeout(resolve, 100));
      }
      return successCount;
    } else {
      // Notify managers and supervisors (role_id = 2)
      return await this.notifyByRole(2, notificationData);
    }
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ŸÖŸÜ Twilio
   */
  async updateMessageStatus(twilioSid: string): Promise<boolean> {
    try {
      if (!this.twilioClient) return false;

      const message = await this.twilioClient.messages(twilioSid).fetch();

      // Update notification status in database
      await this.storage.updateNotificationStatus(twilioSid, {
        external_status: message.status,
        delivered_at: message.status === 'delivered' ? new Date() : undefined,
        error_message: message.errorMessage || undefined
      });

      return true;
    } catch (error: any) {
      console.error(`ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ${twilioSid}:`, error);
      return false;
    }
  }

  /**
   * ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ÿßÿÆÿ™ÿ®ÿßÿ±
   */
  async sendTestMessage(phoneNumber: string): Promise<{ success: boolean; message?: string; error?: string }> {
    const testMessage = `
üîß ÿ±ÿ≥ÿßŸÑÿ© ÿßÿÆÿ™ÿ®ÿßÿ± ŸÖŸÜ ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿµŸÜÿπ

ŸÖÿ±ÿ≠ÿ®ÿßŸã! Ÿáÿ∞Ÿá ÿ±ÿ≥ÿßŸÑÿ© ÿßÿÆÿ™ÿ®ÿßÿ± ŸÑŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿπŸÖŸÑ ÿÆÿØŸÖÿ© ÿßŸÑŸàÿßÿ™ÿ≥ ÿßÿ® ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠.

‚è∞ ÿßŸÑÿ™ŸàŸÇŸäÿ™: ${new Date().toLocaleString('ar-SA')}
‚úÖ ÿßŸÑÿÆÿØŸÖÿ© ÿ™ÿπŸÖŸÑ ÿ®ŸÜÿ¨ÿßÿ≠

ÿ¥ŸÉÿ±ÿßŸã ŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÜÿ∏ÿßŸÖŸÜÿß! 
    `.trim();

    const result = await this.sendWhatsAppMessage(phoneNumber, testMessage, {
      title: 'ÿ±ÿ≥ÿßŸÑÿ© ÿßÿÆÿ™ÿ®ÿßÿ±',
      priority: 'normal',
      context_type: 'system',
      context_id: 'test'
    });

    return {
      success: result.success,
      message: result.success ? 'ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ± ÿ®ŸÜÿ¨ÿßÿ≠' : undefined,
      error: result.error
    };
  }
}

