  private async handleDataQuery(message: string, baseResponse: string): Promise<string> {
    try {
      // Extract order numbers or specific identifiers from the message
      const orderMatch = message.match(/JO-\d{4}-\d{3}|ORD-\d+|R-\d+/);

      if (orderMatch) {
        const identifier = orderMatch[0];

        if (identifier.startsWith('JO-')) {
          // Query job order information
          const stats = await storage.getDashboardStats();
          return `${baseResponse}\n\nÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿ©:\n‚Ä¢ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©: ${stats.activeOrders}\n‚Ä¢ ŸÖÿπÿØŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨: ${stats.productionRate}%\n‚Ä¢ ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ¨ŸàÿØÿ©: ${stats.qualityScore}%\n‚Ä¢ ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸáÿØÿ±: ${stats.wastePercentage}%`;
        }
      }

      // For general queries, provide dashboard stats
      if (message.includes('ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™') || message.includes('ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿµŸÜÿπ')) {
        const stats = await storage.getDashboardStats();
        return `ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿµŸÜÿπ ÿßŸÑÿ≠ÿßŸÑŸäÿ©:\n\n‚Ä¢ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©: ${stats.activeOrders} ÿ∑ŸÑÿ®\n‚Ä¢ ŸÖÿπÿØŸÑ ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨: ${stats.productionRate}%\n‚Ä¢ ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ¨ŸàÿØÿ©: ${stats.qualityScore}%\n‚Ä¢ ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸáÿØÿ±: ${stats.wastePercentage}%\n\nŸáŸÑ ÿ™ÿ≠ÿ™ÿßÿ¨ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© ÿ≠ŸàŸÑ ÿ£Ÿä ŸÖŸÜ Ÿáÿ∞Ÿá ÿßŸÑŸÜŸÇÿßÿ∑ÿü`;
