/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ£ÿ±ŸÇÿßŸÖ ŸÑÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÅÿßÿµŸÑÿ© ÿßŸÑÿπÿ¥ÿ±Ÿäÿ© ŸÑŸÑÿ£ÿ±ŸÇÿßŸÖ ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ©
 * Format numbers to remove decimal places for whole numbers
 */
export function formatNumber(value: number | string | null | undefined): string {
  if (value === null || value === undefined || value === '') {
    return '0';
  }

  const num = typeof value === 'string' ? parseFloat(value) : value;

  if (isNaN(num)) {
    return '0';
  }

  // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ±ŸÇŸÖ ÿµÿ≠Ÿäÿ≠ÿßŸã (ÿ®ÿØŸàŸÜ ŸÉÿ≥Ÿàÿ±)ÿå ÿ£ÿ∏Ÿáÿ±Ÿá ÿ®ÿØŸàŸÜ ŸÅÿßÿµŸÑÿ© ÿπÿ¥ÿ±Ÿäÿ©
  if (num % 1 === 0) {
    return num.toString();
  }

  // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ±ŸÇŸÖ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ŸÉÿ≥Ÿàÿ±ÿå ÿ£ÿ∏Ÿáÿ±Ÿá ÿ®ŸÅÿßÿµŸÑÿ© ÿπÿ¥ÿ±Ÿäÿ© ŸÖÿ≠ÿØŸàÿØÿ©
  return num.toFixed(2).replace(/\.?0+$/, '');
}

/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ£ÿ±ŸÇÿßŸÖ ŸÖÿπ ŸÅŸàÿßÿµŸÑ ÿßŸÑÿ¢ŸÑÿßŸÅ
 * Format numbers with thousands separators
 */
export function formatNumberWithCommas(value: number | string | null | undefined): string {
  const formatted = formatNumber(value);
  const num = parseFloat(formatted);

  if (isNaN(num)) {
    return '0';
  }

  return num.toLocaleString('en-US');
}

/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ£Ÿàÿ≤ÿßŸÜ (ŸÉŸäŸÑŸàÿ∫ÿ±ÿßŸÖ)
 * Format weights (kilograms)
 */
export function formatWeight(value: number | string | null | undefined): string {
  return formatNumber(value) + ' ŸÉÿ∫';
}

/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ£ÿ®ÿπÿßÿØ (ÿ≥ŸÖ)
 * Format dimensions (centimeters)
 */
export function formatDimension(value: number | string | null | undefined): string {
  return formatNumber(value) + ' ÿ≥ŸÖ';
}

/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ≥ŸÖÿßŸÉÿ© (ŸÖŸäŸÉÿ±ŸàŸÜ)
 * Format thickness (microns)
 */
export function formatThickness(value: number | string | null | undefined): string {
  return formatNumber(value) + ' ŸÖŸäŸÉÿ±ŸàŸÜ';
}

/**
 * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑŸÜÿ≥ÿ® ÿßŸÑŸÖÿ¶ŸàŸäÿ©
 * Format percentages
 */
export function formatPercentage(value: number | string | null | undefined): string {
  return formatNumber(value) + '%';
}

