import { Settings, RefreshCw, CheckCircle, XCircle, Clock, AlertTriangle, Plus, Edit, Trash2, TestTube, Database, Server, ArrowUpDown } from "lucide-react";
import { useToast } from "../hooks/use-toast";
import Header from "../components/layout/Header";
import Sidebar from "../components/layout/Sidebar";

const erpConfigSchema = z.object({
  name: z.string().min(1, "ÿßŸÑÿßÿ≥ŸÖ ŸÖÿ∑ŸÑŸàÿ®"),
  name_ar: z.string().min(1, "ÿßŸÑÿßÿ≥ŸÖ ÿ®ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ŸÖÿ∑ŸÑŸàÿ®"),
  type: z.enum(["SAP", "Oracle", "Odoo", "QuickBooks", "Custom"]),
  endpoint: z.string().url("ÿ±ÿßÿ®ÿ∑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠"),
  username: z.string().min(1, "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ∑ŸÑŸàÿ®"),
  password: z.string().min(1, "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  is_active: z.boolean().default(true),
  sync_frequency: z.number().min(5, "ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ÿØŸÜŸâ 5 ÿØŸÇÿßÿ¶ŸÇ").default(60)
});

const databaseConfigSchema = z.object({
  name: z.string().min(1, "ÿßŸÑÿßÿ≥ŸÖ ŸÖÿ∑ŸÑŸàÿ®"),
  name_ar: z.string().min(1, "ÿßŸÑÿßÿ≥ŸÖ ÿ®ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ŸÖÿ∑ŸÑŸàÿ®"),
  type: z.enum(["PostgreSQL", "MySQL", "SQL Server", "Oracle", "MongoDB", "MariaDB"]),
  host: z.string().min(1, "ÿπŸÜŸàÿßŸÜ ÿßŸÑÿÆÿßÿØŸÖ ŸÖÿ∑ŸÑŸàÿ®"),
  port: z.number().min(1, "ÿ±ŸÇŸÖ ÿßŸÑŸÖŸÜŸÅÿ∞ ŸÖÿ∑ŸÑŸàÿ®").default(5432),
  database: z.string().min(1, "ÿßÿ≥ŸÖ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ∑ŸÑŸàÿ®"),
  username: z.string().min(1, "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ∑ŸÑŸàÿ®"),
  password: z.string().min(1, "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©"),
  ssl_enabled: z.boolean().default(false),
  is_active: z.boolean().default(true),
  sync_frequency: z.number().min(5, "ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ÿØŸÜŸâ 5 ÿØŸÇÿßÿ¶ŸÇ").default(60)
});

type ERPConfigFormValues = z.infer<typeof erpConfigSchema>;
type DatabaseConfigFormValues = z.infer<typeof databaseConfigSchema>;

export default function ERPIntegration() {
  const [selectedConfig, setSelectedConfig] = useState<any>(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isAddDbDialogOpen, setIsAddDbDialogOpen] = useState(false);
  const [isEditDbDialogOpen, setIsEditDbDialogOpen] = useState(false);
  const [selectedDbConfig, setSelectedDbConfig] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch ERP configurations
  const { data: configurations = [], isLoading: configsLoading } = useQuery({
    queryKey: ["/api/erp/configurations"],
  });

  // Fetch sync logs
  const { data: syncLogs = [], isLoading: logsLoading } = useQuery({
    queryKey: ["/api/erp/sync-logs"],
  });

  // Fetch database configurations
  const { data: dbConfigurations = [], isLoading: dbConfigsLoading } = useQuery({
    queryKey: ["/api/database/configurations"],
  });

  const form = useForm<ERPConfigFormValues>({
    resolver: zodResolver(erpConfigSchema),
    defaultValues: {
      name: "",
      name_ar: "",
      type: "SAP",
      endpoint: "",
      username: "",
      password: "",
      is_active: true,
      sync_frequency: 60
    }
  });

  const dbForm = useForm<DatabaseConfigFormValues>({
    resolver: zodResolver(databaseConfigSchema),
    defaultValues: {
      name: "",
      name_ar: "",
      type: "PostgreSQL",
      host: "",
      port: 5432,
      database: "",
      username: "",
      password: "",
      ssl_enabled: false,
      is_active: true,
      sync_frequency: 60
    }
  });

  // Create configuration mutation
  const createConfig = useMutation({
    mutationFn: async (data: ERPConfigFormValues) => {
      const response = await fetch("/api/erp/configurations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error("ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/erp/configurations"] });
      setIsAddDialogOpen(false);
      form.reset();
      toast({ title: "ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿπÿØÿßÿØÿßÿ™ ERP ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™", variant: "destructive" });
    }
  });

  // Create database configuration mutation
  const createDbConfig = useMutation({
    mutationFn: async (data: DatabaseConfigFormValues) => {
      const response = await fetch("/api/database/configurations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error("ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/database/configurations"] });
      setIsAddDbDialogOpen(false);
      dbForm.reset();
      toast({ title: "ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™", variant: "destructive" });
    }
  });

  // Update database configuration mutation
  const updateDbConfig = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<DatabaseConfigFormValues> }) => {
      const response = await fetch(`/api/database/configurations/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error("ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/database/configurations"] });
      setIsEditDbDialogOpen(false);
      setSelectedDbConfig(null);
      dbForm.reset();
      toast({ title: "ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™", variant: "destructive" });
    }
  });

  // Delete database configuration mutation
  const deleteDbConfig = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/database/configurations/${id}`, {
        method: "DELETE"
      });
      if (!response.ok) throw new Error("ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/database/configurations"] });
      toast({ title: "ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠" });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™", variant: "destructive" });
    }
  });

  // Test connection mutation
  const testConnection = useMutation({
    mutationFn: async (config: any) => {
      const endpoint = config.type ? "/api/database/test-connection" : "/api/erp/test-connection";
      const response = await fetch(endpoint, {
        method: "POST", 
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(config)
      });
      const result = await response.json();
      if (!response.ok) throw new Error(result.message);
      return result;
    },
    onSuccess: (data) => {
      toast({ 
        title: "ŸÜÿ¨ÿ≠ ÿßÿÆÿ™ÿ®ÿßÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ", 
        description: data.details ? `ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿ∫ÿ±ŸÇ: ${data.details.responseTime}ms` : data.message
      });
    },
    onError: (error: any) => {
      toast({ 
        title: "ŸÅÿ¥ŸÑ ÿßÿÆÿ™ÿ®ÿßÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Sync entities mutation
  const syncEntities = useMutation({
    mutationFn: async ({ configId, entityType }: { configId: number; entityType: string }) => {
      const response = await fetch(`/api/erp/sync/${configId}/${entityType}`, {
        method: "POST"
      });
      if (!response.ok) throw new Error("ŸÅÿ¥ŸÑ ŸÅŸä ÿßŸÑŸÖÿ≤ÿßŸÖŸÜÿ©");
      return response.json();
    },
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/erp/sync-logs"] });
      toast({ 
        title: "ÿ™ŸÖÿ™ ÿßŸÑŸÖÿ≤ÿßŸÖŸÜÿ© ÿ®ŸÜÿ¨ÿßÿ≠", 
        description: `ÿ™ŸÖ ŸÖÿπÿßŸÑÿ¨ÿ© ${data.success} ÿπŸÜÿµÿ± ÿ®ŸÜÿ¨ÿßÿ≠${data.failed > 0 ? ` Ÿà ${data.failed} ŸÅÿ¥ŸÑ` : ''}` 
      });
    },
    onError: () => {
      toast({ title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿ≤ÿßŸÖŸÜÿ©", variant: "destructive" });
    }
  });

  const onSubmit = (data: ERPConfigFormValues) => {
    createConfig.mutate(data);
  };

  const onDbSubmit = (data: DatabaseConfigFormValues) => {
    if (selectedDbConfig) {
      updateDbConfig.mutate({ id: selectedDbConfig.id, data });
    } else {
      createDbConfig.mutate(data);
    }
  };

  const handleEditDbConfig = (config: any) => {
    setSelectedDbConfig(config);
    dbForm.reset({
      name: config.name,
      name_ar: config.name_ar,
      type: config.type,
      host: config.host,
      port: config.port,
      database: config.database,
      username: config.username,
      password: config.password,
      ssl_enabled: config.ssl_enabled,
      is_active: config.is_active,
      sync_frequency: config.sync_frequency
    });
    setIsEditDbDialogOpen(true);
  };

  const handleDeleteDbConfig = (id: number) => {
    if (confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ÿü")) {
      deleteDbConfig.mutate(id);
    }
  };

  const handleTestConnection = (config: any) => {
    testConnection.mutate(config);
  };

  const handleSync = (configId: number, entityType: string) => {
    syncEntities.mutate({ configId, entityType });
  };

  const getStatusBadge = (status: string) => {
    const variants: Record<string, "default" | "secondary" | "destructive" | "outline"> = {
      success: "default",
      failed: "destructive", 
      partial: "secondary",
      pending: "outline"
    };

    const icons: Record<string, React.ReactElement> = {
      success: <CheckCircle className="h-3 w-3" />,
      failed: <XCircle className="h-3 w-3" />,
      partial: <AlertTriangle className="h-3 w-3" />,
      pending: <Clock className="h-3 w-3" />
    };

    return (
      <Badge variant={variants[status] || "outline"} className="flex items-center gap-1">
        {icons[status] || <Clock className="h-3 w-3" />}
        {status === 'success' ? 'ŸÜÿ¨ÿ≠' : status === 'failed' ? 'ŸÅÿ¥ŸÑ' : status === 'partial' ? 'ÿ¨ÿ≤ÿ¶Ÿä' : 'ŸÅŸä ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±'}
      </Badge>
    );
  };

  const getSystemIcon = (type: string) => {
    const icons: Record<string, string> = {
      SAP: "üè¢",
      Oracle: "üî∂", 
      Odoo: "üü£",
      QuickBooks: "üí∞",
      Custom: "‚öôÔ∏è"
    };
    return icons[type] || "‚öôÔ∏è";
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="flex">
        <Sidebar />
        <main className="flex-1 lg:mr-64 p-6" dir="rtl">
