import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { ScrollArea } from "../ui/scroll-area";
import { Badge } from "../ui/badge";
import { useToast } from "../../hooks/use-toast";
import { apiRequest } from "../../lib/queryClient";
import ErrorBoundary from "../ErrorBoundary";
import { Bot, User, Send, Mic, MicOff, Volume2, FileText, Bell, TrendingUp, Settings } from "lucide-react";
import { generateMessageId } from "../../../../shared/id-generator";

interface Message {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

export default function AIAssistant() {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      type: 'assistant',
      content: `ü§ñ ŸÖÿ±ÿ≠ÿ®ÿßŸã! ÿ£ŸÜÿß ŸÖÿ≥ÿßÿπÿØŸÉ ÿßŸÑÿ∞ŸÉŸä ÿßŸÑŸÖÿ™ÿ∑Ÿàÿ± ŸÅŸä ŸÜÿ∏ÿßŸÖ MPBF Next.

**ŸÇÿØÿ±ÿßÿ™Ÿä ÿßŸÑŸÖÿ™ŸÇÿØŸÖÿ©:**
üóÑÔ∏è **ÿ•ÿØÿßÿ±ÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÉÿßŸÖŸÑÿ©** - ÿ•ÿ∂ÿßŸÅÿ©ÿå ÿ™ÿπÿØŸäŸÑÿå ÿ≠ÿ∞ŸÅ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≥ÿ¨ŸÑÿßÿ™
üìä **ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿßŸÑÿ∞ŸÉŸäÿ©** - ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ Ÿàÿ•ŸÜÿ¥ÿßÿ° ÿ™ŸÇÿßÿ±Ÿäÿ± ÿ™ŸÅÿßÿπŸÑŸäÿ©  
üîî **ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑÿ∞ŸÉŸä ŸÑŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™** - ÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿ™ŸÑŸÇÿßÿ¶Ÿäÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ≠ÿßÿ¨ÿ©
üß† **ÿßŸÑÿ™ÿπŸÑŸÖ ÿßŸÑŸÖÿ≥ÿ™ŸÖÿ±** - ÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ£ÿØÿßÿ° ŸÖŸÜ ÿÆŸÑÿßŸÑ ÿ™ÿ≠ŸÑŸäŸÑ ÿ£ŸÜŸÖÿßÿ∑ ÿßŸÑÿπŸÖŸÑ
‚öôÔ∏è **ÿßŸÑÿ™ÿ∑ŸàŸäÿ± ÿßŸÑÿ∞ÿßÿ™Ÿä** - ÿ™ÿ≠ÿ≥ŸäŸÜ Ÿàÿ™ÿ∑ŸàŸäÿ± Ÿàÿ∏ÿßÿ¶ŸÅ ÿßŸÑŸÜÿ∏ÿßŸÖ

**ÿ£ŸÖÿ´ŸÑÿ© ÿπŸÑŸâ ŸÖÿß ŸäŸÖŸÉŸÜŸÜŸä ŸÅÿπŸÑŸá:**
‚Ä¢ "ÿ£ÿ∂ŸÅ ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ ÿßÿ≥ŸÖŸá ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ ŸÖŸÜ ÿßŸÑÿ±Ÿäÿßÿ∂"
‚Ä¢ "ÿßÿπÿ±ÿ∂ ŸÑŸä ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ŸÑŸáÿ∞ÿß ÿßŸÑÿ£ÿ≥ÿ®Ÿàÿπ"  
‚Ä¢ "ÿ≠ÿØÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ∑ŸÑÿ® ÿ±ŸÇŸÖ ORD-123 ÿ•ŸÑŸâ ŸÖŸÉÿ™ŸÖŸÑ"
‚Ä¢ "ÿßÿ≠ÿ∞ŸÅ ÿßŸÑŸÖŸÉŸäŸÜÿ© ÿ±ŸÇŸÖ 5"
‚Ä¢ "ÿ£ÿ±ÿ≥ŸÑ ÿ™ŸÜÿ®ŸäŸá ÿµŸäÿßŸÜÿ© ŸÑŸÑŸÖŸÉÿßÿ¶ŸÜ ÿßŸÑŸÖÿ™ŸàŸÇŸÅÿ©"

ŸÉŸäŸÅ ŸäŸÖŸÉŸÜŸÜŸä ŸÖÿ≥ÿßÿπÿØÿ™ŸÉ ÿßŸÑŸäŸàŸÖÿü`,
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [showQuickActions, setShowQuickActions] = useState(true);
  const { toast } = useToast();

  // ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© ŸÑŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä
  const quickActions = [
    { 
      label: 'ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨', 
      icon: TrendingUp, 
      command: 'ÿßÿπÿ±ÿ∂ ŸÑŸä ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑÿ∞ŸÉŸä',
      description: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿ¥ÿßŸÖŸÑ ŸÑŸÑÿ•ŸÜÿ™ÿßÿ¨ ŸÖÿπ ÿ™ŸàÿµŸäÿßÿ™'
    },
    { 
      label: 'ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÖŸäŸÑ', 
      icon: User, 
      command: 'ÿ£ÿ∂ŸÅ ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ',
      description: 'ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä'
    },
    { 
      label: 'ŸÅÿ≠ÿµ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™', 
      icon: Bell, 
      command: 'ÿßÿπÿ±ÿ∂ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ŸàÿßŸÑÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©',
      description: 'ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑÿ∞ŸÉŸäÿ©'
    },
    { 
      label: 'ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ', 
      icon: Settings, 
      command: 'ŸÖÿß ŸáŸä ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ ÿ≠ÿßŸÑŸäÿßŸãÿü',
      description: 'ŸÖÿ±ÿßÿ¨ÿπÿ© ÿ≠ÿßŸÑÿ© ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸÉÿßÿ¶ŸÜ'
    }
  ];

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await fetch('/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message, context: 'factory_operations', userId: 1 })
      });
      return response.json();
    },
    onSuccess: (response: any) => {
      const assistantMessage: Message = {
        id: generateMessageId(),
        type: 'assistant',
        content: response.reply || 'ÿπÿ∞ÿ±ÿßŸãÿå ŸÑŸÖ ÿ£ÿ≥ÿ™ÿ∑ÿπ ŸÖÿπÿßŸÑÿ¨ÿ© ÿ∑ŸÑÿ®ŸÉ ŸÅŸä ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ≠ÿßŸÑŸä.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, assistantMessage]);
    },
    onError: () => {
      const errorMessage: Message = {
        id: generateMessageId(),
        type: 'assistant',
        content: 'ÿπÿ∞ÿ±ÿßŸãÿå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
      toast({
        title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä",
        description: "ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑŸàÿµŸàŸÑ ŸÑÿÆÿØŸÖÿ© ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä ÿ≠ÿßŸÑŸäÿßŸã",
        variant: "destructive"
      });
    }
  });

  const handleSendMessage = (message?: string) => {
    const messageToSend = message || inputValue.trim();
    if (!messageToSend) return;

    const userMessage: Message = {
      id: generateMessageId(),
      type: 'user',
      content: messageToSend,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    sendMessageMutation.mutate(messageToSend);
    setInputValue('');
    setShowQuickActions(false);
  };

  const handleQuickAction = (command: string) => {
    handleSendMessage(command);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const toggleVoiceInput = () => {
    if (!isListening) {
      // Start voice recognition
      setIsListening(true);
      toast({
        title: "ÿßŸÑÿßÿ≥ÿ™ŸÖÿßÿπ ŸÜÿ¥ÿ∑",
        description: "ÿ™ÿ≠ÿØÿ´ ÿßŸÑÿ¢ŸÜ..."
      });

      // Simulate voice recognition (replace with actual implementation)
      const timeoutId = setTimeout(() => {
        setIsListening(false);
        setInputValue("ŸÖÿß ŸáŸà ÿ≠ÿßŸÑÿ© ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿßŸÑŸäŸàŸÖÿü");
      }, 3000);

      // Store timeout ID for cleanup
      (window as any).__voiceTimeout = timeoutId;
    } else {
      setIsListening(false);
      // Clear any pending timeout
      if ((window as any).__voiceTimeout) {
        clearTimeout((window as any).__voiceTimeout);
        (window as any).__voiceTimeout = null;
      }
    }
  };

  const speakMessage = (content: string) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(content);
      utterance.lang = 'ar-SA';
      speechSynthesis.speak(utterance);
    }
  };

  return (
    <Card className="h-96">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2">
          <Bot className="w-5 h-5 text-blue-500" />
          ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä
          <Badge variant="secondary" className="mr-auto">
            ŸÜÿ¥ÿ∑
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent className="p-0">
        <ScrollArea className="h-64 p-4">
          <div className="space-y-4">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex gap-3 ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div className={`flex gap-2 max-w-[80%] ${message.type === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    message.type === 'user' ? 'bg-blue-100' : 'bg-green-100'
                  }`}>
                    {message.type === 'user' ? (
                      <User className="w-4 h-4 text-blue-600" />
                    ) : (
                      <Bot className="w-4 h-4 text-green-600" />
                    )}
                  </div>
                  <div className={`rounded-lg p-3 ${
                    message.type === 'user' 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-100 text-gray-900'
                  }`}>
                    <p className="text-sm">{message.content}</p>
                    {message.type === 'assistant' && (
                      <Button
                        variant="ghost"
                        size="sm"
                        className="mt-1 h-6 p-1 text-gray-500 hover:text-gray-700"
                        onClick={() => speakMessage(message.content)}
                      >
                        <Volume2 className="w-3 h-3" />
                      </Button>
                    )}
                  </div>
                </div>
              </div>
            ))}
            {sendMessageMutation.isPending && (
              <div className="flex justify-start">
                <div className="flex gap-2">
                  <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center">
                    <Bot className="w-4 h-4 text-green-600" />
                  </div>
                  <div className="bg-gray-100 rounded-lg p-3">
                    <div className="flex gap-1">
