                            <Clock className="w-3 h-3" />
                            <span>
                              {new Date(roll.created_at).toLocaleDateString('ar-SA', {
                                day: 'numeric',
                                month: 'short',
                                hour: '2-digit',
                                minute: '2-digit'
                              })}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="text-right">
                      {roll.length && (
                        <div className="text-sm font-medium text-gray-900">
                          {roll.length} ŸÖ
                        </div>
                      )}
                      {roll.weight && (
                        <div className="text-xs text-gray-500">
                          {roll.weight} ŸÉÿ∫
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Progress bar for in-progress rolls */}
                  {roll.status === 'in_progress' && roll.length && roll.target_length && (
                    <div className="mt-3">
                      <div className="flex justify-between text-xs text-gray-600 mb-1">
                        <span>ÿßŸÑÿ™ŸÇÿØŸÖ</span>
                        <span>{Math.round((roll.length / roll.target_length) * 100)}%</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${Math.min((roll.length / roll.target_length) * 100, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <div className="p-8 text-center">
              <Package className="w-12 h-12 text-gray-400 mx-auto mb-3" />
              <p className="text-gray-600 mb-2">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ±ŸàŸÑÿßÿ™ ÿ≠ÿØŸäÿ´ÿ©</p>
              <p className="text-sm text-gray-500">ÿ≥ÿ™ÿ∏Ÿáÿ± ÿßŸÑÿ±ŸàŸÑÿßÿ™ ÿßŸÑÿ¨ÿØŸäÿØÿ© ŸáŸÜÿß</p>
            </div>
          )}
        </ScrollArea>
      </CardContent>
    </Card>
  );
}

