              <div className="mr-4">
                <p className="text-sm font-medium text-gray-600">ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿßŸÑÿ∫ÿØÿßÿ°</p>
                <p className="text-2xl font-bold text-orange-600">
                  {attendanceSummary.filter((u: AttendanceSummaryItem) => u.attendance.status === 'ÿßÿ≥ÿ™ÿ±ÿßÿ≠ÿ© ÿ∫ÿØÿßÿ°').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <LogOut className="h-8 w-8 text-gray-600" />
              <div className="mr-4">
                <p className="text-sm font-medium text-gray-600">ÿßŸÑŸÖÿ∫ÿßÿØÿ±ŸàŸÜ</p>
                <p className="text-2xl font-bold text-gray-600">
                  {attendanceSummary.filter((u: AttendanceSummaryItem) => u.attendance.status === 'ŸÖÿ∫ÿßÿØÿ±').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Attendance Table */}
      <Card>
        <CardHeader>
          <CardTitle>ÿ≠ÿ∂Ÿàÿ± ÿßŸÑŸäŸàŸÖ - {format(new Date(), 'dd/MM/yyyy')}</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="text-center">ÿßŸÑŸÖŸàÿ∏ŸÅ</TableHead>
                <TableHead className="text-center">ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</TableHead>
                <TableHead className="text-center">ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ∂Ÿàÿ±</TableHead>
                <TableHead className="text-center">ÿßŸÑŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™</TableHead>
                <TableHead className="text-center">ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´</TableHead>
                <TableHead className="text-center">ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {isLoading ? (
                <TableRow>
                  <TableCell colSpan={6} className="text-center py-8">
                    ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...
                  </TableCell>
                </TableRow>
              ) : attendanceSummary.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={6} className="text-center py-8">
                    ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿ≠ÿ∂Ÿàÿ±
                  </TableCell>
                </TableRow>
              ) : (
                attendanceSummary.map((user: any) => (
                  <TableRow key={user.id}>
                    <TableCell className="font-medium text-center">
                      {user.display_name_ar || user.display_name || user.username}
                    </TableCell>
                    <TableCell className="text-center text-gray-500">
                      {user.username}
                    </TableCell>
                    <TableCell className="text-center">
                      {getStatusBadge(user.attendance.status)}
                    </TableCell>
                    <TableCell className="text-center">
                      {user.attendance.notes || '-'}
                    </TableCell>
                    <TableCell className="text-center">
                      {user.attendance.updated_at ? 
                        format(new Date(user.attendance.updated_at), 'HH:mm') : 
                        '-'
                      }
                    </TableCell>
                    <TableCell className="text-center">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEdit(user.attendance)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

