              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={evaluationForm.control}
                  name="strengths"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ŸÜŸÇÿßÿ∑ ÿßŸÑŸÇŸàÿ©</FormLabel>
                      <FormControl>
                        <Textarea {...field} rows={3} data-testid="textarea-strengths" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={evaluationForm.control}
                  name="areas_for_improvement"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ŸÖÿ¨ÿßŸÑÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ≥ŸäŸÜ</FormLabel>
                      <FormControl>
                        <Textarea {...field} rows={3} data-testid="textarea-improvements" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={evaluationForm.control}
                name="additional_notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©</FormLabel>
                    <FormControl>
                      <Textarea {...field} rows={2} data-testid="textarea-additional-notes" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex justify-end gap-2">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsEvaluationOpen(false)}
                  data-testid="button-cancel-evaluation"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </Button>
                <Button type="submit" disabled={createEvaluationMutation.isPending} data-testid="button-save-evaluation">
                  {createEvaluationMutation.isPending ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏..." : "ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ŸÇŸäŸäŸÖ"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

