import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "../ui/card";
import { Button } from "../ui/button";
import { Badge } from "../ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogTrigger } from "../ui/dialog";
import { Textarea } from "../ui/textarea";
import { Label } from "../ui/label";
import { useToast } from "../../hooks/use-toast";
import { apiRequest } from "../../lib/queryClient";
import { 
  Calendar, 
  Clock, 
  CheckCircle,
  XCircle,
  AlertCircle,
  Plus,
  User,
  CalendarDays,
  Eye,
  Check,
  X
} from "lucide-react";

interface UserRequest {
  id: number;
  user_id: number;
  type: string;
  title: string;
  description: string;
  status: string; // Allow any status value to handle Arabic statuses
  priority: string; // Allow any priority value to handle Arabic priorities
  start_date?: string;
  end_date?: string;
  requested_amount?: number;
  manager_comments?: string;
  created_at: string;
  updated_at?: string;
  user?: {
    id: number;
    username: string;
    display_name?: string;
    display_name_ar?: string;
  };
}

export default function LeaveManagement() {
  const [selectedRequest, setSelectedRequest] = useState<UserRequest | null>(null);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [isApprovalDialogOpen, setIsApprovalDialogOpen] = useState(false);
  const [approvalComments, setApprovalComments] = useState("");
  const [approvalAction, setApprovalAction] = useState<'approve' | 'reject'>('approve');

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { 
    data: userRequests = [], 
    isLoading: requestsLoading, 
    error: requestsError,
    refetch: refetchRequests,
    isFetching 
  } = useQuery<UserRequest[]>({
    queryKey: ['/api/user-requests'],
    initialData: [],
    refetchOnWindowFocus: false,
    staleTime: 0, // Always refetch
    retry: 3,
    enabled: true // Explicitly enable the query
  });

  // Debug logging
  console.log('User requests data:', userRequests);
  console.log('Loading state:', requestsLoading);
  console.log('Is fetching:', isFetching);
  console.log('Error state:', requestsError);

  // Force refetch when component mounts
  React.useEffect(() => {
    console.log('LeaveManagement component mounted, forcing refetch...');
    queryClient.invalidateQueries({ queryKey: ['/api/user-requests'] });
  }, [queryClient]);

  // Manual test function
  const testApiCall = async () => {
    try {
      console.log('Testing manual API call...');
      const response = await fetch('/api/user-requests', {
        credentials: 'include'
      });
      const data = await response.json();
      console.log('Manual API response:', data);
      console.log('Response status:', response.status);
      console.log('Response headers:', Array.from(response.headers.entries()));
    } catch (error) {
      console.error('Manual API error:', error);
    }
  };

  const { data: users = [], isLoading: usersLoading } = useQuery<any[]>({
    queryKey: ['/api/users'],
    initialData: []
  });

  const updateRequestMutation = useMutation({
    mutationFn: async ({ id, status, comments }: { id: number; status: string; comments: string }) => {
      return await apiRequest(`/api/user-requests/${id}`, {
        method: 'PATCH',
        body: JSON.stringify({
          status,
          manager_comments: comments
        })
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user-requests'] });
      setIsApprovalDialogOpen(false);
      setApprovalComments("");
      toast({
        title: "ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ∑ŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠",
        description: "ÿ™ŸÖ ÿ≠ŸÅÿ∏ ŸÇÿ±ÿßÿ± ÿßŸÑŸÖŸàÿßŸÅŸÇÿ©/ÿßŸÑÿ±ŸÅÿ∂",
      });
    },
    onError: () => {
      toast({
        title: "ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ∑ŸÑÿ®",
        description: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ∑ŸÑÿ®",
        variant: "destructive",
      });
    }
  });

  const getStatusColor = (status: string) => {
    if (!status) return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    // Handle both English and Arabic status values
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === 'approved' || status === 'ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸá' || status === 'ŸÖŸàÿßŸÅŸÇ') {
      return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
    }
    if (lowerStatus === 'rejected' || status === 'ŸÖÿ±ŸÅŸàÿ∂') {
      return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
    }
    if (lowerStatus === 'pending' || status === 'ŸÖÿπŸÑŸÇ' || status === 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©') {
      return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
    }
    return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
  };

  const getStatusText = (status: string) => {
    if (!status) return status;
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === 'approved' || status === 'ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸá' || status === 'ŸÖŸàÿßŸÅŸÇ') {
      return 'ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸá';
    }
    if (lowerStatus === 'rejected' || status === 'ŸÖÿ±ŸÅŸàÿ∂') {
      return 'ŸÖÿ±ŸÅŸàÿ∂';
    }
    if (lowerStatus === 'pending' || status === 'ŸÖÿπŸÑŸÇ' || status === 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©') {
      return 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©';
    }
    return status;
  };

  const getStatusIcon = (status: string) => {
    if (!status) return <Clock className="w-4 h-4" />;
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === 'approved' || status === 'ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸá' || status === 'ŸÖŸàÿßŸÅŸÇ') {
      return <CheckCircle className="w-4 h-4" />;
    }
    if (lowerStatus === 'rejected' || status === 'ŸÖÿ±ŸÅŸàÿ∂') {
      return <XCircle className="w-4 h-4" />;
    }
    if (lowerStatus === 'pending' || status === 'ŸÖÿπŸÑŸÇ' || status === 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©') {
      return <AlertCircle className="w-4 h-4" />;
    }
    return <Clock className="w-4 h-4" />;
  };

  const getPriorityColor = (priority: string) => {
    switch (priority.toLowerCase()) {
      case 'high':
      case 'ÿπÿßŸÑŸäÿ©': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      case 'medium':
      case 'ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©':
      case 'ÿπÿßÿØŸä':
      case 'ÿπÿßÿØŸäÿ©': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'low':
      case 'ŸÖŸÜÿÆŸÅÿ∂ÿ©': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  const getPriorityText = (priority: string) => {
    switch (priority.toLowerCase()) {
      case 'high':
      case 'ÿπÿßŸÑŸäÿ©': return 'ÿπÿßŸÑŸäÿ©';
      case 'medium':
      case 'ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©':
      case 'ÿπÿßÿØŸä':
      case 'ÿπÿßÿØŸäÿ©': return 'ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©';
      case 'low':
      case 'ŸÖŸÜÿÆŸÅÿ∂ÿ©': return 'ŸÖŸÜÿÆŸÅÿ∂ÿ©';
      default: return priority;
    }
  };

  const handleApproval = (request: UserRequest, action: 'approve' | 'reject') => {
    setSelectedRequest(request);
    setApprovalAction(action);
    setIsApprovalDialogOpen(true);
  };

  const handleSubmitApproval = () => {
    if (!selectedRequest) return;

    updateRequestMutation.mutate({
      id: selectedRequest.id,
      status: approvalAction,
      comments: approvalComments
    });
  };

  const getUserDisplayName = (userId: number) => {
    if (!Array.isArray(users) || users.length === 0) return `ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}`;
    const user = users.find((u: any) => u.id === userId);
    return user ? (user.display_name_ar || user.display_name || user.username) : `ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${userId}`;
  };

  if (requestsLoading || usersLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="text-gray-600 dark:text-gray-400 mt-2">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ...</p>
        </div>
      </div>
    );
  }

  const pendingRequests = Array.isArray(userRequests) ? userRequests.filter((req: any) => {
    const status = req.status?.toLowerCase();
    return status === 'pending' || req.status === 'ŸÖÿπŸÑŸÇ' || req.status === 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©';
  }) : [];
  const approvedRequests = Array.isArray(userRequests) ? userRequests.filter((req: any) => {
    const status = req.status?.toLowerCase();
    return status === 'approved' || req.status === 'ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸá' || req.status === 'ŸÖŸàÿßŸÅŸÇ';
  }) : [];
  const rejectedRequests = Array.isArray(userRequests) ? userRequests.filter((req: any) => {
    const status = req.status?.toLowerCase();
    return status === 'rejected' || req.status === 'ŸÖÿ±ŸÅŸàÿ∂';
  }) : [];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            ÿ•ÿØÿßÿ±ÿ© ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
          </h2>
          <p className="text-gray-600 dark:text-gray-300">
            ŸÖÿ±ÿßÿ¨ÿπÿ© ŸàÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ÿπŸÑŸâ ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
          </p>
        </div>
        <div className="flex gap-2">
          <Button onClick={testApiCall} variant="outline" className="text-sm">
            ÿßÿÆÿ™ÿ®ÿßÿ± API
          </Button>
          <Button onClick={() => refetchRequests()} variant="outline" className="text-sm">
            ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                  ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
                </p>
                <p className="text-2xl font-bold text-blue-600">
                  {Array.isArray(userRequests) ? userRequests.length : 0}
                </p>
              </div>
              <CalendarDays className="w-8 h-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                  ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©
                </p>
                <p className="text-2xl font-bold text-yellow-600">
                  {pendingRequests.length}
                </p>
              </div>
              <Clock className="w-8 h-8 text-yellow-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                  ŸÖŸàÿßŸÅŸÇ ÿπŸÑŸäŸáÿß
                </p>
                <p className="text-2xl font-bold text-green-600">
                  {approvedRequests.length}
                </p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                  ŸÖÿ±ŸÅŸàÿ∂ÿ©
                </p>
                <p className="text-2xl font-bold text-red-600">
                  {rejectedRequests.length}
                </p>
              </div>
              <XCircle className="w-8 h-8 text-red-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Requests Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5" />
            ŸÇÿßÿ¶ŸÖÿ© ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
          </CardTitle>
        </CardHeader>
        <CardContent>
          {requestsLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
              <p className="text-gray-600 dark:text-gray-400 mt-2">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™...</p>
            </div>
          ) : !Array.isArray(userRequests) || userRequests.length === 0 ? (
            <div className="text-center py-8 text-gray-500 dark:text-gray-400">
              <Calendar className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™ ŸÖÿ≥ÿ¨ŸÑÿ©</p>
              {requestsError && (
                <p className="text-red-500 mt-2">ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™: {String(requestsError)}</p>
              )}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="border-b bg-gray-50 dark:bg-gray-800">
                    <th className="text-right p-3 font-semibold">ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</th>
                    <th className="text-right p-3 font-semibold">ŸÜŸàÿπ ÿßŸÑÿ∑ŸÑÿ®</th>
                    <th className="text-right p-3 font-semibold">ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
                    <th className="text-right p-3 font-semibold">ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©</th>
                    <th className="text-right p-3 font-semibold">ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                    <th className="text-right p-3 font-semibold">ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ∑ŸÑÿ®</th>
                    <th className="text-center p-3 font-semibold">ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                  </tr>
                </thead>
                <tbody>
                  {Array.isArray(userRequests) && userRequests.map((request: any) => (
                    <tr key={request.id} className="border-b hover:bg-gray-50 dark:hover:bg-gray-800">
                      <td className="p-3">
                        <div className="flex items-center gap-2">
                          <User className="w-4 h-4 text-gray-400" />
                          <span className="font-medium">
                            {getUserDisplayName(request.user_id)}
                          </span>
                        </div>
                      </td>
                      <td className="p-3">
                        <Badge variant="outline" className="bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-300">
                          {request.type}
                        </Badge>
                      </td>
                      <td className="p-3">
                        <span className="text-sm font-medium">
                          {request.title}
                        </span>
                      </td>
                      <td className="p-3">
                        <Badge className={getPriorityColor(request.priority)}>
                          {getPriorityText(request.priority)}
                        </Badge>
                      </td>
                      <td className="p-3">
                        <Badge className={getStatusColor(request.status)}>
                          <span className="flex items-center gap-1">
                            {getStatusIcon(request.status)}
                            {getStatusText(request.status)}
                          </span>
                        </Badge>
                      </td>
                      <td className="p-3 text-sm text-gray-600 dark:text-gray-400">
                        {new Date(request.created_at).toLocaleDateString('ar-SA')}
                      </td>
                      <td className="p-3">
                        <div className="flex justify-center gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            className="bg-blue-50 text-blue-600 hover:bg-blue-100 dark:bg-blue-900 dark:text-blue-300"
                            onClick={() => {
                              setSelectedRequest(request);
                              setIsViewDialogOpen(true);
                            }}
                          >
                            <Eye className="w-4 h-4 mr-1" />
                            ÿπÿ±ÿ∂
                          </Button>
                          {(request.status?.toLowerCase() === 'pending' || request.status === 'ŸÖÿπŸÑŸÇ' || request.status === 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©') && (
                            <>
                              <Button
                                size="sm"
                                className="bg-green-600 hover:bg-green-700 text-white"
                                onClick={() => handleApproval(request, 'approve')}
                              >
                                <Check className="w-4 h-4 mr-1" />
                                ŸÖŸàÿßŸÅŸÇÿ©
                              </Button>
                              <Button
                                size="sm"
                                className="bg-red-600 hover:bg-red-700 text-white"
                                onClick={() => handleApproval(request, 'reject')}
                              >
                                <X className="w-4 h-4 mr-1" />
                                ÿ±ŸÅÿ∂
                              </Button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
