import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "../ui/card";
import { Button } from "../ui/button";
import { Badge } from "../ui/badge";
import { Progress } from "../ui/progress";
import { formatNumber, formatPercentage } from '../../lib/formatNumber';
import { 
  Target, 
  Star,
  TrendingUp,
  Calendar,
  User,
  Plus,
  BarChart3,
  Award
} from "lucide-react";

interface PerformanceReview {
  id: number;
  employee_id: number;
  review_period_start: string;
  review_period_end: string;
  reviewer_id: number;
  review_type: 'annual' | 'quarterly' | 'project_based' | 'probation';
  status: 'draft' | 'in_progress' | 'completed' | 'approved';
  overall_score?: number;
  overall_rating?: 'excellent' | 'very_good' | 'good' | 'needs_improvement' | 'unsatisfactory';
  goals_for_next_period?: string;
  development_plan?: string;
  reviewer_comments?: string;
  employee_comments?: string;
  created_at: string;
  updated_at?: string;
}

interface PerformanceCriteria {
  id: number;
  name: string;
  name_ar: string;
  description?: string;
  description_ar?: string;
  weight: number;
  is_active: boolean;
  category: string;
}

export default function PerformanceReviews() {
  const [selectedReview, setSelectedReview] = useState<number | null>(null);

  const { data: reviews = [], isLoading: reviewsLoading } = useQuery<PerformanceReview[]>({
    queryKey: ['/api/hr/performance-reviews'],
    initialData: []
  });

  const { data: criteria = [], isLoading: criteriaLoading } = useQuery<PerformanceCriteria[]>({
    queryKey: ['/api/hr/performance-criteria'],
    initialData: []
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'in_progress': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'draft': return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
      case 'approved': return 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'completed': return 'ŸÖŸÉÿ™ŸÖŸÑ';
      case 'in_progress': return 'ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©';
      case 'draft': return 'ŸÖÿ≥ŸàÿØÿ©';
      case 'approved': return 'ŸÖÿπÿ™ŸÖÿØ';
      default: return status;
    }
  };

  const getRatingColor = (rating: string) => {
    switch (rating) {
      case 'excellent': return 'text-green-600 dark:text-green-400';
      case 'very_good': return 'text-blue-600 dark:text-blue-400';
      case 'good': return 'text-yellow-600 dark:text-yellow-400';
      case 'needs_improvement': return 'text-orange-600 dark:text-orange-400';
      case 'unsatisfactory': return 'text-red-600 dark:text-red-400';
      default: return 'text-gray-600 dark:text-gray-400';
    }
  };

  const getRatingText = (rating: string) => {
    switch (rating) {
      case 'excellent': return 'ŸÖŸÖÿ™ÿßÿ≤';
      case 'very_good': return 'ÿ¨ŸäÿØ ÿ¨ÿØÿßŸã';
      case 'good': return 'ÿ¨ŸäÿØ';
      case 'needs_improvement': return 'Ÿäÿ≠ÿ™ÿßÿ¨ ÿ™ÿ≠ÿ≥ŸäŸÜ';
      case 'unsatisfactory': return 'ÿ∫Ÿäÿ± ŸÖÿ±ÿ∂Ÿä';
      default: return rating;
    }
  };

  const getReviewTypeText = (type: string) => {
    switch (type) {
      case 'annual': return 'ÿ™ŸÇŸäŸäŸÖ ÿ≥ŸÜŸàŸä';
      case 'quarterly': return 'ÿ™ŸÇŸäŸäŸÖ ÿ±ÿ®ÿπ ÿ≥ŸÜŸàŸä';
      case 'project_based': return 'ÿ™ŸÇŸäŸäŸÖ ŸÖÿ¥ÿ±Ÿàÿπ';
      case 'probation': return 'ÿ™ŸÇŸäŸäŸÖ ŸÅÿ™ÿ±ÿ© ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ©';
      default: return type;
    }
  };

  const getScoreColor = (score: number) => {
    if (score >= 90) return 'text-green-600 dark:text-green-400';
    if (score >= 80) return 'text-blue-600 dark:text-blue-400';
    if (score >= 70) return 'text-yellow-600 dark:text-yellow-400';
    if (score >= 60) return 'text-orange-600 dark:text-orange-400';
    return 'text-red-600 dark:text-red-400';
  };

  const averageScore = reviews.length > 0 
    ? parseFloat((reviews.filter(r => r.overall_score).reduce((sum, r) => sum + (r.overall_score || 0), 0) / reviews.filter(r => r.overall_score).length).toFixed(1))
    : 0;

  if (reviewsLoading || criteriaLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="text-gray-600 dark:text-gray-400 mt-2">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ™ŸÇŸäŸäŸÖÿßÿ™ ÿßŸÑÿ£ÿØÿßÿ°...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            ŸÜÿ∏ÿßŸÖ ÿ™ŸÇŸäŸäŸÖ ÿßŸÑÿ£ÿØÿßÿ°
          </h2>
          <p className="text-gray-600 dark:text-gray-300">
            ŸÖÿ™ÿßÿ®ÿπÿ© Ÿàÿ™ŸÇŸäŸäŸÖ ÿ£ÿØÿßÿ° ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ ÿ®ÿ¥ŸÉŸÑ ÿØŸàÿ±Ÿä ŸàŸÖŸÜŸáÿ¨Ÿä
          </p>
        </div>
        <Button className="bg-green-600 hover:bg-green-700 text-white">
          <Plus className="w-4 h-4 ml-2" />
          ÿ™ŸÇŸäŸäŸÖ ÿ¨ÿØŸäÿØ
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ™ŸÇŸäŸäŸÖÿßÿ™</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">{formatNumber(reviews.length)}</p>
              </div>
              <Target className="w-8 h-8 text-blue-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">ÿßŸÑŸÖÿ™Ÿàÿ≥ÿ∑ ÿßŸÑÿπÿßŸÖ</p>
                <p className={`text-2xl font-bold ${getScoreColor(averageScore)}`}>
                  {formatPercentage(averageScore)}
                </p>
              </div>
              <BarChart3 className="w-8 h-8 text-green-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">ÿßŸÑÿ™ŸÇŸäŸäŸÖÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(reviews.filter(r => r.status === 'completed' || r.status === 'approved').length)}
                </p>
              </div>
              <Award className="w-8 h-8 text-purple-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">ŸÇŸäÿØ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπÿ©</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(reviews.filter(r => r.status === 'in_progress').length)}
                </p>
              </div>
              <TrendingUp className="w-8 h-8 text-orange-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Performance Criteria */}
      <Card>
