  const getStatusText = (status: string) => {
    switch (status) {
      case 'completed': return 'ŸÖŸÉÿ™ŸÖŸÑ';
      case 'in_progress': return 'ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞';
      case 'not_started': return 'ŸÑŸÖ Ÿäÿ®ÿØÿ£';
      case 'dropped': return 'ŸÖÿ™ŸàŸÇŸÅ';
