import { useState, useEffect } from 'react';
import { Button } from '../ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Badge } from '../ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { useSpeechRecognition } from '../../hooks/use-speech-recognition';
import { useSpeechSynthesis } from '../../hooks/use-speech-synthesis';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { 
  Mic, 
  MicOff, 
  Volume2, 
  VolumeX, 
  MessageSquare,
  Settings,
  Languages,
  Loader2,
  CheckCircle,
  AlertCircle,
  Globe
} from 'lucide-react';

interface VoiceCommand {
  command: string;
  confidence: number;
  timestamp: Date;
  response?: string;
}

interface AIResponse {
  message: string;
  action?: string;
  data?: any;
}

type ArabicDialect = 'standard' | 'egyptian' | 'gulf' | 'levantine' | 'maghreb';

export function VoiceAssistant() {
  const [isEnabled, setIsEnabled] = useState(false);
  const [language, setLanguage] = useState<'ar-SA' | 'en-US'>('ar-SA');
  const [selectedDialect, setSelectedDialect] = useState<ArabicDialect>('standard');
  const [commandHistory, setCommandHistory] = useState<VoiceCommand[]>([]);
  const [currentResponse, setCurrentResponse] = useState<string>('');

  const queryClient = useQueryClient();

  const {
    transcript,
    isListening,
    hasRecognitionSupport,
    startListening,
    stopListening,
    resetTranscript,
    confidence
  } = useSpeechRecognition({
    continuous: false,
    interimResults: true,
    language: language,
    dialect: language === 'ar-SA' ? selectedDialect : undefined
  });

  const {
    speak,
    stop: stopSpeaking,
    isSpeaking,
    isSupported: isSpeechSupported,
    getArabicVoices,
    getVoicesByDialect,
    getAvailableDialects
  } = useSpeechSynthesis();

  // AI Assistant mutation
  const aiMutation = useMutation({
    mutationFn: async (command: string) => {
      const response = await fetch('/api/ai/voice-command', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          command,
          language,
          dialect: language === 'ar-SA' ? selectedDialect : undefined,
          context: 'voice_assistant'
        })
      });

      if (!response.ok) {
        throw new Error('ŸÅÿ¥ŸÑ ŸÅŸä ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ŸÖÿ± ÿßŸÑÿµŸàÿ™Ÿä');
      }

      return response.json() as Promise<AIResponse>;
    },
    onSuccess: (data, command) => {
      const newCommand: VoiceCommand = {
        command,
        confidence,
        timestamp: new Date(),
        response: data.message
      };

      setCommandHistory(prev => [newCommand, ...prev.slice(0, 9)]);
      setCurrentResponse(data.message);

      // Speak the response with selected dialect
      if (isEnabled && data.message) {
        speak(data.message, { 
          lang: language,
          dialect: language === 'ar-SA' ? selectedDialect : undefined
        });
      }

      // Execute any actions
      if (data.action) {
        executeVoiceAction(data.action, data.data);
      }
    },
    onError: (error) => {
      const errorMsg = language === 'ar-SA' 
        ? 'ÿπÿ∞ÿ±ÿßŸãÿå ŸÑŸÖ ÿ£ÿ™ŸÖŸÉŸÜ ŸÖŸÜ ŸÅŸáŸÖ ÿßŸÑÿ£ŸÖÿ±' 
        : 'Sorry, I could not understand the command';

      setCurrentResponse(errorMsg);
      if (isEnabled) {
        speak(errorMsg, { 
          lang: language,
          dialect: language === 'ar-SA' ? selectedDialect : undefined
        });
      }
    }
  });

  // Process voice command when transcript is final
  useEffect(() => {
    if (transcript && !isListening && transcript.trim().length > 2 && isEnabled) {
      aiMutation.mutate(transcript);
      resetTranscript();
    }
  }, [transcript, isListening, isEnabled]);

  const executeVoiceAction = (action: string, data: any) => {
    switch (action) {
      case 'navigate':
        if (data?.route) {
          window.location.href = data.route;
        }
        break;
      case 'refresh_data':
        if (data?.queryKey) {
          queryClient.invalidateQueries({ queryKey: [data.queryKey] });
        }
        break;
      case 'show_stats':
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        break;
      default:
        console.log('Unknown voice action:', action);
    }
  };

  const toggleVoiceAssistant = () => {
    if (isEnabled) {
      stopListening();
      stopSpeaking();
      setIsEnabled(false);
    } else {
      setIsEnabled(true);
    }
  };

  const handleVoiceInput = () => {
    if (isListening) {
      stopListening();
    } else {
      startListening();
    }
  };

  const toggleLanguage = () => {
    const newLang = language === 'ar-SA' ? 'en-US' : 'ar-SA';
    setLanguage(newLang);

    const message = newLang === 'ar-SA' 
      ? 'ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿ∫ÿ© ÿ•ŸÑŸâ ÿßŸÑÿπÿ±ÿ®Ÿäÿ©' 
      : 'Language changed to English';

    speak(message, { 
      lang: newLang,
      dialect: newLang === 'ar-SA' ? selectedDialect : undefined
    });
  };

  const handleDialectChange = (newDialect: ArabicDialect) => {
    setSelectedDialect(newDialect);

    const dialectNames: Record<ArabicDialect, string> = {
      'standard': 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑŸÅÿµÿ≠Ÿâ',
      'egyptian': 'ÿßŸÑŸÑŸáÿ¨ÿ© ÿßŸÑŸÖÿµÿ±Ÿäÿ©',
      'gulf': 'ÿßŸÑŸÑŸáÿ¨ÿ© ÿßŸÑÿÆŸÑŸäÿ¨Ÿäÿ©',
      'levantine': 'ÿßŸÑŸÑŸáÿ¨ÿ© ÿßŸÑÿ¥ÿßŸÖŸäÿ©',
      'maghreb': 'ÿßŸÑŸÑŸáÿ¨ÿ© ÿßŸÑŸÖÿ∫ÿßÿ±ÿ®Ÿäÿ©'
    };

    const message = `ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑŸáÿ¨ÿ© ÿ•ŸÑŸâ ${dialectNames[newDialect]}`;
    speak(message, { dialect: newDialect });
  };

  if (!hasRecognitionSupport || !isSpeechSupported) {
    return (
      <Card className="w-full max-w-md">
        <CardContent className="pt-6">
          <div className="text-center text-muted-foreground">
            <AlertCircle className="mx-auto h-8 w-8 mb-2" />
            <p className="text-sm">
              {language === 'ar-SA' 
                ? 'ÿßŸÑŸÖÿ™ÿµŸÅÿ≠ ŸÑÿß ŸäÿØÿπŸÖ ÿßŸÑÿ£ŸàÿßŸÖÿ± ÿßŸÑÿµŸàÿ™Ÿäÿ©'
                : 'Voice commands not supported in this browser'
              }
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Main Voice Control */}
      <Card className="w-full">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg flex items-center gap-2">
              <MessageSquare className="h-5 w-5" />
              {language === 'ar-SA' ? 'ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿµŸàÿ™Ÿä' : 'Voice Assistant'}
            </CardTitle>

            <div className="flex items-center gap-2">
              {/* Dialect Selector for Arabic */}
              {language === 'ar-SA' && (
                <Select value={selectedDialect} onValueChange={(value: ArabicDialect) => handleDialectChange(value)}>
                  <SelectTrigger className="w-[140px] h-8">
                    <Globe className="h-4 w-4 mr-1" />
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="standard">ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑŸÅÿµÿ≠Ÿâ</SelectItem>
                    <SelectItem value="egyptian">ÿßŸÑŸÖÿµÿ±Ÿäÿ©</SelectItem>
                    <SelectItem value="gulf">ÿßŸÑÿÆŸÑŸäÿ¨Ÿäÿ©</SelectItem>
                    <SelectItem value="levantine">ÿßŸÑÿ¥ÿßŸÖŸäÿ©</SelectItem>
                    <SelectItem value="maghreb">ÿßŸÑŸÖÿ∫ÿßÿ±ÿ®Ÿäÿ©</SelectItem>
                  </SelectContent>
                </Select>
              )}

              <Button
                variant="outline"
                size="sm"
                onClick={toggleLanguage}
                className="gap-2"
              >
                <Languages className="h-4 w-4" />
                {language === 'ar-SA' ? 'ÿπÿ±ÿ®Ÿä' : 'EN'}
              </Button>

              <Button
                variant={isEnabled ? "default" : "outline"}
                size="sm"
                onClick={toggleVoiceAssistant}
                className="gap-2"
              >
                <Settings className="h-4 w-4" />
                {isEnabled 
                  ? (language === 'ar-SA' ? 'ŸÖŸÅÿπŸÑ' : 'ON') 
                  : (language === 'ar-SA' ? 'ÿ∫Ÿäÿ± ŸÖŸÅÿπŸÑ' : 'OFF')
                }
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Voice Input Control */}
          <div className="flex items-center gap-3">
            <Button
              variant={isListening ? "destructive" : "default"}
              size="lg"
              onClick={handleVoiceInput}
              disabled={!isEnabled || aiMutation.isPending}
              className="gap-2"
            >
              {isListening ? (
                <>
                  <MicOff className="h-5 w-5" />
                  {language === 'ar-SA' ? 'ÿ•ŸäŸÇÿßŸÅ' : 'Stop'}
                </>
              ) : (
                <>
                  <Mic className="h-5 w-5" />
                  {language === 'ar-SA' ? 'ÿ™ÿ≠ÿØÿ´' : 'Speak'}
                </>
              )}
            </Button>

            {isSpeaking && (
              <Button
                variant="outline"
                onClick={stopSpeaking}
                className="gap-2"
              >
                <VolumeX className="h-4 w-4" />
                {language === 'ar-SA' ? 'ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿµŸàÿ™' : 'Stop Audio'}
              </Button>
            )}

            {aiMutation.isPending && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Loader2 className="h-4 w-4 animate-spin" />
                {language === 'ar-SA' ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©...' : 'Processing...'}
              </div>
            )}
          </div>

          {/* Live Transcript */}
          {(transcript || isListening) && (
            <div className="p-3 bg-muted rounded-lg">
              <div className="flex items-center gap-2 mb-2">
                <Mic className="h-4 w-4 text-blue-500" />
                <span className="text-sm font-medium">
                  {language === 'ar-SA' ? 'ÿßŸÑŸÜÿµ ÿßŸÑŸÖÿ≥ŸÖŸàÿπ:' : 'Transcript:'}
                </span>
                {confidence > 0 && (
                  <Badge variant="secondary" className="text-xs">
                    {Math.round(confidence * 100)}%
                  </Badge>
                )}
              </div>
              <p className="text-sm">
                {transcript || (language === 'ar-SA' ? 'ÿßÿ≥ÿ™ŸÖÿπ...' : 'Listening...')}
              </p>
            </div>
          )}

          {/* Current Response */}
          {currentResponse && (
            <div className="p-3 bg-blue-50 dark:bg-blue-950 rounded-lg">
              <div className="flex items-center gap-2 mb-2">
                <Volume2 className="h-4 w-4 text-blue-500" />
                <span className="text-sm font-medium text-blue-700 dark:text-blue-300">
                  {language === 'ar-SA' ? 'ÿ±ÿØ ÿßŸÑŸÖÿ≥ÿßÿπÿØ:' : 'Assistant Response:'}
                </span>
              </div>
              <p className="text-sm text-blue-800 dark:text-blue-200">
                {currentResponse}
              </p>
            </div>
          )}

          {/* Voice Commands Help with Dialect Examples */}
          <div className="text-xs text-muted-foreground">
            <p className="font-medium mb-1">
              {language === 'ar-SA' ? 'ÿ£ŸÖÿ´ŸÑÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸàÿßŸÖÿ± ÿßŸÑÿµŸàÿ™Ÿäÿ©:' : 'Voice command examples:'}
            </p>
            <ul className="space-y-1">
              {language === 'ar-SA' ? (
