import { Request, Response, NextFunction } from 'express';
import { z, ZodSchema, ZodError } from 'zod';

export interface ValidationError {
  field: string;
  message: string;
  code: string;
}

export class RequestValidationError extends Error {
  public validationErrors: ValidationError[];
  public statusCode: number;

  constructor(errors: ValidationError[]) {
    super('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ∑ŸÑÿ® ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©');
    this.name = 'RequestValidationError';
    this.validationErrors = errors;
    this.statusCode = 400;
  }
}

function formatZodErrors(zodError: ZodError): ValidationError[] {
  return zodError.errors.map(err => ({
    field: err.path.join('.'),
    message: getArabicErrorMessage(err.code, err.message, err.path.join('.')),
    code: err.code
  }));
}

function getArabicErrorMessage(code: string, originalMessage: string, field: string): string {
  const fieldNames: Record<string, string> = {
    'username': 'ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ',
    'password': 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
    'email': 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä',
    'phone': 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ',
    'phone_number': 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ',
    'customer_id': 'ŸÖÿπÿ±ŸÅ ÿßŸÑÿπŸÖŸäŸÑ',
    'order_number': 'ÿ±ŸÇŸÖ ÿßŸÑÿ∑ŸÑÿ®',
    'quantity': 'ÿßŸÑŸÉŸÖŸäÿ©',
    'quantity_kg': 'ÿßŸÑŸÉŸÖŸäÿ© ÿ®ÿßŸÑŸÉŸäŸÑŸàÿ¨ÿ±ÿßŸÖ',
    'status': 'ÿßŸÑÿ≠ÿßŸÑÿ©',
    'delivery_date': 'ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ™ÿ≥ŸÑŸäŸÖ',
    'name': 'ÿßŸÑÿßÿ≥ŸÖ',
    'title': 'ÿßŸÑÿπŸÜŸàÿßŸÜ',
    'message': 'ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©',
    'id': 'ÿßŸÑŸÖÿπÿ±ŸÅ'
  };

  const arabicField = fieldNames[field] || field;

  switch (code) {
    case 'invalid_type':
      return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ŸÖŸÜ ÿßŸÑŸÜŸàÿπ ÿßŸÑÿµÿ≠Ÿäÿ≠`;
    case 'too_small':
      if (originalMessage.includes('String must contain at least')) {
        const minLength = originalMessage.match(/\d+/)?.[0] || '1';
        return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ${minLength} ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ`;
      }
      if (originalMessage.includes('Number must be greater than or equal to')) {
        const minValue = originalMessage.match(/\d+/)?.[0] || '0';
        return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ${minValue} ÿ£Ÿà ÿ£ŸÉÿ´ÿ±`;
      }
      return `${arabicField} ÿµÿ∫Ÿäÿ± ÿ¨ÿØÿßŸã`;
    case 'too_big':
      if (originalMessage.includes('String must contain at most')) {
        const maxLength = originalMessage.match(/\d+/)?.[0] || '100';
        return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸÑÿß Ÿäÿ™ÿ¨ÿßŸàÿ≤ ${maxLength} ÿ≠ÿ±ŸÅ`;
      }
      if (originalMessage.includes('Number must be less than or equal to')) {
        const maxValue = originalMessage.match(/\d+/)?.[0] || '100';
        return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ${maxValue} ÿ£Ÿà ÿ£ŸÇŸÑ`;
      }
      return `${arabicField} ŸÉÿ®Ÿäÿ± ÿ¨ÿØÿßŸã`;
    case 'invalid_string':
      if (originalMessage.includes('Invalid email')) {
        return `${arabicField} ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠`;
      }
      if (originalMessage.includes('Invalid url')) {
        return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ±ÿßÿ®ÿ∑ ÿµÿ≠Ÿäÿ≠`;
      }
      return `${arabicField} ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠`;
    case 'invalid_date':
      return `${arabicField} Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ™ÿßÿ±ŸäÿÆ ÿµÿ≠Ÿäÿ≠`;
    case 'invalid_enum_value':
      return `${arabicField} Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ŸÇŸäŸÖÿ© ÿ∫Ÿäÿ± ŸÖÿ≥ŸÖŸàÿ≠ÿ©`;
    case 'unrecognized_keys':
      return `Ÿäÿ≠ÿ™ŸàŸä ÿßŸÑÿ∑ŸÑÿ® ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿ∫Ÿäÿ± ŸÖÿπÿ™ÿ±ŸÅ ÿ®Ÿáÿß`;
    default:
      return `${arabicField} ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠`;
  }
}

// Validation middleware factory
export function validateRequest<T extends Record<string, ZodSchema>>(
  schemas: T
) {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const errors: ValidationError[] = [];

      // Validate request body
      if (schemas.body) {
        try {
          console.log('üîç Validating request body:', JSON.stringify(req.body, null, 2));
          req.body = schemas.body.parse(req.body);
          console.log('‚úÖ Validation successful');
        } catch (error) {
          console.log('‚ùå Validation failed:', error);
          if (error instanceof ZodError) {
            console.log('‚ùå Zod validation errors:', JSON.stringify(error.errors, null, 2));
            errors.push(...formatZodErrors(error));
          } else {
            errors.push({
              field: 'body',
              message: 'ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ∑ŸÑÿ® ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
              code: 'invalid_body'
            });
          }
        }
      }

      // Validate query parameters
      if (schemas.query) {
        try {
          req.query = schemas.query.parse(req.query);
        } catch (error) {
          if (error instanceof ZodError) {
            errors.push(...formatZodErrors(error));
          } else {
            errors.push({
              field: 'query',
              message: 'ŸÖÿπÿßŸÖŸÑÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿπŸÑÿßŸÖ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
              code: 'invalid_query'
            });
          }
        }
      }

      // Validate route parameters
      if (schemas.params) {
        try {
          req.params = schemas.params.parse(req.params);
        } catch (error) {
          if (error instanceof ZodError) {
            errors.push(...formatZodErrors(error));
          } else {
            errors.push({
              field: 'params',
              message: 'ŸÖÿπÿßŸÖŸÑÿßÿ™ ÿßŸÑŸÖÿ≥ÿßÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
              code: 'invalid_params'
            });
          }
        }
      }

      // If there are validation errors, return them
      if (errors.length > 0) {
        return res.status(400).json({
          message: 'ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ∑ŸÑÿ® ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
          errors: errors,
          success: false
        });
      }

      next();
    } catch (error) {
      console.error('Validation middleware error:', error);
      return res.status(500).json({
        message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
        success: false
      });
    }
  };
}

// Phone number validation schema (defined separately to avoid circular dependency)
const phoneNumberSchema = z.string()
  .min(10, 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ 10 ÿ£ÿ±ŸÇÿßŸÖ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ')
  .max(15, 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ Ÿäÿ¨ÿ® ÿ£ŸÜ ŸÑÿß Ÿäÿ™ÿ¨ÿßŸàÿ≤ 15 ÿ±ŸÇŸÖ')
  .regex(/^[\+]?[0-9\-\(\)\s]+$/, 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');

// Common validation schemas
export const commonSchemas = {
  // ID parameter validation
  idParam: z.object({
    id: z.string().regex(/^\d+$/, 'ÿßŸÑŸÖÿπÿ±ŸÅ Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ±ŸÇŸÖ ÿµÿ≠Ÿäÿ≠').transform(Number)
  }),

  // Pagination query validation
  pagination: z.object({
    limit: z.string().regex(/^\d+$/).transform(Number).optional(),
    offset: z.string().regex(/^\d+$/).transform(Number).optional(),
    page: z.string().regex(/^\d+$/).transform(Number).optional()
  }),

  // Phone number validation
  phoneNumber: phoneNumberSchema,

  // User authentication
  loginCredentials: z.object({
    username: z.string()
      .min(3, 'ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ 3 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ')
      .max(50, 'ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ Ÿäÿ¨ÿ® ÿ£ŸÜ ŸÑÿß Ÿäÿ™ÿ¨ÿßŸàÿ≤ 50 ÿ≠ÿ±ŸÅ')
      .trim(),
    password: z.string()
      .min(6, 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ 6 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ')
      .max(100, 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∑ŸàŸäŸÑÿ© ÿ¨ÿØÿßŸã')
  }),

  // Order creation validation
  createOrder: z.object({
