}

// Admin role validation middleware
export async function requireAdmin(req: Request, res: Response, next: NextFunction) {
  // First check if user is authenticated
  if (!req.session?.userId) {
    return res.status(401).json({
      message: 'ÿ∫Ÿäÿ± ŸÖÿ≥ÿ¨ŸÑ ÿßŸÑÿØÿÆŸàŸÑ - Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã',
      success: false
    });
  }

  try {
    // Import db and related schemas here to avoid circular dependencies
    const { db } = await import('../db');
    const { users, roles } = await import('@shared/schema');
    const { eq } = await import('drizzle-orm');

    // Get user with role information
    const userWithRole = await db
      .select({
        user_id: users.id,
        user_role_id: users.role_id,
        role_name: roles.name,
        role_permissions: roles.permissions
      })
      .from(users)
      .leftJoin(roles, eq(users.role_id, roles.id))
      .where(eq(users.id, req.session.userId))
      .limit(1);

    if (userWithRole.length === 0) {
      return res.status(403).json({
        message: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ',
        success: false
      });
    }

    const user = userWithRole[0];

    // Check if user has admin role or admin permissions
    const isAdmin = user.role_name === 'admin' || 
                   user.role_name === 'ŸÖÿØŸäÿ± ÿßŸÑŸÜÿ∏ÿßŸÖ' ||
                   user.role_name === 'administrator' ||
                   user.user_role_id === 1 || // Assume role ID 1 is admin
                   (user.role_permissions && Array.isArray(user.role_permissions) && 
                    user.role_permissions.includes('admin'));

    if (!isAdmin) {
      return res.status(403).json({
        message: 'ÿ∫Ÿäÿ± ŸÖÿÆŸàŸÑ ŸÑŸÑŸàÿµŸàŸÑ - ÿµŸÑÿßÿ≠Ÿäÿßÿ™ ÿßŸÑŸÖÿØŸäÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©',
        success: false
      });
    }

    // User is authenticated and has admin privileges
    next();
  } catch (error) {
    console.error('Admin role validation error:', error);
    return res.status(500).json({
      message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™',
      success: false
    });
  }
}

