  className?: string;
  innerRadius?: number;
  outerRadius?: number;
}

const COLORS = [
  '#3b82f6', // blue
  '#10b981', // emerald
  '#f59e0b', // amber
  '#ef4444', // red
  '#8b5cf6', // violet
  '#06b6d4', // cyan
  '#84cc16', // lime
  '#f97316', // orange
  '#ec4899', // pink
  '#6b7280'  // gray
];

const CustomTooltip = ({ active, payload, formatValue }: any) => {
  if (active && payload && payload.length) {
    const data = payload[0];
    return (
      <div className="bg-white p-3 border border-gray-300 rounded-lg shadow-lg" dir="rtl">
        <p className="font-medium text-gray-900">{data.name}</p>
        <p className="text-sm" style={{ color: data.fill }}>
          {`ÿßŸÑŸÇŸäŸÖÿ©: ${formatValue ? formatValue(data.value) : data.value}`}
        </p>
        {data.payload.percentage && (
          <p className="text-sm text-gray-600">
            {`ÿßŸÑŸÜÿ≥ÿ®ÿ©: ${data.payload.percentage.toFixed(1)}%`}
          </p>
        )}
      </div>
    );
  }
  return null;
};

const renderCustomLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent, name }: any) => {
  const RADIAN = Math.PI / 180;
  const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  if (percent < 0.05) return null; // Don't show label for slices less than 5%

  return (
    <text 
      x={x} 
      y={y} 
      fill="white" 
      textAnchor={x > cx ? 'start' : 'end'} 
      dominantBaseline="central"
      fontSize={12}
      fontWeight="bold"
    >
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};

export function InteractivePieChart({
  data,
  title,
  description,
  nameKey,
  valueKey,
  colors = COLORS,
  height = 300,
  showLegend = true,
  showLabels = true,
  formatValue,
  className = "",
  innerRadius = 0,
  outerRadius = 80
}: InteractivePieChartProps) {
  // Calculate percentages
  const total = data.reduce((sum, item) => sum + (item[valueKey] || 0), 0);
  const dataWithPercentages = data.map(item => ({
    ...item,
    percentage: total > 0 ? (item[valueKey] / total) * 100 : 0
  }));

  return (
    <Card className={`${className}`} data-testid="chart-interactive-pie">
      <CardHeader>
        <CardTitle className="text-lg font-semibold text-gray-900" data-testid="text-chart-title">
          {title}
        </CardTitle>
        {description && (
          <p className="text-sm text-gray-600" data-testid="text-chart-description">
            {description}
          </p>
        )}
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={height}>
          <PieChart>
            <Pie
              data={dataWithPercentages}
              cx="50%"
              cy="50%"
              labelLine={false}
              label={showLabels ? renderCustomLabel : false}
              outerRadius={outerRadius}
              innerRadius={innerRadius}
              fill="#8884d8"
              dataKey={valueKey}
              nameKey={nameKey}
            >
              {dataWithPercentages.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
              ))}
            </Pie>
            <Tooltip 
              content={<CustomTooltip formatValue={formatValue} />}
            />
            {showLegend && (
              <Legend 
                verticalAlign="bottom" 
                height={36}
                iconType="circle"
                formatter={(value) => <span className="text-sm text-gray-700">{value}</span>}
              />
            )}
          </PieChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
}

client/src/components/charts/MetricsCard.content:
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { TrendingUp, TrendingDown, Minus } from "lucide-react";

interface MetricCardProps {
  title: string;
  value: string | number;
  description?: string;
  icon?: React.ReactNode;
  trend?: {
    value: number;
    isPositive: boolean;
    label: string;
  };
  className?: string;
  valueClassName?: string;
}

export function MetricCard({
  title,
  value,
  description,
  icon,
  trend,
  className = "",
  valueClassName = ""
}: MetricCardProps) {
  const getTrendIcon = () => {
    if (!trend) return null;

    if (trend.value === 0) {
      return <Minus className="w-4 h-4 text-gray-500" />;
    }

    return trend.isPositive ? 
      <TrendingUp className="w-4 h-4 text-green-500" /> : 
      <TrendingDown className="w-4 h-4 text-red-500" />;
  };

  const getTrendColor = () => {
    if (!trend) return "";
    if (trend.value === 0) return "text-gray-500";
    return trend.isPositive ? "text-green-600" : "text-red-600";
  };

  return (
    <Card className={`${className}`} data-testid="card-metric">
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div className="flex-1">
            <div className="flex items-center justify-between">
              <p className="text-sm font-medium text-gray-600" data-testid="text-metric-title">
                {title}
              </p>
              {icon && <div className="text-gray-400">{icon}</div>}
            </div>

            <div className="mt-2">
              <p className={`text-2xl font-bold text-gray-900 ${valueClassName}`} data-testid="text-metric-value">
                {value}
              </p>

              {description && (
                <p className="text-xs text-gray-500 mt-1" data-testid="text-metric-description">
                  {description}
                </p>
              )}

              {trend && (
                <div className="flex items-center gap-1 mt-2">
                  {getTrendIcon()}
                  <span className={`text-xs font-medium ${getTrendColor()}`} data-testid="text-metric-trend">
                    {trend.value !== 0 && `${Math.abs(trend.value)}%`} {trend.label}
                  </span>
                </div>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

interface MetricsGridProps {
  metrics: MetricCardProps[];
  columns?: number;
  className?: string;
}

export function MetricsGrid({
  metrics,
  columns = 4,
  className = ""
}: MetricsGridProps) {
  const gridCols = {
    1: "grid-cols-1",
    2: "grid-cols-1 md:grid-cols-2",
    3: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3",
    4: "grid-cols-1 md:grid-cols-2 lg:grid-cols-4",
    5: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5",
    6: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6"
  };

  return (
    <div className={`grid gap-4 ${gridCols[columns as keyof typeof gridCols]} ${className}`} data-testid="grid-metrics">
      {metrics.map((metric, index) => (
        <MetricCard
          key={index}
          {...metric}
        />
      ))}
    </div>
  );
}

