import { EventEmitter } from 'events';
import type { IStorage } from '../storage';
import { db, pool } from '../db';
import { eq, desc, and, sql, count, gte, lte } from 'drizzle-orm';
import type { 
  SystemAlert, 
  InsertSystemAlert, 
  SystemHealthCheck, 
  InsertSystemHealthCheck,
  SystemPerformanceMetric,
  InsertSystemPerformanceMetric,
  AlertRule,
  InsertAlertRule
} from '@shared/schema';
import { getNotificationManager } from './notification-manager';

// ÿ£ŸÜŸàÿßÿπ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ŸàÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
export interface HealthCheckResult {
  checkName: string;
  checkName_ar: string;
  status: 'healthy' | 'warning' | 'critical' | 'unknown';
  duration: number;
  details: Record<string, any>;
  error?: string;
}

export interface AlertCondition {
  metric: string;
  operator: '>' | '<' | '>=' | '<=' | '=' | '!=';
  value: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

export interface SmartAlert {
  id?: number;
  title: string;
  title_ar: string;
  message: string;
  message_ar: string;
  type: 'system' | 'production' | 'quality' | 'inventory' | 'maintenance' | 'security';
  category: 'warning' | 'error' | 'critical' | 'info' | 'success';
  severity: 'low' | 'medium' | 'high' | 'critical';
  source: string;
  source_id?: string;
  context_data?: Record<string, any>;
  suggested_actions?: {action: string; priority: number; description?: string}[];
  target_users?: number[];
  target_roles?: number[];
  requires_action: boolean;
}

/**
 * ŸÜÿ∏ÿßŸÖ ŸÖÿ±ÿßŸÇÿ®ÿ© ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ŸàÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿßŸÑÿ∞ŸÉŸäÿ©
 */
export class SystemHealthMonitor extends EventEmitter {
  private storage: IStorage;
  private monitoringInterval: NodeJS.Timeout | null = null;
  private healthCheckInterval: NodeJS.Timeout | null = null;
  private alertRules: AlertRule[] = [];
  private lastHealthStatus: Map<string, HealthCheckResult> = new Map();
  private consecutiveStatusCounts: Map<string, {status: string, count: number}> = new Map(); // Track consecutive status counts for hysteresis
  private lastAlertTimes: Map<string, Date> = new Map(); // Track last alert times for rate limiting
  private static instance: SystemHealthMonitor | null = null; // Singleton pattern

  // ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© - Increased intervals to reduce alert frequency
  private readonly MONITORING_INTERVAL = 15 * 60 * 1000; // ÿ™ŸÖ ÿ≤ŸäÿßÿØÿ™Ÿáÿß ÿ•ŸÑŸâ 15 ÿØŸÇŸäŸÇÿ© ŸÑÿ™ŸÇŸÑŸäŸÑ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
  private readonly HEALTH_CHECK_INTERVAL = 10 * 60 * 1000; // ÿ™ŸÖ ÿ≤ŸäÿßÿØÿ™Ÿáÿß ÿ•ŸÑŸâ 10 ÿØŸÇÿßÿ¶ŸÇ ŸÑÿ™ŸÇŸÑŸäŸÑ ÿßŸÑÿ≠ÿ≥ÿßÿ≥Ÿäÿ©
  private readonly PERFORMANCE_RETENTION_DAYS = 30; // ÿßŸÑÿßÿ≠ÿ™ŸÅÿßÿ∏ ÿ®ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÖÿØÿ© 30 ŸäŸàŸÖ

  // ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ - Significantly increased cooldowns to reduce spam
  private readonly ALERT_COOLDOWN_MEMORY = 60 * 60 * 1000; // ÿ≤ŸäÿßÿØÿ© ÿ•ŸÑŸâ ÿ≥ÿßÿπÿ© ŸÉÿßŸÖŸÑÿ© ŸÑŸÑÿ∞ÿßŸÉÿ±ÿ©
  private readonly ALERT_COOLDOWN_DATABASE = 2 * 60 * 60 * 1000; // ÿ≤ŸäÿßÿØÿ© ÿ•ŸÑŸâ ÿ≥ÿßÿπÿ™ŸäŸÜ ŸÑŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  private readonly ALERT_COOLDOWN_DEFAULT = 90 * 60 * 1000; // ÿ≤ŸäÿßÿØÿ© ÿ•ŸÑŸâ 90 ÿØŸÇŸäŸÇÿ© ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä

  // Sustained condition settings for hysteresis - Increased to reduce noise
  private readonly SUSTAINED_CONDITION_COUNT = 5; // 5 consecutive checks before changing status

  constructor(storage: IStorage) {
    super();

    // Enforce singleton pattern to prevent duplicate intervals on hot reloads
    if (SystemHealthMonitor.instance) {
      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ŸÖÿ´ŸäŸÑ ŸÖŸàÿ¨ŸàÿØÿå ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÖÿ´ŸäŸÑ ÿßŸÑŸÇÿØŸäŸÖ');
      SystemHealthMonitor.instance.stopMonitoring();
    }

    this.storage = storage;
    SystemHealthMonitor.instance = this;

    console.log('[SystemHealthMonitor] ŸÜÿ∏ÿßŸÖ ŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿ≥ŸÑÿßŸÖÿ© ŸÖŸèŸÅÿπŸÑ');
    this.initialize();
  }

  /**
   * Get singleton instance
   */
  public static getInstance(storage?: IStorage): SystemHealthMonitor | null {
    if (storage && !SystemHealthMonitor.instance) {
      new SystemHealthMonitor(storage);
    }
    return SystemHealthMonitor.instance;
  }

  /**
   * ÿ™ÿ¥ÿ∫ŸäŸÑ ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©
   */
  private async initialize(): Promise<void> {
    try {
      // ÿ™ÿ≠ŸÖŸäŸÑ ŸÇŸàÿßÿπÿØ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
      await this.loadAlertRules();

      // Hydrate alert times from persistent storage
      await this.hydrateLastAlertTimes();

      // ÿ•ŸÜÿ¥ÿßÿ° ŸÅÿ≠Ÿàÿµÿßÿ™ ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
      await this.createDefaultHealthChecks();

      // ÿ®ÿØÿ° ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿØŸàÿ±Ÿäÿ©
      this.startMonitoring();

      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ™ÿ¥ÿ∫ŸäŸÑ ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ÿ®ŸÜÿ¨ÿßÿ≠ ‚úÖ');
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ¥ÿ∫ŸäŸÑ ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©:', error);
    }
  }

  /**
   * ÿ®ÿØÿ° ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿØŸàÿ±Ÿäÿ©
   */
  private startMonitoring(): void {
    // ŸÅÿ≠Ÿàÿµÿßÿ™ ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ
    this.healthCheckInterval = setInterval(async () => {
      await this.performHealthChecks();
    }, this.HEALTH_CHECK_INTERVAL);

    // ŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿ£ÿØÿßÿ° ŸàÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
    this.monitoringInterval = setInterval(async () => {
      await this.performMonitoring();
    }, this.MONITORING_INTERVAL);

    console.log('[SystemHealthMonitor] ÿ®ÿØÿ£ÿ™ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿØŸàÿ±Ÿäÿ©');
  }

  /**
   * ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©
   */
  public stopMonitoring(): void {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = null;
    }

    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }

    // Clear singleton instance
    SystemHealthMonitor.instance = null;

    console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©');
  }

  /**
   * ÿ™ÿ≠ŸÖŸäŸÑ ŸÇŸàÿßÿπÿØ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
   */
  private async loadAlertRules(): Promise<void> {
    try {
      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© Ÿáÿ∞Ÿá ÿßŸÑÿπŸÖŸÑŸäÿ© ŸÅŸä storage.ts ŸÑÿßÿ≠ŸÇÿßŸã
      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ŸÇŸàÿßÿπÿØ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™');
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ŸÇŸàÿßÿπÿØ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™:', error);
    }
  }

  /**
   * Hydrate lastAlertTimes Map from persistent storage
   */
  private async hydrateLastAlertTimes(): Promise<void> {
    try {
      // Define the check names we need to hydrate
      const checkNames = ['Database Connection', 'Database Performance', 'Memory Usage', 'System Health API'];

      for (const checkName of checkNames) {
        const normalizedKey = this.normalizeAlertKey('', checkName);
        const lastAlertTime = await this.storage.getLastAlertTime(normalizedKey);

        if (lastAlertTime) {
          this.lastAlertTimes.set(normalizedKey, lastAlertTime);
          console.log(`[SystemHealthMonitor] ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ŸàŸÇÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ± ÿßŸÑÿ£ÿÆŸäÿ± ŸÑŸÑŸÅÿ≠ÿµ: ${checkName} ŸÅŸä ${lastAlertTime.toISOString()}`);
        }
      }

      console.log(`[SystemHealthMonitor] ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${this.lastAlertTimes.size} ŸÖŸÜ ÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ± ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™`);
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ± ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      // Continue initialization even if hydration fails
    }
  }

  /**
   * ÿ•ŸÜÿ¥ÿßÿ° ŸÅÿ≠Ÿàÿµÿßÿ™ ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
   */
  private async createDefaultHealthChecks(): Promise<void> {
    try {
      const defaultChecks: InsertSystemHealthCheck[] = [
        {
          check_name: 'Database Connection',
          check_name_ar: 'ÿßÿ™ÿµÿßŸÑ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
          check_type: 'database',
          thresholds: { warning: 1000, critical: 5000, unit: 'ms' },
          is_critical: true
        },
        {
          check_name: 'Database Performance',
          check_name_ar: 'ÿ£ÿØÿßÿ° ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
          check_type: 'database',
          thresholds: { warning: 500, critical: 2000, unit: 'ms' },
          is_critical: false
        },
        {
          check_name: 'Memory Usage',
          check_name_ar: 'ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ∞ÿßŸÉÿ±ÿ©',
          check_type: 'memory',
          thresholds: { warning: 85, critical: 95, unit: 'percent' },
          is_critical: false
        },
        {
          check_name: 'System Health API',
          check_name_ar: 'API ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ',
          check_type: 'api',
          thresholds: { warning: 1000, critical: 3000, unit: 'ms' },
          is_critical: false
        }
      ];

      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© Ÿáÿ∞Ÿá ÿßŸÑÿπŸÖŸÑŸäÿ© ŸÅŸä storage.ts ŸÑÿßÿ≠ŸÇÿßŸã
      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ŸÅÿ≠Ÿàÿµÿßÿ™ ÿßŸÑÿ≥ŸÑÿßŸÖÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©');
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ŸÅÿ≠Ÿàÿµÿßÿ™ ÿßŸÑÿ≥ŸÑÿßŸÖÿ©:', error);
    }
  }

  /**
   * ÿ™ŸÜŸÅŸäÿ∞ ŸÅÿ≠Ÿàÿµÿßÿ™ ÿ≥ŸÑÿßŸÖÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ
   */
  private async performHealthChecks(): Promise<void> {
    try {
      const checks = [
        this.checkDatabaseConnection(),
        this.checkDatabasePerformance(),
        this.checkSystemMemory(),
        this.checkSystemHealth()
      ];

      const results = await Promise.allSettled(checks);

      for (const result of results) {
        if (result.status === 'fulfilled') {
          await this.processHealthCheckResult(result.value);
        } else {
          console.error('[SystemHealthMonitor] ŸÅÿ¥ŸÑ ŸÅŸä ŸÅÿ≠ÿµ ÿßŸÑÿ≥ŸÑÿßŸÖÿ©:', result.reason);
        }
      }
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ŸÜŸÅŸäÿ∞ ŸÅÿ≠Ÿàÿµÿßÿ™ ÿßŸÑÿ≥ŸÑÿßŸÖÿ©:', error);
    }
  }

  /**
   * ŸÅÿ≠ÿµ ÿßÿ™ÿµÿßŸÑ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
   */
  private async checkDatabaseConnection(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      await db.execute(sql`SELECT 1 as test`);
      const duration = Date.now() - startTime;

      return {
        checkName: 'Database Connection',
        checkName_ar: 'ÿßÿ™ÿµÿßŸÑ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
        status: duration > 10000 ? 'critical' : duration > 3000 ? 'warning' : 'healthy', // Increased thresholds to reduce false positives
        duration,
        details: { responseTime: duration, connected: true }
      };
    } catch (error: any) {
      return {
        checkName: 'Database Connection',
        checkName_ar: 'ÿßÿ™ÿµÿßŸÑ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
        status: 'critical',
        duration: Date.now() - startTime,
        details: { connected: false },
        error: error.message
      };
    }
  }

  /**
   * ŸÅÿ≠ÿµ ÿ£ÿØÿßÿ° ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
   */
  private async checkDatabasePerformance(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      // ŸÅÿ≠ÿµ ÿπÿØÿØ ÿßŸÑÿßÿ™ÿµÿßŸÑÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      const activeConnections = await db.execute(sql`
        SELECT count(*) as active_connections 
        FROM pg_stat_activity 
        WHERE state = 'active'
      `);

      // ŸÅÿ≠ÿµ ÿ≠ÿ¨ŸÖ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const dbSize = await db.execute(sql`
        SELECT pg_size_pretty(pg_database_size(current_database())) as db_size
      `);

      const duration = Date.now() - startTime;
      const connectionCount = (activeConnections as unknown as any[])[0]?.active_connections || 0;

      return {
        checkName: 'Database Performance',
        checkName_ar: 'ÿ£ÿØÿßÿ° ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
