            recipient_type: 'user',
            recipient_id: userId.toString(),
            context_type: alert.type,
            context_id: alert.source_id,
            sound: alert.severity === 'critical',
            icon: this.getAlertIcon(alert.type)
          });
        }
      }
    } catch (error) {
