   */
  private mapAlertTypeToNotificationType(alertType: string): 'system' | 'order' | 'production' | 'maintenance' | 'quality' | 'hr' {
    const typeMapping: Record<string, 'system' | 'order' | 'production' | 'maintenance' | 'quality' | 'hr'> = {
