      maintenance: 'üîß',
      security: 'üîí'
    };
    return icons[type as keyof typeof icons] || 'üö®';
  }

  /**
   * ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ© Ÿàÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
   */
  private async cleanupOldData(): Promise<void> {
    try {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - this.PERFORMANCE_RETENTION_DAYS);

      // Clean up old alert states (older than 24 hours)
      const alertCutoffTime = Date.now() - (24 * 60 * 60 * 1000); // 24 hours ago
      const keysToRemove: string[] = [];

      for (const [key, alertTime] of Array.from(this.lastAlertTimes.entries()) as [string, Date][]) {
        if (alertTime.getTime() < alertCutoffTime) {
          keysToRemove.push(key);
        }
      }

      keysToRemove.forEach((key: string) => this.lastAlertTimes.delete(key));

      if (keysToRemove.length > 0) {
        console.log(`[SystemHealthMonitor] ÿ™ŸÖ ÿ™ŸÜÿ∏ŸäŸÅ ${keysToRemove.length} ÿ≠ÿßŸÑÿ© ÿ™ÿ≠ÿ∞Ÿäÿ± ŸÇÿØŸäŸÖÿ©`);
      }

      // ÿ≠ÿ∞ŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ© ŸÖŸÜ ÿ¨ÿØŸàŸÑ ÿßŸÑÿ£ÿØÿßÿ°
      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© Ÿáÿ∞Ÿá ÿßŸÑÿπŸÖŸÑŸäÿ© ŸÅŸä storage.ts ŸÑÿßÿ≠ŸÇÿßŸã

      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ©');
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ©:', error);
    }
  }

  /**
   * ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÜÿ∏ÿßŸÖ ÿ®ÿ£ŸÖÿßŸÜ
   */
  public async shutdown(): Promise<void> {
    try {
      this.stopMonitoring();
      console.log('[SystemHealthMonitor] ÿ™ŸÖ ÿ•ŸäŸÇÿßŸÅ ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ© ÿ®ÿ£ŸÖÿßŸÜ');
    } catch (error) {
      console.error('[SystemHealthMonitor] ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸäŸÇÿßŸÅ ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©:', error);
    }
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑÿ≠ÿßŸÑŸäÿ©
   */
  public getSystemStatus(): Record<string, any> {
    const status: Record<string, any> = {
      monitoring: this.monitoringInterval !== null,
      healthChecks: this.healthCheckInterval !== null,
      lastHealthChecks: Array.from(this.lastHealthStatus.values()),
      totalAlertRules: this.alertRules.length
    };

    return status;
  }
}

// ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ´ŸäŸÑ ŸÖÿ¥ÿ™ÿ±ŸÉ
let systemHealthMonitor: SystemHealthMonitor | null = null;

export function getSystemHealthMonitor(storage: IStorage): SystemHealthMonitor {
  if (!systemHealthMonitor) {
    systemHealthMonitor = new SystemHealthMonitor(storage);
  }
  return systemHealthMonitor;
}

export default SystemHealthMonitor;

