  });

  // ÿ¨ŸÑÿ® ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ
  const { data: healthStatus } = useQuery<HealthStatus>({
    queryKey: ['/api/system/health'],
