    }
  };

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ£ŸäŸÇŸàŸÜÿ© ÿßŸÑÿ≠ÿßŸÑÿ©
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active': return AlertTriangle;
      case 'resolved': return CheckCircle2;
      case 'dismissed': return XCircle;
      default: return Clock;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6" dir="rtl">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            ŸÖÿ±ŸÉÿ≤ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿßŸÑÿ∞ŸÉŸäÿ©
          </h1>
          <p className="text-gray-600 dark:text-gray-300 mt-2">
            ŸÖÿ±ÿßŸÇÿ®ÿ© ÿ¥ÿßŸÖŸÑÿ© ŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ŸàÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ®ÿßŸÇŸäÿ©
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="text-sm">
            <Activity className="w-4 h-4 ml-1" />
            ŸÖÿ±ÿßŸÇÿ®ÿ© ŸÖÿ®ÿßÿ¥ÿ±ÿ©
          </Badge>
        </div>
      </div>

      {/* ŸÖŸÑÿÆÿµ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ */}
      {healthStatus && (
        <Card className="border-2">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2">
              <Shield className="w-5 h-5" />
              ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑÿπÿßŸÖÿ©
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className={`text-2xl font-bold ${
                  healthStatus.overall_status === 'healthy' ? 'text-green-600' : 
                  healthStatus.overall_status === 'warning' ? 'text-yellow-600' : 'text-red-600'
                }`}>
                  {healthStatus.overall_status === 'healthy' ? 'ÿ≥ŸÑŸäŸÖ' :
                   healthStatus.overall_status === 'warning' ? 'ÿ™ÿ≠ÿ∞Ÿäÿ±' : 'ÿÆÿ∑ÿ±'}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-300">ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑÿπÿßŸÖÿ©</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">{healthStatus.healthy_checks}</div>
                <div className="text-sm text-gray-600 dark:text-gray-300">ŸÅÿ≠Ÿàÿµÿßÿ™ ÿ≥ŸÑŸäŸÖÿ©</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-yellow-600">{healthStatus.warning_checks}</div>
                <div className="text-sm text-gray-600 dark:text-gray-300">ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-red-600">{healthStatus.critical_checks}</div>
                <div className="text-sm text-gray-600 dark:text-gray-300">ÿ≠ÿßŸÑÿßÿ™ ÿÆÿ∑ÿ±ÿ©</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™</p>
                  <p className="text-2xl font-bold">{stats.total_alerts}</p>
                </div>
                <Bell className="w-8 h-8 text-blue-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</p>
                  <p className="text-2xl font-bold text-orange-600">{stats.active_alerts}</p>
                </div>
                <AlertTriangle className="w-8 h-8 text-orange-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿ≠ÿ±ÿ¨ÿ©</p>
                  <p className="text-2xl font-bold text-red-600">{stats.critical_alerts}</p>
                </div>
