            <Select value={filterType} onValueChange={setFilterType}>
              <SelectTrigger className="w-40" data-testid="select-filter-type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÜŸàÿßÿπ</SelectItem>
                <SelectItem value="system">ŸÜÿ∏ÿßŸÖ</SelectItem>
                <SelectItem value="production">ÿ•ŸÜÿ™ÿßÿ¨</SelectItem>
                <SelectItem value="inventory">ŸÖÿÆÿ≤ŸàŸÜ</SelectItem>
                <SelectItem value="quality">ÿ¨ŸàÿØÿ©</SelectItem>
                <SelectItem value="maintenance">ÿµŸäÿßŸÜÿ©</SelectItem>
                <SelectItem value="security">ÿ£ŸÖÿßŸÜ</SelectItem>
              </SelectContent>
            </Select>

            <Select value={filterSeverity} onValueChange={setFilterSeverity}>
              <SelectTrigger className="w-40" data-testid="select-filter-severity">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖÿ≥ÿ™ŸàŸäÿßÿ™</SelectItem>
                <SelectItem value="critical">ÿ≠ÿ±ÿ¨</SelectItem>
                <SelectItem value="high">ÿπÿßŸÑŸä</SelectItem>
                <SelectItem value="medium">ŸÖÿ™Ÿàÿ≥ÿ∑</SelectItem>
                <SelectItem value="low">ŸÖŸÜÿÆŸÅÿ∂</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertTriangle className="w-5 h-5" />
            ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ({filteredAlerts.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-[600px]">
            {alertsLoading ? (
              <div className="flex items-center justify-center h-32">
                <div className="text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="mt-2 text-sm text-gray-600 dark:text-gray-300">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™...</p>
                </div>
              </div>
            ) : filteredAlerts.length === 0 ? (
              <div className="text-center py-8">
                <CheckCircle2 className="w-16 h-16 text-green-600 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                  ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  ÿ±ÿßÿ¶ÿπ! ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿ™ÿ∑ÿßÿ®ŸÇ ÿßŸÑŸÖÿπÿßŸäŸäÿ± ÿßŸÑŸÖÿ≠ÿØÿØÿ©
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {filteredAlerts.map((alert: SystemAlert) => {
                  const TypeIcon = getTypeIcon(alert.type);
                  const StatusIcon = getStatusIcon(alert.status);

                  return (
                    <Card key={alert.id} className={`border-r-4 ${
                      alert.severity === 'critical' ? 'border-r-red-500' :
                      alert.severity === 'high' ? 'border-r-orange-500' :
                      alert.severity === 'medium' ? 'border-r-yellow-500' :
                      'border-r-blue-500'
                    }`}>
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex items-start gap-3 flex-1">
                            <div className="p-2 rounded-lg bg-gray-100 dark:bg-gray-800">
                              <TypeIcon className="w-5 h-5 text-gray-700 dark:text-gray-300" />
                            </div>

                            <div className="flex-1">
                              <div className="flex items-center gap-2 mb-2">
                                <h3 className="font-semibold text-gray-900 dark:text-white">
                                  {alert.title_ar}
                                </h3>
                                <Badge variant={getSeverityColor(alert.severity)}>
                                  {alert.severity === 'critical' ? 'ÿ≠ÿ±ÿ¨' :
                                   alert.severity === 'high' ? 'ÿπÿßŸÑŸä' :
                                   alert.severity === 'medium' ? 'ŸÖÿ™Ÿàÿ≥ÿ∑' : 'ŸÖŸÜÿÆŸÅÿ∂'}
                                </Badge>
                                <Badge variant="outline">
                                  {alert.type === 'system' ? 'ŸÜÿ∏ÿßŸÖ' :
                                   alert.type === 'production' ? 'ÿ•ŸÜÿ™ÿßÿ¨' :
                                   alert.type === 'inventory' ? 'ŸÖÿÆÿ≤ŸàŸÜ' :
                                   alert.type === 'quality' ? 'ÿ¨ŸàÿØÿ©' :
                                   alert.type === 'maintenance' ? 'ÿµŸäÿßŸÜÿ©' :
                                   alert.type === 'security' ? 'ÿ£ŸÖÿßŸÜ' : alert.type}
                                </Badge>
                              </div>

                              <p className="text-gray-600 dark:text-gray-300 mb-3">
                                {alert.message_ar}
                              </p>

                              <div className="flex items-center gap-4 text-sm text-gray-500 dark:text-gray-400">
                                <div className="flex items-center gap-1">
                                  <StatusIcon className="w-4 h-4" />
                                  {alert.status === 'active' ? 'ŸÜÿ¥ÿ∑' :
                                   alert.status === 'resolved' ? 'ŸÖÿ≠ŸÑŸàŸÑ' :
                                   alert.status === 'dismissed' ? 'ŸÖÿ∫ŸÑŸÇ' : alert.status}
                                </div>
                                <div className="flex items-center gap-1">
                                  <Clock className="w-4 h-4" />
                                  {formatDistanceToNow(new Date(alert.created_at), { 
                                    addSuffix: true, 
                                    locale: ar 
                                  })}
                                </div>
                                {alert.occurrences > 1 && (
                                  <div className="flex items-center gap-1">
                                    <TrendingUp className="w-4 h-4" />
                                    {alert.occurrences} ŸÖÿ±ÿ©
                                  </div>
                                )}
                              </div>

                              {/* ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠ÿ© */}
                              {alert.suggested_actions && alert.suggested_actions.length > 0 && (
                                <div className="mt-3 p-3 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
                                  <p className="text-sm font-medium text-blue-900 dark:text-blue-100 mb-2">
                                    ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ŸÖŸÇÿ™ÿ±ÿ≠ÿ©:
                                  </p>
                                  <ul className="text-sm text-blue-800 dark:text-blue-200 space-y-1">
                                    {alert.suggested_actions.map((action, index) => (
                                      <li key={index} className="flex items-center gap-2">
                                        <ChevronRight className="w-3 h-3" />
                                        {action.description || action.action}
                                      </li>
                                    ))}
                                  </ul>
                                </div>
                              )}
                            </div>
                          </div>

                          {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ */}
                          {alert.status === 'active' && (
                            <div className="flex gap-2">
                              <Button
                                size="sm"
                                onClick={() => resolveAlertMutation.mutate({ alertId: alert.id })}
                                disabled={resolveAlertMutation.isPending}
                                data-testid={`button-resolve-${alert.id}`}
                              >
                                <CheckCircle2 className="w-4 h-4 ml-1" />
                                ÿ≠ŸÑ
                              </Button>
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => dismissAlertMutation.mutate(alert.id)}
                                disabled={dismissAlertMutation.isPending}
                                data-testid={`button-dismiss-${alert.id}`}
                              >
                                <XCircle className="w-4 h-4 ml-1" />
                                ÿ•ÿ∫ŸÑÿßŸÇ
                              </Button>
                            </div>
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            )}
          </ScrollArea>
        </CardContent>
      </Card>
    </div>
  );
}

