import { Router } from 'express';
import type { IStorage } from '../storage';
import { getAlertManager } from '../services/alert-manager';
import { getSystemHealthMonitor } from '../services/system-health-monitor';
import { getDataValidator } from '../services/data-validator';
import { z } from 'zod';

export function createAlertsRouter(storage: IStorage) {
  const router = Router();
  const alertManager = getAlertManager(storage);
  const healthMonitor = getSystemHealthMonitor(storage);
  const dataValidator = getDataValidator(storage);

  // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ŸÖÿπ ÿßŸÑŸÅŸÑÿßÿ™ÿ±
  router.get('/', async (req, res) => {
    try {
      const filters = {
        status: req.query.status as string,
        type: req.query.type as string,
        severity: req.query.severity as string,
        limit: req.query.limit ? parseInt(req.query.limit as string) : 50,
        offset: req.query.offset ? parseInt(req.query.offset as string) : 0
      };

      const alerts = await storage.getSystemAlerts(filters);
      res.json(alerts);
    } catch (error: any) {
      console.error('Error fetching alerts:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™' });
    }
  });

  // ÿ¨ŸÑÿ® ÿ™ÿ≠ÿ∞Ÿäÿ± ŸÖÿ≠ÿØÿØ
  router.get('/:id', async (req, res) => {
    try {
      const alertId = parseInt(req.params.id);
      const alert = await storage.getSystemAlertById(alertId);

      if (!alert) {
        return res.status(404).json({ message: 'ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ± ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ' });
      }

      res.json(alert);
    } catch (error: any) {
      console.error('Error fetching alert:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±' });
    }
  });

  // ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ≠ÿ∞Ÿäÿ± ÿ¨ÿØŸäÿØ
  router.post('/', async (req, res) => {
    try {
      const alertData = req.body;

      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const alertSchema = z.object({
        title: z.string().min(1),
        title_ar: z.string().min(1),
        message: z.string().min(1),
        message_ar: z.string().min(1),
        type: z.string(),
        category: z.string(),
        severity: z.string(),
        source: z.string(),
        source_id: z.string().optional(),
        context_data: z.record(z.any()).optional(),
        suggested_actions: z.array(z.object({
          action: z.string(),
          priority: z.number(),
          description: z.string().optional()
        })).optional(),
        target_users: z.array(z.number()).optional(),
        target_roles: z.array(z.number()).optional(),
        requires_action: z.boolean().optional()
      });

      const validatedData = alertSchema.parse(alertData);
      const alert = await alertManager.createAlert(validatedData);

      res.status(201).json(alert);
    } catch (error: any) {
      console.error('Error creating alert:', error);
      if (error.name === 'ZodError') {
        return res.status(400).json({ message: 'ÿ®ŸäÿßŸÜÿßÿ™ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©', errors: error.errors });
      }
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±' });
    }
  });

  // ÿ≠ŸÑ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±
  router.post('/:id/resolve', async (req, res) => {
    try {
      const alertId = parseInt(req.params.id);
      const { notes } = req.body;
      const userId = (req as any).user?.id || 1; // ŸÖÿ§ŸÇÿ™

      const alert = await alertManager.resolveAlert(alertId, userId, notes);
      res.json(alert);
    } catch (error: any) {
      console.error('Error resolving alert:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ŸÑ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±' });
    }
  });

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±
  router.post('/:id/dismiss', async (req, res) => {
    try {
      const alertId = parseInt(req.params.id);
      const userId = (req as any).user?.id || 1; // ŸÖÿ§ŸÇÿ™

      const alert = await alertManager.dismissAlert(alertId, userId);
      res.json(alert);
    } catch (error: any) {
      console.error('Error dismissing alert:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±' });
    }
  });

  // ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™
  router.get('/stats', async (req, res) => {
    try {
      const stats = await alertManager.getAlertStatistics();
      res.json(stats);
    } catch (error: any) {
      console.error('Error fetching alert stats:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™' });
    }
  });

  // ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿ≠ÿ≥ÿ® ÿßŸÑŸÜŸàÿπ
  router.get('/type/:type', async (req, res) => {
    try {
      const type = req.params.type;
      const alerts = await storage.getAlertsByType(type);
      res.json(alerts);
    } catch (error: any) {
      console.error('Error fetching alerts by type:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿ≠ÿ≥ÿ® ÿßŸÑŸÜŸàÿπ' });
    }
  });

